INSERT INTO `abouts` (`about_id`, `about_name`, `about_content`, `created_at`, `updated_at`) VALUES
(1, 'story', '<p>ZikZak được ra đời v&agrave;o th&aacute;ng 6 năm 2018, &yacute; tưởng về kh&ocirc;ng gian l&agrave;m việc chung đ&atilde; được CGroup ấp ủ từ l&acirc;u trước đ&oacute;. Gầy dựng n&ecirc;n ZikZak ch&iacute;nh l&agrave; anh Đo&agrave;n C&ocirc;ng Chung &ndash; CEO &amp; Chủ tịch HĐQT CGroup, cũng đ&atilde; từng l&agrave; một StartUp th&agrave;nh c&ocirc;ng trong lĩnh vực c&ocirc;ng nghệ. Hiểu được t&acirc;m l&yacute; StartUp, Freelancer v&agrave; c&aacute;c doanh nghiệp vừa v&agrave; nhỏ (SME) cần một kh&ocirc;ng gian l&agrave;m việc để thỏa sức s&aacute;ng tạo, để tạo n&ecirc;n hiệu suất l&agrave;m việc tốt nhất, qua nhiều năm l&agrave;m việc, Anh nhận thấy điều dẫn đến c&aacute;i chết yểu của c&aacute;c StartUp, đặc biệt l&agrave; c&aacute;c StartUp mới l&agrave; mức chi ph&iacute; vận h&agrave;nh qu&aacute; cao l&agrave;m giảm sự cạnh tranh v&agrave; l&agrave; c&aacute;i phanh l&agrave;m k&igrave;m h&atilde;m sự ph&aacute;t triển của SME.</p>\r\n\r\n<p>Hiểu được điều đ&oacute;, ZikZak ra đời để lấp khoảng trống c&ograve;n thiếu của c&aacute;c StartUp. C&ugrave;ng kh&ocirc;ng gian l&agrave;m việc chung ZikZak tạo ra cho c&aacute;c StartUp, Freelancer cơ hội học hỏi với điều kiện l&agrave;m việc thoải m&aacute;i, mở rộng mối quan hệ, t&igrave;m kiếm cơ hội đầu tư.</p>', NULL, '2018-10-31 18:44:57'),
(2, 'view', '<p>X&acirc;y dựng v&agrave; ph&aacute;t triển hệ sinh th&aacute;i khởi nghiệp Việt Nam lớn mạnh, tạo ra những thay đổi về kinh tế v&agrave; ph&aacute;t triển nền tảng trong cộng đồng Startup.</p>\r\n\r\n<p>ZikZak mang trong m&igrave;nh sứ mệnh đồng h&agrave;nh v&agrave; hỗ trợ c&aacute;c Startup tối đa để họ c&oacute; thể&nbsp;ph&aacute;t triển mạnh mẽ hơn&nbsp;trong hệ sinh th&aacute;i khởi nghiệp ở Việt Nam.</p>', NULL, '2018-10-31 18:45:36'),
(3, 'value', '<p>ZikZak lu&ocirc;n lu&ocirc;n phấn đấu v&agrave; ho&agrave;n thiện kh&ocirc;ng gian để c&aacute;c Startup l&agrave;m việc trong kh&ocirc;ng gian tiện nghi với mức chi ph&iacute; rẻ nhất, v&agrave; dễ d&agrave;ng&nbsp;kết nối được với&nbsp;cộng đồng Startup trong ZikZak.</p>', NULL, '2018-10-31 18:46:13');

