INSERT INTO `advices` (`advice_id`, `advice_name`, `advice_description`, `advice_price`, `created_at`, `updated_at`) VALUES
(1, '<p>Tư vấn miễn ph&iacute;</p>', '<p>Tư vấn miễn ph&iacute; thứ 3 h&agrave;ng tuần tại cơ sở của Zikzak</p>', '<p><strong>Miễn ph&iacute;</strong></p>', NULL, '2018-05-30 00:01:31'),
(2, '<p>Tư vấn thường xuy&ecirc;n</p>', '<p>Tư vấn c&aacute;c bộ hợp đồng mẫu được soạn thảo bởi C&ocirc;ng ty luật Duane Morris, v&iacute; dụ:</p>\r\n\r\n<ul>\r\n	<li>Hợp đồng hợp t&aacute;c kinh doanh;</li>\r\n	<li>Hợp đồng lao động;</li>\r\n	<li>Hợp đồng g&oacute;p vốn;</li>\r\n	<li>Hợp đồng vay vốn c&oacute; chuyển đổi.</li>\r\n	<li>Tư vấn ri&ecirc;ng c&ugrave;ng chuy&ecirc;n gia: 120 ph&uacute;t/th&aacute;ng</li>\r\n</ul>', '<p><strong>2.000.000VND</strong> / th&aacute;ng</p>', NULL, '2018-05-30 00:01:49'),
(3, 'Tư vấn thành lập doanh nghiệp, đăng ký kinh doanh', '<p>Doanh nghiệp trong nước</p>\r\n<ul>\r\n<li>Thành lập doanh nghiệp và thông báo tài khoản ngân hàng</li>\r\n<li>Thay đổi trụ sở (con dấu)</li>\r\n<li>Thay đổi nội dung Đăng ký kinh doanh</li>\r\n<li>Chào bán/ tăng vốn cổ phần công ty (công ty cổ phần)</li>\r\n', '<p>Từ 1.500.000 VND</p>\r\n\r\n<p>(*) Liên hệ để được tư vấn chi tiết</p>\r\n', NULL, NULL),
(4, '', '<p>Doanh nghiệp có vốn đầu tư nước ngoài</p>\r\n<ul>\r\n<li>Thành lập công ty nước ngoài</li>\r\n<li>Thành lập chi nhánh công ty nước ngoài, mở văn phòng đại diện, thay đổi trụ sở</li>\r\n', '<p>Từ 1.000.000 VND</p>\r\n\r\n<p>(*) Liên hệ để được tư vấn chi tiết</p>\r\n', NULL, NULL),
(5, 'Tư vấn sở hữu trí tuệ', '<p>Tư vấn và thực hiện các thủ tục về sở hữu trí tuệ như: đăng ký nhãn hiệu, đăng ký bản quyền, quyền sở hữu công nghiệp...</p>', '<p>(*) Liên hệ để được tư vấn chi tiết</p>', NULL, NULL);

