INSERT INTO `contents` (`id`, `name`, `content`, `created_at`, `updated_at`) VALUES
(1, 'Làm việc hiệu quả và sáng tạo hơn tại zikzak coworking space', '<p>Bạn l&agrave;m việc kh&ocirc;ng c&oacute; động lực v&agrave; cảm hứng, kh&ocirc;ng thể tập trung v&igrave; sự ồn &agrave;o xung quanh? StartUp của bạn đang cần văn ph&ograve;ng với mức chi ph&iacute; thấp ?</p>\r\n\r\n<p>ZikZak sẽ giải quyết cho bạn điều đ&oacute; Zikzak nằm ngay tại trung t&acirc;m th&agrave;nh phố Hồ Ch&iacute; Minh, th&agrave;nh phố Nha Trang. Kh&ocirc;ng gian được thiết kế th&ocirc;ng minh v&agrave; cung cấp tất cả c&aacute;c chức năng để bạn thỏa sức sang tạo v&agrave; sự đột ph&aacute; trong c&ocirc;ng việc v&agrave; đưa doanh nghiệp bạn l&ecirc;n một cấp độ mới.</p>', '2018-11-07 00:16:51', '2018-11-07 00:17:26'),
(2, 'Dịch vụ doanh nghiệp', '<p>Bạn c&oacute; &yacute; tưởng kinh doanh v&agrave; muốn khởi nghiệp , kh&ocirc;ng biết chọn m&ocirc; h&igrave;nh c&ocirc;ng ty, doanh nghiệp n&agrave;o cho ph&ugrave; hợp<br />\r\nBạn đang băn khoăn thủ tục ph&aacute;p l&yacute; cơ bản để ho&agrave;n th&agrave;nh một doanh nghiệp ?<br />\r\nBạn đang cần tư vấn c&aacute;c giấy tờ l&agrave;m việc với c&aacute;c nh&agrave; đầu tư ?</p>', '2018-11-07 00:17:30', '2018-11-07 00:29:31'),
(3, 'Thành viên Zikzak', '<p>Dịch vụ Văn ph&ograve;ng ảo của Zikzak Coworking Space l&agrave; h&igrave;nh thức văn ph&ograve;ng cung cấp dịch vụ địa chỉ v&agrave; li&ecirc;n lạc m&agrave; kh&ocirc;ng cần đến diện t&iacute;ch thực tế.<br />\r\nDịch Văn ph&ograve;ng ảo gi&uacute;p Doanh nghiệp v&agrave; Startup sở hữu địa chỉ kinh doanh đẹp, sang trọng, ngắn gọn v&agrave; dễ nhớ nhưng vẫn tiết kiệm được đến 80% chi ph&iacute;.</p>', '2018-11-07 00:29:36', '2018-11-07 00:29:45'),
(4, 'Phòng họp', '<p>Bạn c&oacute; &yacute; tưởng kinh doanh v&agrave; muốn khởi nghiệp , kh&ocirc;ng biết chọn m&ocirc; h&igrave;nh c&ocirc;ng ty, doanh nghiệp n&agrave;o cho ph&ugrave; hợp<br />\r\nBạn đang băn khoăn thủ tục ph&aacute;p l&yacute; cơ bản để ho&agrave;n th&agrave;nh một doanh nghiệp ?<br />\r\nBạn đang cần tư vấn c&aacute;c giấy tờ l&agrave;m việc với c&aacute;c nh&agrave; đầu tư ?</p>', '2018-11-07 00:29:47', '2018-11-07 00:29:59'),
(5, 'Hệ sinh thái dịch vụ', '<p>Những &yacute; tưởng mới chỉ bắt nguồn từ những c&aacute;ch l&agrave;m việc v&agrave; tư duy mới.</p>\r\n\r\n<p>V&igrave; vậy, ch&uacute;ng t&ocirc;i kh&ocirc;ng chỉ cung cấp c&aacute;c địa điểm được trang bị đầy phong c&aacute;ch ở những vị tr&iacute; tốt nhất.</p>\r\n\r\n<p>Hơn nữa, ch&uacute;ng t&ocirc;i tạo ra những rung cảm s&aacute;ng tạo, l&agrave;m sống lại định nghĩa cuộc họp v&agrave; l&agrave;m những điều bạn kh&ocirc;ng thể ngờ tới.</p>', '2018-11-07 00:31:16', '2018-11-07 00:31:30'),
(6, 'Văn phòng ảo', '<p>Dịch vụ Văn ph&ograve;ng ảo của Zikzak Coworking Space l&agrave; h&igrave;nh thức văn ph&ograve;ng cung cấp dịch vụ địa chỉ</p>\r\n\r\n<p>v&agrave; li&ecirc;n lạc m&agrave; kh&ocirc;ng cần đến diện t&iacute;ch thực tế. Gi&uacute;p doanh nghiệp cắt giảm chi ph&iacute; vận h&agrave;nh nhưng vẫn đảm bảo</p>\r\n\r\n<p>được sự chuy&ecirc;n nghiệp trong hoạt động.</p>', '2018-11-07 00:31:33', '2018-11-07 00:31:49'),
(7, 'Trải nghiệm khoảnh khắc tuyệt vời', '<p>Những &yacute; tưởng mới chỉ bắt nguồn từ những c&aacute;ch l&agrave;m việc v&agrave; tư duy mới.</p>\r\n\r\n<p>V&igrave; vậy, ch&uacute;ng t&ocirc;i kh&ocirc;ng chỉ cung cấp c&aacute;c địa điểm được trang bị đầy phong c&aacute;ch ở những vị tr&iacute; tốt nhất.</p>\r\n\r\n<p>Hơn nữa, ch&uacute;ng t&ocirc;i tạo ra những rung cảm s&aacute;ng tạo, l&agrave;m sống lại định nghĩa cuộc họp v&agrave; l&agrave;m những điều bạn kh&ocirc;ng thể ngờ tới.</p>', '2018-11-07 00:32:09', '2018-11-07 00:32:24'),
(8, 'Trải nghiệm khoảnh khắc tuyệt vời', '<p>Những &yacute; tưởng mới chỉ bắt nguồn từ những c&aacute;ch l&agrave;m việc v&agrave; tư duy mới.</p>\r\n\r\n<p>V&igrave; vậy, ch&uacute;ng t&ocirc;i kh&ocirc;ng chỉ cung cấp c&aacute;c địa điểm được trang bị đầy phong c&aacute;ch ở những vị tr&iacute; tốt nhất.</p>\r\n\r\n<p>Hơn nữa, ch&uacute;ng t&ocirc;i tạo ra những rung cảm s&aacute;ng tạo, l&agrave;m sống lại định nghĩa cuộc họp v&agrave; l&agrave;m những điều bạn kh&ocirc;ng thể ngờ tới.</p>', '2018-11-07 00:32:52', '2018-11-07 00:33:08'),
(9, 'Đội ngũ sáng lập', '<p>Ch&uacute;ng t&ocirc;i mong muốn x&acirc;y dựng một cộng đồng s&aacute;ng tạo của những người khởi nghiệp</p>\r\n\r\n<p>Nơi m&agrave; gi&aacute; trị cốt l&otilde;i kh&ocirc;ng chỉ dừng lại ở một kh&ocirc;ng gian l&agrave;m việc chung</p>\r\n\r\n<p>M&agrave; c&ograve;n l&agrave; những cơ hội để cộng đồng khởi nghiệp trẻ biến ho&agrave;i b&atilde;o thay đổi thế giới của họ th&agrave;nh sự thật.</p>', '2018-11-07 00:36:41', '2018-11-07 00:36:54'),
(10, 'Zikzak member', '<p>ZikZak lu&ocirc;n lu&ocirc;n phấn đấu v&agrave; ho&agrave;n thiện kh&ocirc;ng gian để c&aacute;c Startup l&agrave;m việc trong kh&ocirc;ng gian tiện nghi với mức chi ph&iacute; rẻ nhất,</p>\r\n\r\n<p>v&agrave; dễ d&agrave;ng kết nối được với cộng đồng Startup trong ZikZak.</p>', '2018-11-07 00:36:56', '2018-11-07 00:37:08');

