INSERT INTO `news` (`news_id`, `news_name`, `news_slug`, `news_summary`, `news_image`, `news_content`, `tag`, `created_at`, `updated_at`) VALUES
(1, 'Các địa điểm co-working space thu hút cộng đồng khởi nghiệp và giới trẻ Hà Nội', 'cac-dia-diem-co-working-space-thu-hut-cong-dong-khoi-nghiep-va-gioi-tre-ha-noi', 'Bạn mới khởi nghiệp , bạn cần một không gian làm việc hiệu quả , học hỏi kết nối cộng động StartUp lại với nhau đặc biệt là tiết kiệm chi phí thuê văn phòng ? \r\nCo-Working Space là sự lựa chọn tuyệt vời cho bạn!Dưới đây là 7 địa điểm Co-Working Space có không gian đẹp nhất Hà Nội do ZikZak tổng hợp', 'zz_2018-06-01_1527838791.jpeg', '<p style=\"margin-left:0in; margin-right:0in\"><a href=\"http://zikzak-co.vn/news-detail/nhung-co-working-space-noi-tieng-voi-khong-gian-lam-viec-ly-tuong-o-tp-ho-chi-minh\" target=\"_blank\"><span style=\"font-size:14px\">--Xem th&ecirc;m: Những co-working space c&oacute; kh&ocirc;ng gian l&yacute; tưởng tại TP HCM</span></a></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><strong><em><span style=\"font-size:11.0pt\"><span style=\"color:#303030\">1. Hatch! Nest</span></span></em></strong></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:#303030\">Hatch! Nest hay c&ograve;n gọi l&agrave; TỔ 14 l&agrave; kh&ocirc;ng gian l&agrave;m việc s&aacute;ng tạo thuộc Hatch! &ndash; một doanh nghiệp x&atilde; hội hoạt động trong lĩnh vực hỗ trợ v&agrave; ươm mầm c&aacute;c đơn vị khởi nghiệp tại Việt Nam.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:#303030\">M&agrave;u v&agrave;ng chanh tươi s&aacute;ng được chọn l&agrave;m tone m&agrave;u chủ đạo của Tổ 14, c&oacute; khu vực để tổ chức hội thảo. Tuy nhi&ecirc;n kh&ocirc;ng gian sẽ c&oacute; một ch&uacute;t k&iacute;ch mắt với những bạn n&agrave;o th&iacute;ch l&agrave;m việc trong m&ocirc;i trường mở. Điểm cộng l&agrave; vị tr&iacute; của Tổ 14 c&oacute; rất nhiều đồ ăn ngon thỏa m&atilde;n c&aacute;c th&agrave;nh vi&ecirc;n sau h&agrave;ng giờ l&agrave;m việc căng thẳng.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><img alt=\"\" src=\"/local/storage/app/image/images/hatch-nest-coworking-space.jpg\" style=\"height:746px; width:1600px\" /></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:#303030\">Đ&acirc;y l&agrave; địa điểm lui tới thường xuy&ecirc;n của c&aacute;c tổ chức sinh vi&ecirc;n, c&aacute;c doanh nghiệp x&atilde; hội hay cộng đồng start-up, v&igrave; thế, đến Hatch! Nest, chắc chắn bạn sẽ được truyền cảm hứng v&agrave; học hỏi được rất nhiều điều bổ &iacute;ch từ những chuy&ecirc;n gia hay bạn trẻ hoạt động c&ugrave;ng lĩnh vực.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:#303030\">Hiện tại, với g&oacute;i cơ bản (Dedicated Seat), ph&iacute; dịch vụ l&agrave; 800.000đ/ 80 giờ/ th&aacute;ng (c&oacute; ưu đ&atilde;i giảm 10% cho c&aacute;c start-up hoặc nh&oacute;m 3 người).</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><strong><span style=\"font-size:11.0pt\"><span style=\"color:#303030\">2.</span></span></strong><span style=\"font-size:11.0pt\"><span style=\"color:#303030\"> <strong><em>iHouse</em></strong></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:#303030\">Nếu bạn muốn t&igrave;m một kh&ocirc;ng gian co-working xanh m&aacute;t để h&ograve;a m&igrave;nh với thi&ecirc;n nhi&ecirc;n th&igrave; iHouse 101 Đ&agrave;o Tấn ch&iacute;nh l&agrave; một lựa chọn l&iacute; tưởng cho bạn. Với diện t&iacute;ch 200m2, đ&acirc;y l&agrave; kh&ocirc;ng gian l&agrave;m việc chung duy nhất tại H&agrave; Nội c&oacute; thiết kế s&acirc;n vườn rộng r&atilde;i, phong cảnh đẹp, tạo cho bạn sự thư th&aacute;i cũng như nhiều lựa chọn chỗ ngồi linh hoạt để ph&ugrave; hợp với sở th&iacute;ch của bản th&acirc;n.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:#303030\">B&ecirc;n cạnh c&aacute;c ph&ograve;ng họp, khu&ocirc;n vi&ecirc;n c&acirc;y xanh, iHouse c&ograve;n c&oacute; quầy bar với nhiều loại đồ uống như tr&agrave;, c&agrave; ph&ecirc;, v&agrave; cả bia.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><img alt=\"iHouse\" src=\"/local/storage/app/image/images/ihouse-coworking.jpeg\" style=\"height:416px; width:640px\" /></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:#303030\">Chỉ một lưu &yacute; nhỏ l&agrave; địa điểm hơi kh&oacute; t&igrave;m v&igrave; nằm tận s&acirc;u trong ng&otilde;, kh&ocirc;ng để được nhiều xe. Gi&aacute; cả th&igrave; kh&aacute; dễ chịu, c&oacute; phục vụ tr&agrave; nước, một số m&oacute;n đồ ăn vặt nữa.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:#303030\">Giờ mở cửa: 8:30 &ndash; 18:30</span></span><br />\r\n<span style=\"font-size:11.0pt\"><span style=\"color:#303030\">Thẻ theo ng&agrave;y: 150,000 VND/ng&agrave;y</span></span><br />\r\n<span style=\"font-size:11.0pt\"><span style=\"color:#303030\">Thẻ 05 ng&agrave;y: 550,000 VND.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><strong><span style=\"font-size:11.0pt\"><span style=\"color:#303030\">3.</span></span></strong> <strong><span style=\"font-size:11.0pt\"><span style=\"background-color:white\"><span style=\"color:black\">TikTak </span></span></span></strong></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"background-color:white\"><span style=\"color:black\">Bạn muốn một kh&ocirc;ng gian&nbsp;l&agrave;m việc chung rộng r&atilde;i v&agrave; đầy đủ tiện nghi ? Tiktak Co- working Space mang đến kh&ocirc;ng gian l&agrave;m việc chung chuy&ecirc;n nghiệp, hiện đại, s&aacute;ng tạo cho cộng đồng Start up H&agrave; Nội.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"background-color:white\"><span style=\"color:black\">&nbsp;Với diện t&iacute;ch tổng thể l&ecirc;n đến gần 2.000m2, thiết kế nội thất sang trọng, tinh tế đem đến cho người sử dụng cảm gi&aacute;c thoải m&aacute;i, s&aacute;ng tạo.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><img alt=\"TikTak\" src=\"/local/storage/app/image/images/tiktak-coworking.jpeg\" style=\"height:953px; width:1430px\" /></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:black\">Tiktak Co-working Space sở hữu vị tr&iacute; đắc địa, tại tầng 5 t&ograve;a Diamond Flower Tower (ng&atilde; tư L&ecirc; Văn Lương - Ho&agrave;ng Đạo Th&uacute;y).</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:black\">Tạo điều kiện thuận lợi về giao th&ocirc;ng, kết nối dễ d&agrave;ng đến trung t&acirc;m th&agrave;nh phố v&agrave; c&aacute;c tiện &iacute;ch l&acirc;n cận.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:black\">Kh&ocirc;ng gian tho&aacute;ng rộng nội thất sang trọng gi&uacute;p thỏa sức s&aacute;ng tạo. Sảnh tiếp kh&aacute;ch hội trường rộng l&agrave; nơi giao lưu kết nối th&uacute;c đẩy hợp t&aacute;c.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:black\">Quầy bar v&agrave; ph&ograve;ng ăn l&agrave; nơi thư gi&atilde;n sau những giờ l&agrave;m việc căng thẳng.</span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Giờ mở cửa : 8h30 &ndash; 17h30</span></p>\r\n\r\n<p><span style=\"font-size:14px\">G&oacute;i Daily : 120.000VNĐ/Ng&agrave;y</span></p>\r\n\r\n<p><span style=\"font-size:14px\">G&oacute;i Check : 195.000VNĐ/3 Ng&agrave;y/ Th&aacute;ng .</span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><strong><span style=\"font-size:11.0pt\">4.<strong><em><span style=\"color:#303030\"> DESKA</span></em></strong></span></strong></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:#303030\">Mặc d&ugrave; mới đưa v&agrave;o hoạt động nhưng DESKA, tọa lạc tại tầng 16 t&ograve;a nh&agrave; số 10 Hoa Lư, L&ecirc; Đại H&agrave;nh, Hai B&agrave; Trưng, H&agrave; Nội đang được giới trẻ v&agrave; cộng đồng Startup ở H&agrave; Nội ưa chuộng.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:#303030\">H&ograve;a quyện ho&agrave;n hảo giữa m&agrave;u xanh turquoise k&iacute;ch th&iacute;ch s&aacute;ng tạo v&agrave; nội thất đơn giản th&acirc;n thiện theo phong c&aacute;ch Bắc &Acirc;u - DESKA l&agrave; m&ocirc; h&igrave;nh working-space quen thuộc d&agrave;nh cho những người đi l&agrave;m m&agrave; kh&ocirc;ng th&iacute;ch văn ph&ograve;ng</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:#303030\">DESKA cũng c&oacute; những khu vực cơ bản như ph&ograve;ng l&agrave;m việc chung, ph&ograve;ng họp, khu vực tổ chức sự kiện, thư viện&hellip;, c&aacute;c tiện &iacute;ch văn ph&ograve;ng như wifi, m&aacute;y t&iacute;nh để b&agrave;n, m&aacute;y in, photocopy, cũng như tr&agrave; v&agrave; c&agrave; ph&ecirc; miễn ph&iacute;.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:#303030\">Th&ecirc;m một ưu điểm cho DESKA so với c&aacute;c kh&ocirc;ng gian co - working kh&aacute;c l&agrave; chỉ c&aacute;ch phố cổ 5 ph&uacute;t đi xe, DESKA được bao quanh bởi c&aacute;c khu trung t&acirc;m thương mại, văn ph&ograve;ng v&agrave; c&aacute;c trường đại học lớn, giao th&ocirc;ng v&ocirc; c&ugrave;ng thuận tiện, mở cửa 24h/7.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><img alt=\"DESKA\" src=\"/local/storage/app/image/images/deska-coworking.jpeg\" style=\"height:426px; width:640px\" /></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:#303030\">Nhắm đến đối tượng ch&iacute;nh l&agrave; sinh vi&ecirc;n, học sinh, những freelancers v&agrave; nh&oacute;m start-up trẻ tuổi n&ecirc;n mức gi&aacute; tại DESKA cũng tương đối dễ chịu so với c&aacute;c kh&ocirc;ng gian l&agrave;m việc chung kh&aacute;c 50.000đ/3 giờ; 130.000 đ/ng&agrave;y, 100.000 đ/tối (miễn ph&iacute; tr&agrave; v&agrave; c&agrave; ph&ecirc;)... Gi&aacute; theo th&aacute;ng từ 1,5 triệu - 1,8 triệu đồng/th&aacute;ng.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:#303030\">Đặc biệt, DESKA c&ograve;n đưa ra nhiều ch&iacute;nh s&aacute;ch khai trương, ưu đ&atilde;i cho kh&aacute;ch h&agrave;ng như tặng ngay g&oacute;i 3 giờ trải nghiệm miễn ph&iacute; cho kh&aacute;ch đến l&agrave;m việc từ 15/7 - 15/8. Miễn ph&iacute; cho c&aacute;c c&aacute; nh&acirc;n/ tổ chức cần kh&ocirc;ng gian để tổ chức c&aacute;c sự kiện, lớp học, buổi chia sẻ, họp h&agrave;nh, casting.... với quy m&ocirc; cho ph&eacute;p.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:14px\"><span style=\"font-family:Tahoma,Geneva,sans-serif\"><strong><em><span style=\"background-color:white\"><span style=\"color:#303030\">6. UP Coworking Space (Tầng 8, t&ograve;a nh&agrave; Hanoi Creative City, 1 Lương Y&ecirc;n)</span></span></em></strong></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:#303030\">Địa điểm tại Hanoi Creative City &ndash; kh&ocirc;ng gian s&aacute;ng tạo mới tại H&agrave; Nội, Up mang đến một kh&ocirc;ng gian cực k&igrave; năng động v&agrave; thoải m&aacute;i. Nằm tr&ecirc;n tầng 8 n&ecirc;n view l&agrave;m việc cực kỳ ổn.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:#303030\">To&agrave;n bộ kh&ocirc;ng gian v&agrave; c&aacute;c g&oacute;i dịch vụ tại Up đều được thiết kế để d&agrave;nh ri&ecirc;ng cho c&aacute;c bạn khởi nghiệp. Ở đ&acirc;y, thiết kế được m&ocirc; phỏng theo những kh&ocirc;ng gian l&agrave;m việc chung tại &ldquo;Thung Lũng C&ocirc;ng Nghệ&rdquo; Sillicon Valley, kiểu kh&ocirc;ng gian m&agrave; bạn c&oacute; thể dễ d&agrave;ng t&igrave;m thấy tại văn ph&ograve;ng l&agrave;m việc của Facebook hay Google.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><img alt=\"UP Coworking Space (Tầng 8, tòa nhà Hanoi Creative City, 1 Lương Yên)\" src=\"/local/storage/app/image/images/up-coworking-space.jpeg\" style=\"height:360px; width:640px\" /></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:#303030\">B&ecirc;n cạch c&aacute;c khu vực l&agrave;m việc chung th&igrave; c&ograve;n c&oacute; c&aacute;c khu ph&ograve;ng họp, l&agrave;m việc ri&ecirc;ng, khu ăn trưa, khu giải tr&iacute;, thậm ch&iacute; cả đệm nằm nghỉ trưa nữa. L&agrave;m việc căng thẳng th&igrave; bạn ho&agrave;n to&agrave;n c&oacute; thể đi dạo mua sắm, ăn uống, chơi game giải tr&iacute;, tập gym trong &hellip;.ch&iacute;nh t&ograve;a nh&agrave;.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:#303030\">Điểm trừ l&agrave; đồ ăn ở khu vực n&agrave;y gi&aacute; cả hơi cao so với mức trung b&igrave;nh một x&iacute;u. Cụ thể, thẻ theo ng&agrave;y: 200,000 VND/3 ng&agrave;y. Thẻ theo th&aacute;ng: 1,500,000 VND/th&aacute;ng.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><strong><em><span style=\"font-size:11.0pt\"><span style=\"color:#303030\">10. Nest by AIA</span></span></em></strong></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:#303030\">C&ocirc;ng ty Bảo hiểm Nh&acirc;n thọ AIA Việt Nam vừa ch&iacute;nh thức khai trương m&ocirc; h&igrave;nh dịch vụ kh&aacute;ch h&agrave;ng kh&aacute;c biệt mang t&ecirc;n &ldquo;nest by AIA&rdquo; (Tổ ấm với AIA) tại tầng 4, Th&aacute;p B, Trung t&acirc;m Thương mại Vincom, 191 B&agrave; Triệu, H&agrave; Nội.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><img alt=\"Nest by AIA\" src=\"/local/storage/app/image/images/net-by-aia-coworking%20space.jpeg\" style=\"height:807px; width:1200px\" /></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:#303030\">Nest by AIA l&agrave; m&ocirc; h&igrave;nh dịch vụ kh&aacute;ch h&agrave;ng s&aacute;ng tạo của c&ocirc;ng ty bảo hiểm n&agrave;y, bao gồm kh&ocirc;ng gian để tổ chức sự kiện, khu vực d&agrave;nh cho c&aacute;c gia đ&igrave;nh trẻ c&oacute; con nhỏ, qu&aacute;n c&agrave; ph&ecirc;, sảnh, thư viện s&aacute;ch, c&ugrave;ng khu vực ri&ecirc;ng để tr&ograve; chuyện với chuy&ecirc;n vi&ecirc;n quan hệ kh&aacute;ch h&agrave;ng của AIA Việt Nam.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:11.0pt\"><span style=\"color:#303030\">Kh&ocirc;ng chỉ dừng lại ở đ&oacute;, Nest by AIA cũng khuyến kh&iacute;ch c&aacute;c đối t&aacute;c v&agrave; c&aacute;c tổ chức cộng đồng sẽ lựa chọn như một điểm đến tổ chức c&aacute;c buổi hội nghị, hay sự kiện. Khi tổ chức hoặc l&agrave;m việc ở đ&acirc;y, kh&aacute;ch h&agrave;ng, đối t&aacute;c kh&ocirc;ng mất ph&iacute; dịch vụ như ở c&aacute;c co-working kh&aacute;c.</span></span></span></span></p>', 'hà nội, không gian làm việc, co-working space, coworking, thuê văn phòng', '2018-05-27 17:00:00', '2018-07-01 18:34:36'),
(2, 'Những Co-Working Space nổi tiếng với không gian làm việc lý tưởng ở TP Hồ Chí Minh', 'nhung-co-working-space-noi-tieng-voi-khong-gian-lam-viec-ly-tuong-o-tp-ho-chi-minh', 'Co-working space hay còn gọi là không gian làm việc chung đang là sự lựa chọn của nhiều người muốn tìm một nơi làm việc năng động, linh hoạt, chi phí hợp lý mà vẫn đảm bảo tiện nghi. Sau đây ZikZak giới thiệu 5 không gian làm việc chung ở TP.Hồ Chí Minh mà được giới Startup và freelancers lựa chọn nhiều nhất tại TP Hồ Chí Minh', 'zz_2018-06-01_1527839375.jpeg', '<p><span style=\"font-size:14px\"><a href=\"http://zikzak-co.vn/news-detail/cac-dia-diem-co-working-space-thu-hut-cong-dong-khoi-nghiep-va-gioi-tre-ha-noi\" target=\"_blank\">--Xem th&ecirc;m: C&aacute;c co-working space thu h&uacute;t cộng đồng khởi nghiệp H&agrave; Nội&nbsp;</a></span></p>\r\n\r\n<ol>\r\n	<li>\r\n	<p><span style=\"color:null\"><strong>ZIKZAK</strong></span></p>\r\n	</li>\r\n</ol>\r\n\r\n<p>ZikZak co-working space&nbsp; nằm ngay tại trung t&acirc;m th&agrave;nh phố Hồ Ch&iacute; Minh v&agrave; th&agrave;nh phố Nha Trang. Kh&ocirc;ng gian được thiết kế th&ocirc;ng minh v&agrave; cung cấp tất cả c&aacute;c chức năng để bạn thỏa sức sang tạo v&agrave; sự đột ph&aacute; trong c&ocirc;ng việc v&agrave; đưa doanh nghiệp bạn l&ecirc;n một cấp độ mới.</p>\r\n\r\n<p>Ngo&agrave;i thiết kế th&ocirc;ng minh v&agrave; cung cấp c&aacute;c chức năng để bạn thỏa sức s&aacute;ng&nbsp; tạo v&agrave; tạo sự đột ph&aacute; trong c&ocirc;ng việc của bạn v&agrave; doanh nghiệp . ZikZak sẽ tạo ra cho bạn một kh&ocirc;ng gian thư gi&atilde;n được cung cấp&nbsp; tr&agrave; v&agrave; c&agrave; ph&ecirc; miễn phi , nh&agrave; bếp đầy đủ chức năng v&agrave; b&agrave;n ăn trưa với đồ ăn vặt.&nbsp;Ch&uacute;ng t&ocirc;i cũng cung cấp dịch vụ vệ sinh v&agrave; an ninh an to&agrave;n tuyệt đối để bận tận hưởng kh&ocirc;ng gian l&agrave;m việc thư gi&atilde;n v&agrave; thoải m&aacute;i nhất .</p>\r\n\r\n<p><img alt=\"ZikZak co-working space\" src=\"/local/storage/app/image/images/zikzak-co-working-space-1.jpg\" style=\"height:471px; width:730px\" /></p>\r\n\r\n<p>ZikZak Tp.Hồ Ch&iacute; Minh thuộc Tầng 5-6, t&ograve;a nh&agrave; CPhone, số 456 X&ocirc; Viết Nghệ Tĩnh ,Quận B&igrave;nh Thạnh với gi&aacute; dịch vụ giao động từ &nbsp;69.000 VND &ndash; 7.000.000 VND .</p>\r\n\r\n<p>Thời gian mở cửa : Từ 8h &ndash; 18h</p>\r\n\r\n<ul>\r\n	<li>G&oacute;i th&agrave;nh vi&ecirc;n ng&agrave;y : 69.000 VNĐ/ Ng&agrave;y</li>\r\n	<li>G&oacute;i th&agrave;nh vi&ecirc;n linh hoạt : 150.000 VNDD/3 Ng&agrave;y / Th&aacute;ng</li>\r\n</ul>\r\n\r\n<ol start=\"2\">\r\n	<li>\r\n	<p><span style=\"color:null\"><strong>Work Saigon</strong></span></p>\r\n	</li>\r\n</ol>\r\n\r\n<p>Work Saigon hay c&ograve;n gọi l&agrave; ( Ghiền Cafe) l&agrave; một trong những địa điểm l&agrave;m việc l&yacute; tưởng với kh&ocirc;ng gian l&agrave;m việc cổ điển, khi bạn đến đ&acirc;y bạn sẽ được thưởng thức những t&aacute;ch cafe thơm ngon v&agrave; kh&ocirc;ng gian l&agrave;m việc y&ecirc;n tĩnh ph&ugrave; hợp với những bạn th&iacute;ch sự nhẹ nh&agrave;ng v&agrave; cổ điển .</p>\r\n\r\n<p><img alt=\"\" src=\"/local/storage/app/image/images/working-space-work-sai-gon.jpg\" style=\"height:467px; width:700px\" /></p>\r\n\r\n<p>Nằm ở Điện Bi&ecirc;n Phủ, Quận 3, với gi&aacute;: 2.000.000 VNĐ l&agrave; một chỗ ngồi cố định &ndash; thời gian hoạt động từ 9h đến 21h đối với c&aacute;c ng&agrave;y thường, v&agrave; 9h đến 19h ng&agrave;y cuối tuần. Work Saigon được th&agrave;nh lập bởi hai người l&agrave;m trong lĩnh vực thiết kế, c&oacute; diện t&iacute;ch khoảng 400 m2 bao gồm 2 tầng được x&acirc;y dựng tr&ecirc;n một villa cũ theo kiểu Ph&aacute;p. Kh&ocirc;ng gian chung của Work Saigon l&agrave; c&aacute;c bức tường c&oacute; m&agrave;u gỗ than, v&agrave; cũng c&oacute; kh&aacute; đầy đủ những vật dụng cần thiết cho một văn ph&ograve;ng l&agrave;m việc. Kh&ocirc;ng gian mở ở tầng tr&ecirc;n d&agrave;nh cho c&aacute;c kh&aacute;ch thu&ecirc; chỗ ngồi cố định.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ol start=\"3\">\r\n	<li>\r\n	<p><span style=\"color:null\"><strong>Start Saigon</strong></span></p>\r\n	</li>\r\n</ol>\r\n\r\n<p>Bạn l&agrave; những t&iacute;n đồ c&ocirc;ng nghệ , Start Saigon ch&iacute;nh l&agrave; những g&igrave; bạn cần ở Start Saigon chủ&nbsp; yếu l&agrave; những t&iacute;n đồ y&ecirc;u c&ocirc;ng nghệ , v&agrave; l&agrave;m việc li&ecirc;n quan đến c&ocirc;ng nghệ từ đ&oacute; sẽ gi&uacute;p bạn c&oacute; thể kết nối lại với nhau tạo th&agrave;nh một cộng đồng l&agrave;m việc chung của c&aacute;c t&iacute;n đồ c&ocirc;ng nghệ .</p>\r\n\r\n<p><img alt=\"\" src=\"/local/storage/app/image/images/start-saigon-coworking.jpeg\" style=\"height:669px; width:1024px\" /></p>\r\n\r\n<p>Start Saigon nằm tr&ecirc;n đường Nguyễn Thi Minh Khai, Quận 1, đ&acirc;y l&agrave; nơi cho thu&ecirc; nơi l&agrave;m việc với gi&aacute; khoảng 1.500.000 VNĐ cho một chỗ ngồi cố định, v&agrave; hoạt động 24/7. Cũng giống như Saigon Coworking c&oacute; 4 tầng, kh&ocirc;ng gian l&agrave;m việc bắt đầu từ tầng 1 trở l&ecirc;n. Phần lớn c&aacute;c bạn l&agrave;m việc ở The Start Center for Entrepreneurs l&agrave; trong lĩnh vực c&ocirc;ng nghệ th&ocirc;ng tin.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ol start=\"4\">\r\n	<li>\r\n	<p><span style=\"color:null\"><strong>Dreamplex</strong></span></p>\r\n	</li>\r\n</ol>\r\n\r\n<p>Với kh&ocirc;ng gian l&agrave;m việc hiện đại, đam chất c&ocirc;ng nghệ v&agrave; thiết kế mang hơi thở Phương T&acirc;y . Nếu bạn th&iacute;ch một kh&ocirc;ng gian hiện đại v&agrave; theo phong c&aacute;ch T&acirc;y &Acirc;u Dreamplex sẽ l&agrave; mang cho bạn một kh&ocirc;ng gian tuyệt vời .</p>\r\n\r\n<p><img alt=\"\" src=\"/local/storage/app/image/images/dreamplex-company.jpeg\" style=\"height:600px; width:962px\" /></p>\r\n\r\n<p>Dreamplex nằm tr&ecirc;n đường Điện Bi&ecirc;n Phủ, quận B&igrave;nh Thạnh, đ&acirc;y l&agrave; nơi cho thu&ecirc; nơi l&agrave;m việc với gi&aacute; khoảng 16-18USD cho mỗi m2. Ph&ugrave; hợp với hầu hết c&aacute;c đối tượng</p>\r\n\r\n<ol start=\"5\">\r\n	<li>\r\n	<p><span style=\"color:null\"><strong>FunWork</strong></span></p>\r\n	</li>\r\n</ol>\r\n\r\n<p>Ra đời ng&agrave;y 27/1/2015,&nbsp;Funwork l&agrave; một trong những Co-Working Space lớn tại TP HCM với tổng diện t&iacute;ch sử dụng gần 400m2, được chia th&agrave;nh nhiều khu vực kh&aacute;c nhau tương ứng cho từng nhu cầu l&agrave;m việc nh&oacute;m, g&oacute;c ri&ecirc;ng tư hay tiếp kh&aacute;ch.</p>\r\n\r\n<p>N&eacute;t đặc biệtcủa Funwork l&agrave; khu vực l&agrave;m việc ri&ecirc;ng tư (Private zone) được thiết kế bao gồm c&aacute;c v&aacute;ch ngăn di động, tạo sự thoải m&aacute;i cho c&aacute;c c&aacute; nh&acirc;n kh&ocirc;ng muốn bị ảnh hưởng bởi c&aacute;c yếu tố b&ecirc;n ngo&agrave;i.</p>\r\n\r\n<p><img alt=\"\" src=\"/local/storage/app/image/images/funwork-co.jpeg\" style=\"height:900px; width:1460px\" /></p>\r\n\r\n<p>Mức gi&aacute; cho c&aacute;c g&oacute;i thu&ecirc; theo giờ hoặc th&aacute;ng tại Funword sẽ dao động trong khoảng từ 60.000VNĐ &ndash; 2.500.000 VNĐ. B&ecirc;n cạnh c&aacute;c tiện &iacute;ch như: Ph&ograve;ng họp nhỏ, locker v&agrave; in ấn, đến với Funwork c&aacute;c co-worker sẽ được phục vụ miễn ph&iacute; tr&agrave; đ&aacute; v&agrave; thoải m&aacute;i sử dụng khu vực gym.</p>\r\n\r\n<p style=\"text-align:right\">--Tổng hợp bởi ZikZak--</p>\r\n\r\n<p>&nbsp;</p>', 'co-working, hồ chí minh, co-working space, sài gòn, zikzak', '2018-05-28 04:45:30', '2018-07-01 20:12:33'),
(3, 'Đặc điểm nổi bật của co-working space và văn phòng chia sẻ', 'dac-diem-noi-bat-cua-co-working-space-va-van-phong-chia-se', 'Mô hình Co Working – Space ( hay còn gọi là không gian làm việc chung ) và văn phòng chia sẻ là những mô hình về không gian làm việc đang khá mới mẻ tại Việt Nam, các cá nhân, tổ chức, doanh nghiệp còn khá lẫm khi nghe đến các không gian làm việc này. Vậy mô hình co-working space là gì? Văn phòng chia sẻ là gì? Nó khác gì so với văn phòng truyền thống và các loại hình văn phòng khác. Những mô hình này phù hợp với những cá nhân, tổ chức nào? Zik-Zak sẽ cùng bạn đi giải đáp những thắc mắc trên.', 'zz_2018-06-01_1527840480.jpeg', '<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Với c&aacute; nh&acirc;n, tổ chức start-up, nhỏ v&agrave; c&ograve;n trẻ, b&agrave;i to&aacute;n chi ph&iacute; lu&ocirc;n l&agrave; một b&agrave;i to&aacute;n g&acirc;y đau đầu. Việc bỏ ra một chi ph&iacute; lớn để thu&ecirc; một văn ph&ograve;ng dịch vụ đầy đủ tiện &iacute;ch tại những th&agrave;nh phố đắt đỏ như H&agrave; Nội hay TP.HCM l&agrave; một trở ngại kh&ocirc;ng hề nhỏ. Sự ra đời của co-working space, văn ph&ograve;ng chia sẻ, văn ph&ograve;ng ảo gi&uacute;p cho c&aacute;c c&aacute; nh&acirc;n, tổ chức vượt qua trở ngại n&agrave;y.</span></span><br />\r\n&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>1. Co-working space l&agrave; g&igrave;&nbsp;?</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><a href=\"http://zikzak-co.vn/\" target=\"_blank\">M&ocirc; h&igrave;nh co-working Space</a> hay c&ograve;n gọi l&agrave; kh&ocirc;ng gian l&agrave;m việc chung l&agrave; một m&ocirc; h&igrave;nh mới xuất hiện gần đ&acirc;y tại Việt Nam. Co-working space cung cấp một<u> </u>kh&ocirc;ng gian l&agrave;m việc chung với đầy đủ những tiện nghi cần thiết như một văn ph&ograve;ng truyền thống để c&aacute;c th&agrave;nh vi&ecirc;n c&oacute; thể l&agrave;m việc hiệu quả, c&aacute;c th&agrave;nh vi&ecirc;n trong m&ocirc; h&igrave;nh co-working space c&oacute; thể đến từ nhiều ng&agrave;nh nghề, lĩnh vực kh&aacute;c nhau. C&ugrave;ng l&agrave;m việc chung trong một kh&ocirc;ng gian để c&oacute; thể t&igrave;m sự tương t&aacute;c, trao đổi c&aacute;c &yacute; tưởng v&agrave; hợp t&aacute;c trong c&aacute;c lĩnh vực, c&ocirc;ng việc kh&aacute;c nhau với chi ph&iacute; tiết kiệm, ph&ugrave; hợp với điều kiện với c&aacute;c c&aacute; nh&acirc;n tổ chức start-up, freelancer.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"Coworking Space\" src=\"/local/storage/app/image/images/zik-zak-van-phong-ao.png\" style=\"height:399px; width:603px\" /></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Coworking space - Kh&ocirc;ng gian l&agrave;m việc chung, bao gồm hai th&agrave;nh phần ch&iacute;nh: </span></span></p>\r\n\r\n<h2 style=\"font-style:italic\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">1. C&aacute;c tiện &iacute;ch văn ph&ograve;ng cần thiết</span></span></h2>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Để trở th&agrave;nh một nơi l&agrave;m việc hiệu quả, co-working space phải đảm bảo, đ&aacute;p ứng những tiện &iacute;ch cần thiết. Th&ocirc;ng thường co-working space cung cấp một chỗ ngồi cố định cho th&agrave;nh vi&ecirc;n ngồi l&agrave;m việc v&agrave; những chỗ ngồi tự do cho kh&aacute;ch gh&eacute; qua, đi k&egrave;m với c&aacute;c tiện &iacute;ch như m&aacute;y in, internet, điều h&ograve;a, c&aacute;c dịch vụ ăn uống....., c&aacute;c dịch vụ đi k&egrave;m nhiều hay &iacute;t phụ thuộc v&agrave;o chi ph&iacute; đầu tư</span></span></p>\r\n\r\n<h2 style=\"font-style:italic\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">2. T&iacute;nh cộng đồng</span></span></h2>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Đọc đến đ&acirc;y nhiều người sẽ h&igrave;nh dung co-working space như l&agrave; việc chia nhỏ mặt bằng một văn ph&ograve;ng truyền thống để cho thu&ecirc;. Nhưng sự kh&aacute;c biệt của co-working space l&agrave; <a href=\"http://zikzak-co.vn/community\" target=\"_blank\">t&iacute;nh cộng đồng</a>. Một kh&ocirc;ng gian l&agrave;m việc chung chuy&ecirc;n nghiệp c&oacute; khả năng chọn lọc th&agrave;nh vi&ecirc;n, tạo sự tương t&aacute;c giữa c&aacute;c th&agrave;nh vi&ecirc;n đến từ nhiều ng&agrave;nh nghề, lĩnh vực kh&aacute;c nhau. Từ đ&oacute; mở ra c&aacute;c cơ hội hợp t&aacute;c l&agrave;m việc, thay v&igrave; bạn phải đi ra ngo&agrave;i v&agrave; t&igrave;m kiếm c&aacute;c mối quan hệ để hợp t&aacute;c. Bạn c&oacute; thể t&igrave;m kiếm đối t&aacute;c ngay ch&iacute;nh tại nơi m&igrave;nh l&agrave;m việc</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:14px\"><em>---<a href=\"http://zikzak-co.vn/news-detail/nhung-co-working-space-noi-tieng-voi-khong-gian-lam-viec-ly-tuong-o-tp-ho-chi-minh\" target=\"_blank\">Xem th&ecirc;m: Những co-working space c&oacute; kh&ocirc;ng gian đẹp tại TP HCM</a></em></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Hiện m&ocirc; h&igrave;nh kinh doanh sử dụng m&ocirc; h&igrave;nh co-working space tại TP.HCM v&agrave; H&agrave; Nội. Sự b&ugrave;ng nổ của n&oacute; trong những năm gần đ&acirc;y dự b&aacute;o đ&acirc;y sẽ l&agrave; xu thế của c&aacute;c doanh nghiệp mới trong tương lai</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"Văn phòng chia sẻ\" src=\"/local/storage/app/image/images/van-phong-chia-se.jpeg\" style=\"height:483px; width:730px\" /></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">2. <strong>Văn ph&ograve;ng chia sẻ l&agrave; g&igrave;? Văn ph&ograve;ng ảo l&agrave; g&igrave;? Co &ndash;Working Space v&agrave; văn ph&ograve;ng chia sẻ kh&aacute;c nhau như thế n&agrave;o ?</strong></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><a href=\"http://zikzak-co.vn/virtual\" target=\"_blank\">Văn ph&ograve;ng chia sẻ</a> &nbsp;hay c&ograve;n gọi l&agrave; văn ph&ograve;ng ảo l&agrave; kh&aacute;i niệm về loại văn ph&ograve;ng cho ph&eacute;p nhiều c&aacute; nh&acirc;n, c&ocirc;ng ty c&ugrave;ng sở hữu hoặc quản l&yacute;.</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Nếu Coworking Space cung cấp cho cho c&aacute;c th&agrave;nh vi&ecirc;n một nơi để l&agrave;m việc, t&igrave;m kiếm v&agrave; gặp đối t&aacute;c, kh&aacute;ch h&agrave;ng th&igrave; văn ph&ograve;ng chia sẻ l&agrave; nơi cung cấp một địa chỉ kinh doanh cho c&ocirc;ng ty. </span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Bạn muốn c&ocirc;ng ty c&oacute; một địa chỉ để đăng k&yacute; kinh doanh, để khai b&aacute;o đ&oacute;ng thuế hoặc đề giao dịch với kh&aacute;ch h&agrave;ng. doanh nghiệp của bạn c&ograve;n nhỏ, chi ph&iacute; của một văn ph&ograve;ng ở một th&agrave;nh phố lớn l&agrave; qu&aacute; đắt đỏ v&agrave; doanh nghiệp của bạn kh&ocirc;ng khai th&aacute;c được hết những cơ sở vật chất m&agrave; văn ph&ograve;ng đ&oacute; cung cấp. M&ocirc;t v&agrave;i c&ocirc;ng ty kh&aacute;c cũng c&oacute; nhu cầu tương tự, v&agrave; c&aacute;c b&ecirc;n hợp t&aacute;c lại với nhau chia sẻ d&ugrave;ng chung một văn ph&ograve;ng, gi&uacute;p chi ph&iacute; giảm xuống, v&agrave; việc khai th&aacute;c cơ sở vật chất hiệu quả hơn.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"Mô hình Coworking Space \" src=\"/local/storage/app/image/images/cho-thue-van-phong-ao-zikzak.jpeg\" style=\"height:468px; width:750px\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><a href=\"http://zikzak-co.vn/virtual\" target=\"_blank\">Văn ph&ograve;ng ảo</a> ra đời theo những nhu cầu như tr&ecirc;n, văn ph&ograve;ng ảo&nbsp;c&oacute; sẵn nh&acirc;n vi&ecirc;n lễ t&acirc;n. Bạn kh&ocirc;ng cần phải chi trả c&aacute;c tiện &iacute;ch cơ bản như điện, nước, internet, điện thoại, <a href=\"http://zikzak-co.vn/meeting-room\" target=\"_blank\">ph&ograve;ng họp</a>&nbsp;v&agrave; c&aacute;c tiện &iacute;ch kh&aacute;c, gi&uacute;p cắt giảm đ&aacute;ng kể chi ph&iacute; bỏ ra. Bạn c&oacute; thể sử dụng văn ph&ograve;ng l&agrave;m nơi giao dịch, gặp gỡ kh&aacute;ch h&agrave;ng. V&agrave; doanh nghiệp cũng hoạt động rất chuy&ecirc;n nghiệp như l&agrave;m ở văn ph&ograve;ng truyền thống, văn ph&ograve;ng ảo (văn ph&ograve;ng chia sẻ) ph&ugrave; hợp với những doanh nghiệp mới, giải quyết b&agrave;i to&aacute;n chi ph&iacute; v&agrave; thời gian, gi&uacute;p c&aacute;c doanh nghiệp tập trung hơn v&agrave;o c&ocirc;ng việc của m&igrave;nh</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Hiện n&agrave;y c&aacute;c Co-Working Space thường t&iacute;ch hợp cả văn ph&ograve;ng chia sẻ để nhằm phục vụ nhu cầu của c&aacute;c SME, Startup, Freelancer .</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"Mô hình văn phòng chia sẻ\" src=\"/local/storage/app/image/images/ZikZak-cho-thue-van-phong.jpeg\" style=\"height:500px; width:760px\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>3. Những c&aacute; nh&acirc;n, tổ chức n&agrave;o n&ecirc;n chọn co-working space hoặc văn ph&ograve;ng chia sẻ</strong></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Với c&aacute;c c&aacute; nh&acirc;n, tổ chức start-up, người l&agrave;m freelancer, kh&ocirc;ng gian l&agrave;m việc chung co-working space l&agrave; nơi ho&agrave;n hảo để l&agrave;m việc, phong c&aacute;ch l&agrave;m việc chuy&ecirc;n nghiệp, chi ph&iacute; tiết kiệm v&agrave; c&oacute; cơ hội t&igrave;m kiếm những c&aacute; nh&acirc;n, tổ chức c&ugrave;ng mục đ&iacute;ch, t&igrave;m kiếm được cơ hội hợp t&aacute;c l&agrave;m việc, chia sẻ c&aacute;c &yacute; tưởng ngay ch&iacute;nh tại nơi m&igrave;nh l&agrave;m việc, l&agrave; một cộng đồng năng động s&aacute;ng tạo</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Văn ph&ograve;ng ảo, văn ph&ograve;ng chia sẻ ph&ugrave; hợp với c&aacute;c doanh nghiệp vừa v&agrave; nhỏ, vốn lu&ocirc;n gặp kh&oacute; khăn với việc thu&ecirc; một văn ph&ograve;ng gi&aacute; rẻ ph&ugrave; hợp với quy m&ocirc; của m&igrave;nh. C&aacute;c c&aacute; nh&acirc;n, tổ chức start-up cũng c&oacute; thể chọn văn ph&ograve;ng ảo để l&agrave;m văn ph&ograve;ng đại diện, với một địa chỉ tốt để quảng b&aacute; h&igrave;nh ảnh m&agrave; kh&ocirc;ng tốn qu&aacute; nhiều chi ph&iacute;. Đặt nền tảng cho sự ph&aacute;t triển của c&ocirc;ng ty, doanh nghiệp m&igrave;nh sau n&agrave;y.</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:14px\"><a href=\"https://www.google.com/maps/place/CPhone+Tower/@10.8072173,106.7096213,17z/data=!3m1!4b1!4m5!3m4!1s0x317528a32c8336eb:0x448dfd63cc536d8a!8m2!3d10.807212!4d106.71181\" target=\"_blank\"><em>--Xem th&ecirc;m: Những địa điểm co-working space thu h&uacute;t cộng đồng khởi nghiệp tại H&agrave; Nội</em></a></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">C&aacute;c doanh nghiệp c&oacute; nhu cầu mở rộng kinh doanh, c&aacute;c doanh nghiệp nước ngo&agrave;i muốn th&acirc;m nhập thị trường, muốn t&igrave;m một văn ph&ograve;ng thu&ecirc; trong thời gian ngắn, c&oacute; sẵn c&aacute;c tiện &iacute;ch để kh&ocirc;ng phải mất thời gian, chi ph&iacute; x&acirc;y dựng từ đầu một văn ph&ograve;ng mới th&igrave; văn ph&ograve;ng chia sẻ l&agrave; một sự lựa chọn kh&ocirc;ng thể tốt hơn. Dễ d&agrave;ng chia sẻ, hợp t&aacute;c với c&aacute;c c&ocirc;ng ty, doanh nghiệp kh&aacute;c để ph&aacute;t triển c&ocirc;ng ty của m&igrave;nh</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Văn ph&ograve;ng chia sẻ v&agrave; co-working space mang lại rất nhiều lợi &iacute;ch cho c&aacute;c doanh nghiệp, t&ugrave;y v&agrave;o quy m&ocirc;, đặc điểm của c&ocirc;ng ty, doanh nghiệp m&igrave;nh m&agrave; c&aacute;c bạn đưa ra được sự lựa chọn tối ưu nhất</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\"><img alt=\"\" src=\"/local/storage/app/image/images/zikzak-co-working-space-1(1).jpg\" style=\"height:500px; width:750px\" /></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Zik-Zak co-working space cung cấp c&aacute;c giải ph&aacute;p về cho thu&ecirc; văn ph&ograve;ng chia sẻ, văn ph&ograve;ng ảo, cho thu&ecirc; văn ph&ograve;ng gi&aacute; rẻ, cho thu&ecirc; kh&ocirc;ng gian co-working space gi&aacute; rẻ tại quận B&igrave;nh Thạnh, TP HCM. Đội ngũ nh&acirc;n vi&ecirc;n của Zik-Zak co-working space lu&ocirc;n sẵn s&agrave;ng tư vấn v&agrave; giải đ&aacute;p c&aacute;c thắc mắc</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:16px\"><a href=\"http://zikzak-co.vn/experience\" target=\"_blank\">Đăng k&yacute; ngay</a> để trải nghiệm một ng&agrave;y l&agrave;m việc miễn ph&iacute; tại ZikZak</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\">&nbsp;</p>', 'văn phòng chia sẻ, văn phòng ảo, đặc điểm, coworking space, coworking space là gì', '2018-06-01 01:07:59', '2018-07-01 19:31:53'),
(4, 'SALE CHÀO HÈ - VĂN PHÒNG ZIKZAK GIẢM GIÁ 50%', 'sale-chao-he-van-phong-zikzak-giam-gia-50', 'Hòa chung không khí nhộn nhịp những ngày hè tháng 6, ZIKZAK CO – WOKING SPACE đã ra mắt dịch vụ văn phòng mở và chính thức đi vào hoạt động. Trong suốt quá trình hình thành, đội ngũ nhân viên ZIKZAK CO – WOKING SPACE đã không ngừng tìm tòi, nghiên cứu nhằm cung cấp những dịch vụ tối ưu nhất cho người sử dụng thông qua không gian làm việc thông minh, chuyên nghiệp nhưng không kém phần năng động để bạn thỏa sức sáng tạo, kiến tạo nên những giá trị bền vững.', 'zz_2018-06-15_1529092018.png', '<p><span style=\"font-size:16px\"><span style=\"color:#000000\">Tưng bừng ch&agrave;o đ&oacute;n m&ugrave;a h&egrave; c&ugrave;ng WORLD CUP 2018,&nbsp; <a href=\"http://zikzak-co.vn\" target=\"_blank\">ZIKZAK CO &ndash; WOKING SPACE</a> d&agrave;nh tặng h&agrave;ng loạt ưu đ&atilde;i si&ecirc;u hấp dẫn cho qu&yacute; kh&aacute;ch h&agrave;ng sử dụng dịch vụ trong th&aacute;ng 6.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"color:#000000\">Lựa chọn vị thế nằm ngay trung t&acirc;m TPHCM v&agrave; Nha Trang, những khu vực được coi l&agrave; &ldquo;miền đất hứa&rdquo; đ&atilde; được nhiều doanh nghiệp đặt trụ sở kinh doanh, ZIKZAK CO &ndash; WOKING SPACE kh&ocirc;ng chỉ giải quyết nhu cầu về vị thế m&agrave; c&ograve;n gi&uacute;p c&aacute;c doanh nghiệp, Start &ndash; up dễ d&agrave;ng kết nối, giao dịch v&agrave; mở rộng hợp t&aacute;c khi đặt văn ph&ograve;ng đại diện tại ZIKZAK.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"color:#000000\">Với c&aacute;c g&oacute;i dịch vụ linh hoạt c&ugrave;ng mức gi&aacute; ph&ugrave; hợp cho mọi đối tượng c&oacute; nhu cầu thu&ecirc; từ kh&ocirc;ng gian l&agrave;m việc chia sẻ đến g&oacute;i dịch vụ theo từng tuần, từng th&aacute;ng hay thu&ecirc; văn ph&ograve;ng cố định theo năm, <a href=\"http://zikzak-co.vn/service\" target=\"_self\">tư vấn th&agrave;nh lập c&ocirc;ng ty</a>, doanh nghiệp&nbsp;Zikzak Co-working space sẽ l&agrave; kh&ocirc;ng gian l&agrave;m việc đa năng, chuy&ecirc;n nghiệp đ&aacute;p ứng mọi nhu cầu, ph&ugrave; hơp với nguồn t&agrave;i ch&iacute;nh sẵn c&oacute; của qu&yacute; kh&aacute;ch m&agrave; c&ograve;n gi&uacute;p bạn tiết kiệm đến 80% chi ph&iacute;.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"color:#000000\">C&ograve;n chần chừ g&igrave; nữa m&agrave; kh&ocirc;ng đến với Zikzak co &ndash; woking space để được trải nghiệm kh&ocirc;ng gian l&agrave;m việc năng động, hứng khởi với mức gi&aacute; si&ecirc;u &ldquo;cheap &ndash; b&ocirc;ng&rdquo;. Zikzak tự tin sẽ l&agrave; đơn vị đ&aacute;p ứng mọi nhu cầu của qu&yacute; kh&aacute;ch trong suốt qu&aacute; tr&igrave;nh khởi nghiệp.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"color:#000000\">1. Dịch vụ <a href=\"http://zikzak-co.vn/virtual\" target=\"_blank\">cho thu&ecirc; văn ph&ograve;ng</a> ri&ecirc;ng, văn ph&ograve;ng cố định&nbsp;tại quận B&igrave;nh Thạnh với gi&aacute; 9.000.000&nbsp; VNĐ/th&aacute;ng, nay chỉ c&ograve;n 7.000.000 VNĐ/th&aacute;ng.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"color:#000000\">2. <a href=\"http://zikzak-co.vn/virtual\" target=\"_self\">Văn ph&ograve;ng ảo</a> hay văn ph&ograve;ng đại diện hiện đang c&oacute; 2 g&oacute;i dịch vụ d&agrave;nh cho những c&ocirc;ng ty v&agrave; start &ndash; up muốn t&igrave;m kiếm địa điểm văn ph&ograve;ng l&agrave;m việc với chi ph&iacute; thấp nhưng vẫn đảm bảo đ&aacute;p ứng được những y&ecirc;u cầu của một văn ph&ograve;ng chuy&ecirc;n nghiệp chỉ với 299.000 VNĐ v&agrave; 599.000VNĐ.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"color:#000000\">B&ecirc;n cạnh đ&oacute;, qu&yacute; kh&aacute;ch đến với Zikzak c&ograve;n được miễn ph&iacute; trải nghiệm một ng&agrave;y l&agrave;m việc tại văn ph&ograve;ng c&ocirc;ng ty.</span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"color:#000000\"><strong>GI&Aacute; RẺ BẤT NGỜ, ƯU Đ&Atilde;I GIẢM 50% &ndash; CHỈ TRONG TH&Aacute;NG 6</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"color:#000000\">Người người World Cup, nh&agrave; nh&agrave; World Cup, qu&yacute; kh&aacute;ch c&ograve;n ngại g&igrave; m&agrave; kh&ocirc;ng đặt ch&acirc;n đến ZIKZAK trốn c&aacute;i nắng h&egrave; 39 độ để tận hưởng kh&ocirc;ng gian &ldquo;m&aacute;t rượi&rdquo; để tập trung hết m&igrave;nh cho sự nghiệp giữa t&acirc;m b&atilde;o b&oacute;ng đ&aacute; ngo&agrave;i kia!</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"color:#000000\">Qu&yacute; kh&aacute;ch h&agrave;ng muốn trải nghiệm c&oacute; thể đăng k&yacute; online tại link:&nbsp;<a href=\"http://zikzak-co.vn/experience\">http://zikzak-co.vn/experience</a></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"color:#000000\"><strong>Mọi th&ocirc;ng tin chi tiết xin li&ecirc;n hệ:</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"color:#000000\">ZIKZAK Co-working Space, Lầu 5-6, t&ograve;a nh&agrave; CPHONE, 456 X&ocirc; Viết Nghệ Tĩnh, phường 25, Quận B&igrave;nh Thạnh, Hồ Ch&iacute; Minh.</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"color:#000000\">Phone: +84 922 383 097</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"color:#000000\">Email: </span><a href=\"http://info@zikzak-co.vn\"><span style=\"color:#000000\">info@zikzak-co.vn</span></a></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"color:#000000\">Rất h&acirc;n hạnh được phục vụ qu&yacute; kh&aacute;ch!</span></span></p>', 'hồ chí minh, Co-working space, văn phòng ảo, văn phòng đại diện, văn phòng cho thuê', '2018-06-15 12:46:57', '2018-07-02 18:59:29');
INSERT INTO `news` (`news_id`, `news_name`, `news_slug`, `news_summary`, `news_image`, `news_content`, `tag`, `created_at`, `updated_at`) VALUES
(5, 'Vai trò của việc phát triển thương hiệu đối với doanh nghiệp', 'vai-tro-cua-viec-phat-trien-thuong-hieu-doi-voi-doanh-nghiep', 'Zikzak là một trong những Startup đã thành công trong việc xây dựng Thương Hiệu trên thị trường, chính vì thề trong bài viết này Zikzak-Co sẽ giúp các bạn có cái nhìn tổng quan hơn về thương hiệu doanh nghiệp, và các yếu tố quyết định sự thành công của một thương hiệu.', 'zz_2018-06-19_1529373169.jpeg', '<p><span style=\"font-size:16px\"><strong>Zikzak</strong> -&nbsp;Thương hiệu l&agrave; g&igrave;? V&agrave; v&igrave; sao thương hiệu lại rất quan trọng đối với bất k&igrave; doanh nghiệp n&agrave;o?</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Về cơ bản, thương hiệu kh&ocirc;ng đơn giản chỉ l&agrave; một c&aacute;i logo đẹp được biết đến rộng r&atilde;i như những g&igrave; nhiều người vẫn nghĩ. Ch&iacute;nh x&aacute;c hơn, khi n&oacute;i đến thương hiệu tức l&agrave; n&oacute;i đến to&agrave;n bộ trải nghiệm của kh&aacute;ch h&agrave;ng đối với sản phẩm v&agrave; dịch vụ của doanh nghiệp ấy từ logo, website, tương t&aacute;c tr&ecirc;n mạng x&atilde; hội, tới c&aacute;ch nh&acirc;n vi&ecirc;n trả lời điện thoại v&agrave; tiếp kh&aacute;ch v&agrave; nhiều nhiều thứ nữa.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">T&oacute;m lại, thương hiệu l&agrave; c&aacute;ch m&agrave; kh&aacute;ch h&agrave;ng nhận diện v&agrave; thấu hiểu doanh nghiệp của bạn.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Một thương hiệu th&agrave;nh c&ocirc;ng kh&ocirc;ng tự nhi&ecirc;n m&agrave; xuất hiện, đ&oacute; l&agrave; kết quả của tư duy ch&iacute;nh x&aacute;c v&agrave; những kế hoạch chi tiết tỉ mỉ. H&atilde;y c&ugrave;ng xem x&eacute;t những lợi &iacute;ch v&ocirc; gi&aacute; thương hiệu c&oacute; thể mang đến cho doanh nghiệp qua những v&iacute; dụ sau đ&acirc;y.</span></p>\r\n\r\n<h2><strong><span style=\"font-size:16px\">1. Thương hiệu gi&uacute;p mọi người nhận ra sản phẩm v&agrave; c&ocirc;ng ty&nbsp;của bạn.</span></strong></h2>\r\n\r\n<p><span style=\"font-size:16px\">Thương hiệu kh&ocirc;ng chỉ l&agrave; c&aacute;i t&ecirc;n hay logo m&agrave; l&agrave; tất cả những g&igrave; gi&uacute;p doanh nghiệp của bạn kh&aacute;c biệt với những c&ocirc;ng ti kh&aacute;c. Thương hiệu c&oacute; thể được nhận diện dễ d&agrave;ng qua một v&agrave;i ti&ecirc;u ch&iacute; sau:</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&bull;&nbsp;Bề ngo&agrave;i của thương hiệu (logo, website hay m&agrave;u sắc chỉ l&agrave; một v&agrave;i v&iacute; dụ).</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&bull; C&aacute;c k&ecirc;nh quảng c&aacute;o v&agrave; giao tiếp.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&bull; Sản phẩm v&agrave; bao b&igrave;.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">&bull; Trải nghiệm mua h&agrave;ng.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img src=\"/local/storage/app/image/images/image.png\" style=\"height:389px; width:400px\" /></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><strong>Gi&aacute; b&aacute;n</strong>.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">H&atilde;y nghĩ thương hiệu như l&agrave; một con người v&agrave; mỗi người th&igrave; c&oacute; một c&aacute; t&iacute;nh, gi&aacute; trị, c&aacute;ch ăn mặc, c&aacute;ch giao tiếp, mạng lưới bạn b&egrave; v&agrave; những c&acirc;u chuyện kh&aacute;c nhau để kể. Đ&oacute; l&agrave; những g&igrave; tạo n&ecirc;n một con người, v&agrave; cũng l&agrave; những g&igrave; tạo n&ecirc;n một thương hiệu.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Một v&iacute; dụ ti&ecirc;u biểu cho sự th&agrave;nh c&ocirc;ng của việc nhận diện thương hiệu th&ocirc;ng qua bề ngo&agrave;i l&agrave; Minute Maid. Năm 2009, Minute Maid thiết kế lại to&agrave;n bộ bề ngo&agrave;i, c&aacute;ch đ&oacute;ng g&oacute;i v&agrave; d&aacute;n nh&atilde;n cho to&agrave;n bộ sản phẩm để đảm bảo sản phẩm của họ dễ d&agrave;ng được t&igrave;m thấy tr&ecirc;n quầy. Hơn thế nữa, Minute Maid sử dụng một vẻ ngo&agrave;i đồng nhất cho tất cả c&aacute;c sản phẩm của m&igrave;nh, kể cả ở những đất nước v&agrave; nền văn ho&aacute; kh&aacute;c nhau.</span></p>\r\n\r\n<p><strong><span style=\"font-size:16px\">2. Thương hiệu tạo ra sự kh&aacute;c biệt cho doanh nghiệp của bạn tr&ecirc;n thị trường.</span></strong></p>\r\n\r\n<p><span style=\"font-size:16px\">Tr&ecirc;n thực tế, kh&aacute;ch h&agrave;ng kh&ocirc;ng c&oacute; r&agrave;ng buộc đối với một sản phẩm cụ thể n&agrave;o, thứ m&agrave; họ gắn b&oacute; v&agrave; trung th&agrave;nh l&agrave; thương hiệu. Nếu như kh&ocirc;ng c&oacute; một thương hiệu nổi trội n&agrave;o gắn với sản phẩm nước đ&oacute;ng chai, kh&aacute;ch h&agrave;ng sẽ chỉ mua đi mua nước, v&agrave; loại nước n&agrave;o cũng thế v&igrave; ch&uacute;ng đều như vậy cả.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img src=\"/local/storage/app/image/images/image(1).png\" style=\"height:240px; width:400px\" /></span></p>\r\n\r\n<p><span style=\"font-size:16px\">V&agrave; thương hiệu l&agrave; thứ tạo ra sự kh&aacute;c biệt trong lựa chọn của người ti&ecirc;u d&ugrave;ng, trở th&agrave;nh l&iacute; do để họ bước v&agrave;o si&ecirc;u thị v&agrave; mua đ&uacute;ng chai nước của bạn chứ kh&ocirc;ng phải của những doanh nghiệp kh&aacute;c. Chưa chắc chai nước của bạn đ&atilde; c&oacute; vị kh&aacute;c biệt, nhưng kh&aacute;ch h&agrave;ng lại cảm thấy gắn b&oacute; v&agrave; th&acirc;n quen với thương hiệu của bạn hơn. Tất cả yếu tố từ những lời hứa hẹn của bạn đến th&aacute;i độ người b&aacute;n h&agrave;ng đến vẻ ngo&agrave;i của c&aacute;i chai nước, đều sẽ cộng hưởng để tạo n&ecirc;n quyết định mua h&agrave;ng của người ti&ecirc;u d&ugrave;ng.</span></p>\r\n\r\n<p><strong><span style=\"font-size:16px\">3. Thương hiệu gi&uacute;p doanh nghiệp kết nối cảm x&uacute;c với kh&aacute;ch h&agrave;ng.</span></strong></p>\r\n\r\n<p><span style=\"font-size:16px\">Ph&aacute;t triển thương hiệu c&ograve;n gi&uacute;p tạo dựng niềm tin ở kh&aacute;ch h&agrave;ng, tạo dựng sự trung th&agrave;nh v&agrave; đảm bảo họ sẽ tiếp tục quay trở lại. Một thương hiệu sẽ bao h&agrave;m nhiều những gi&aacute; trị kh&aacute;c nhau v&agrave; kh&aacute;ch h&agrave;ng sẽ t&igrave;m thấy m&igrave;nh gắn b&oacute; với một hay nhiều những gi&aacute; trị đ&oacute;. Điều n&agrave;y sẽ h&igrave;nh th&agrave;nh sự kết nối cảm x&uacute;c giữa doanh nghiệp v&agrave; kh&aacute;ch h&agrave;ng.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Nike l&agrave; một v&iacute; dụ ti&ecirc;u biểu trong trường hợp n&agrave;y. Nike gắn b&oacute; thương hiệu của m&igrave;nh với những vận động vi&ecirc;n h&agrave;ng đầu thế giới c&ugrave;ng với slogan &lsquo;Just do it&rsquo;. Rất nhiều người mua đồ của Nike v&igrave; sản phẩm của Nike kh&ocirc;ng chỉ khiến bạn trở n&ecirc;n hợp thời trang m&agrave; c&ograve;n mang lại cảm gi&aacute;c bạn c&oacute; thể l&agrave;m được mọi thứ. Khả năng ti&ecirc;u thụ của thị trường đ&ocirc;i khi phụ thuộc rất nhiều v&agrave;o những phản ứng cảm t&iacute;nh như thế.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><strong>4. Th&ocirc;ng qua thương hiệu, kh&aacute;ch h&agrave;ng biết được họ c&oacute; thể tr&ocirc;ng chờ điều g&igrave; v&agrave;o sản phẩm của bạn.</strong></span></p>\r\n\r\n<p><span style=\"font-size:16px\">Một doanh nghiệp lu&ocirc;n quảng b&aacute; thương hiệu của m&igrave;nh một c&aacute;ch r&otilde; r&agrave;ng v&agrave; lu&ocirc;n giữ uy t&iacute;n tr&ecirc;n thị thường, theo thời gian sẽ x&acirc;y dựng được một lượng lớn kh&aacute;ch h&agrave;ng trung th&agrave;nh. Một thương hiệu mạnh c&oacute; thể được v&iacute; như một lối tắt của doanh nghiệp để đi đến quyết định mua h&agrave;ng của người ti&ecirc;u d&ugrave;ng. Những th&ocirc;ng điệp được truyền tải r&otilde; r&agrave;ng, ổn định cũng với những trải nghiệm t&iacute;ch cực về sản phẩm sẽ khiến người mua h&agrave;ng cảm thấy dễ d&agrave;ng hơn khi chọn lựa, v&igrave; họ biết ch&iacute;nh x&aacute;c họ sẽ nhận được điều g&igrave; khi gắn b&oacute; với thương hiệu ấy.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img src=\"/local/storage/app/image/images/image(2).png\" style=\"height:300px; width:400px\" /></span></p>\r\n\r\n<p><span style=\"font-size:16px\">V&iacute; dụ như khi đề cập đến một đ&ocirc;i gi&agrave;y m&ugrave;a đ&ocirc;ng dễ chịu thoải m&aacute;i, thương hiệu đầu ti&ecirc;n c&oacute; thể sẽ xuất hiện trong đầu bạn l&agrave; UGG. Nh&atilde;n h&agrave;ng n&agrave;y chuy&ecirc;n cung cấp những đ&ocirc;i gi&agrave;y &lsquo;xấu x&iacute;&rsquo; nhưng cực k&igrave; ấm &aacute;p v&agrave; thoải m&aacute;i cho m&ugrave;a đ&ocirc;ng. V&agrave; kh&aacute;ch h&agrave;ng của họ th&iacute;ch điều n&agrave;y.</span></p>\r\n\r\n<p><strong><span style=\"font-size:16px\">5. Thương hiệu l&agrave; c&aacute;ch tốt nhất để thu h&uacute;t người t&agrave;i.</span></strong></p>\r\n\r\n<p><span style=\"font-size:16px\">Thương hiệu cũng l&agrave; một c&ocirc;ng cụ rất tốt để những nh&agrave; tuyển dụng c&oacute; thể sử dụng để thu h&uacute;t nh&acirc;n t&agrave;i v&agrave; duy tr&igrave; đội ngũ nh&acirc;n vi&ecirc;n gi&agrave;u năng lực. Đ&ocirc;i khi chỉ cần thương hiệu th&ocirc;i l&agrave; đ&atilde; đủ cho c&aacute;c ứng vi&ecirc;n để trả lời c&acirc;u hỏi &lsquo;Tại sao t&ocirc;i muốn l&agrave;m việc ở đ&acirc;y?&rsquo;.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">V&iacute; dụ&nbsp;Google l&agrave; một trong những c&ocirc;ng ti nổi tiếng nhất trong việc tuyển dụng v&agrave; sử dụng nh&acirc;n sự. L&agrave; một trong những thương hiệu lớn nhất thế giới, đi c&ugrave;ng với những ưu đ&atilde;i tuyệt vời cho nh&acirc;n vi&ecirc;n (kh&ocirc;ng gian tiện &iacute;ch, c&oacute; chỗ ngủ trưa, đồ ăn miễn ph&iacute; v&agrave; một sự nghiệp tầm cỡ quốc tế, vv...) được lan truyền khắp nơi tr&ecirc;n Internet, Google c&oacute; thể thuyết phục bất cứ ai về l&agrave;m việc cho họ.</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><img src=\"/local/storage/app/image/images/image(3).png\" style=\"height:263px; width:400px\" /></span></p>\r\n\r\n<p><span style=\"font-size:16px\">T&oacute;m lại, thương hiệu kh&ocirc;ng đơn giản chỉ l&agrave; một từ ngữ chuy&ecirc;n ng&agrave;nh b&oacute;ng bảy của ng&agrave;nh marketing. Thương hiệu đ&oacute;ng vai tr&ograve; rất lớn đối với một doanh nghiệp v&agrave; sẽ c&agrave;ng ng&agrave;y c&agrave;ng trở n&ecirc;n quan trọng hơn nữa, v&agrave; thậm ch&iacute; c&oacute; thể quy đổi th&agrave;nh những gi&aacute; trị khổng lồ. Sở hữu một thương hiệu mạnh kh&ocirc;ng chỉ gi&uacute;p doanh nghiệp thu h&uacute;t th&ecirc;m kh&aacute;ch h&agrave;ng, tăng lợi nhuận m&agrave; c&ograve;n đảm bảo một tương lai tươi s&aacute;ng cho doanh nghiệp qua thời gian. H&atilde;y nh&igrave;n v&agrave;o những Coca-Cola, McDonald&rsquo;s hay Apple để thấy những điều n&agrave;y. V&agrave; hơn thế nữa, xu hướng hiện nay của nhiều nh&agrave; đầu tư muốn đầu tư hay mua lại một doanh nghiệp, họ kh&ocirc;ng quan t&acirc;m nhiều đến cơ sở vật chất, nh&acirc;n sự hay bất k&igrave; điều g&igrave; kh&aacute;c ngo&agrave;i gi&aacute; trị của thương hiệu. Như Steve Forbes, tổng bi&ecirc;n tập của tạp ch&iacute; Forbes, th&igrave; &lsquo;thương hiệu l&agrave; thứ quan trọng duy nhất bạn cần đầu tư ở trong doanh nghiệp của m&igrave;nh&rsquo;. </span></p>', 'Zikzak, co-working space, văn phòng chia sẻ, hồ chí minh', '2018-06-18 18:52:48', '2018-07-01 20:14:37'),
(6, 'Những điều các sĩ tử nên cân nhắc khi lựa chọn ngành học cho mình', 'nhung-dieu-cac-si-tu-nen-can-nhac-khi-lua-chon-nganh-hoc-cho-minh', 'ZIKZAK Coworking Space - Vài hôm nữa, cả xã hội sẽ lại đổ dồn mọi sự quan tâm vào kì thi THPT quốc gia năm 2018. Các sĩ tử sẽ bước vào phòng thi với sức ép không chỉ từ tương lai bản thân mà còn là những kì vọng lớn lao từ gia đình, bạn bè, và xã hội.', 'zz_2018-06-21_1529610887.jpeg', '<p>Tuy nhi&ecirc;n, k&igrave; thi THPT quốc gia chưa phải l&agrave; kết th&uacute;c m&agrave; sau đ&oacute; vẫn tiếp tục lại l&agrave; một cuộc chiến kh&aacute;c nữa m&agrave; c&aacute;c em học sinh tiếp tục phải tham gia. Cuộc chiến chọn ng&agrave;nh chọn trường.</p>\r\n\r\n<p>Đ&atilde; bắt đầu c&oacute; những sự chuyển dịch trong nhận thức của x&atilde; hội về việc chọn ng&agrave;nh nghề như thế n&agrave;o để ph&ugrave; hợp với mỗi c&aacute; nh&acirc;n v&agrave; nhu cầu của x&atilde; hội hơn l&agrave; chọn trường theo danh tiếng v&agrave; mong muốn của gia đ&igrave;nh.</p>\r\n\r\n<p>Mặc d&ugrave; vậy, v&igrave; nhiều l&iacute; do m&agrave; học sinh Việt Nam hiện nay vẫn chưa được trải nghiệm đầy đủ, dẫn đến cũng chưa ho&agrave;n to&agrave;n hiểu được bản th&acirc;n m&igrave;nh mong muốn g&igrave; v&agrave; c&oacute; thể ph&aacute;t triển ở lĩnh vực n&agrave;o.</p>\r\n\r\n<p>Hi vọng, những gợi &yacute; sau đ&acirc;y c&oacute; thể gi&uacute;p c&aacute;c em v&agrave; gia đ&igrave;nh c&oacute; những định hướng nghề nghiệp ch&iacute;nh x&aacute;c.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>1. X&aacute;c định những kĩ năng v&agrave; sở trường của bản th&acirc;n.</strong></p>\r\n\r\n<p>D&agrave;nh ch&uacute;t thời gian để nghĩ về những điều m&agrave; c&aacute;c em nghĩ bản th&acirc;n m&igrave;nh l&agrave;m tốt, c&oacute; thể như l&agrave; vẽ đẹp, mạnh về c&aacute;c c&ocirc;ng việc đ&ograve;i hỏi sự s&aacute;ng tạo, hay xuất sắc với tư duy logic v&agrave; giải quyết vấn đề, hay giỏi c&aacute;c hoạt động thể chất,... H&atilde;y liệt k&ecirc; tất cả những g&igrave; c&aacute;c em nghĩ m&igrave;nh giỏi.</p>\r\n\r\n<p>V&agrave; nếu như c&aacute;c em kh&ocirc;ng thực sự biết m&igrave;nh điểm mạnh của bản th&acirc;n ở đ&acirc;u, h&atilde;y thử n&oacute;i chuyện với người th&acirc;n v&agrave; bạn b&egrave; xung quanh, họ c&oacute; thể sẽ để &yacute; đến những điều m&agrave; bản th&acirc;n c&aacute;c em nhiều khi kh&ocirc;ng để &yacute; đến.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>2. T&igrave;m hiểu những sở th&iacute;ch v&agrave; đam m&ecirc; của bản th&acirc;n.</strong></p>\r\n\r\n<p>B&acirc;y giờ h&atilde;y nghĩ về những việc m&agrave; c&aacute;c em th&iacute;ch l&agrave;m. V&iacute; dụ như đọc s&aacute;ch, m&agrave;y m&ograve; nghi&ecirc;n cứu khoa học, chơi đ&agrave;n, du lịch, vv...</p>\r\n\r\n<p>L&iacute; tưởng nhất l&agrave; việc c&aacute;c em th&iacute;ch l&agrave;m cũng l&agrave; việc c&aacute;c em l&agrave;m tốt, mọi thứ sẽ rất dễ d&agrave;ng. Nhưng đ&ocirc;i khi việc c&aacute;c em th&iacute;ch l&agrave;m v&agrave; việc c&aacute;c em l&agrave;m tốt lại kh&ocirc;ng giống nhau. Thế n&ecirc;n h&atilde;y c&acirc;n nhắc thật kĩ c&agrave;ng v&agrave; cố gắng t&igrave;m được một điểm giao thoa giữa hai danh s&aacute;ch ở phần 1 v&agrave; phần 2.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>3. Nghĩ về th&agrave;nh tựu bản th&acirc;n muốn đạt được trong tương lai.</strong></p>\r\n\r\n<p>C&oacute; thể c&aacute;c em vẫn chưa biết m&igrave;nh muốn đạt được điều g&igrave; trong tương lai nhưng h&atilde;y d&agrave;nh thời gian nghĩ về n&oacute;, xem xem c&aacute;c em muốn khởi nghiệp, l&agrave;m trong ng&acirc;n h&agrave;ng, muốn l&agrave;m một ca sĩ nổi tiếng, muốn l&agrave;m một masterchef hay chỉ đơn giản muốn một cuộc sống đủ đầy.</p>\r\n\r\n<p>Nhớ c&acirc;n nhắc th&ecirc;m cả độ tuổi th&iacute;ch hợp để l&agrave;m những c&ocirc;ng việc đ&oacute;. V&iacute; dụ như người mẫu ảnh sẽ c&oacute; tuổi nghề rất ngắn v&agrave; c&aacute;c em kh&oacute; c&oacute; thể tiếp tục l&agrave;m khi đ&atilde; c&oacute; tuổi. V&igrave; thế nếu muốn theo đuổi nghề n&agrave;y, c&aacute;c em sẽ phải chuẩn bị chu đ&aacute;o sẽ l&agrave;m những g&igrave; khi kh&ocirc;ng c&ograve;n c&oacute; thể l&agrave;m người mẫu ảnh được nữa. Trong khi c&aacute;c em l&agrave;m kế to&aacute;n c&oacute; thể sẽ chỉ l&agrave;m một c&ocirc;ng việc duy nhất xuy&ecirc;n suốt sự nghiệp của m&igrave;nh.</p>\r\n\r\n<p>Những suy nghĩ n&agrave;y sẽ g&oacute;p phần định h&igrave;nh n&ecirc;n con đường c&aacute;c em sẽ đi trong sự nghiệp của m&igrave;nh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>4. Xem x&eacute;t thật kĩ lĩnh vực c&aacute;c em muốn l&agrave;m v&agrave; những ng&agrave;nh nghề c&oacute; li&ecirc;n quan.</strong></p>\r\n\r\n<p>Tới bước n&agrave;y, cơ bản c&aacute;c em đ&atilde; biết m&igrave;nh muốn l&agrave;m g&igrave; nhưng h&atilde;y mở rộng ra th&ecirc;m nữa những khả năng c&oacute; thể. V&iacute; dụ, c&aacute;c em vẽ rất đẹp v&agrave; muốn tận dụng năng khiếu n&agrave;y, c&aacute;c em c&oacute; thể xem x&eacute;t nhiều những ng&agrave;nh nghề kh&aacute;c nhau từ hoạ sĩ, kiến tr&uacute;c sư, thiết kế thời trang, thiết kế nội thất, l&agrave;m quy hoạch, vv... để từ đ&oacute; chọn ra c&ocirc;ng việc ph&ugrave; hợp nhất với bản th&acirc;n m&igrave;nh.</p>\r\n\r\n<p>Sẽ lu&ocirc;n lu&ocirc;n c&oacute; những &aacute;p lực từ gia đ&igrave;nh hay x&atilde; hội rằng c&aacute;c em l&agrave;m nghề n&agrave;y, đi theo con đường n&agrave;y sẽ tốt hơn. Nhưng phải lu&ocirc;n c&acirc;n nhắc rằng cảm nhận của bản th&acirc;n khi l&agrave;m những việc đ&oacute;. V&igrave; những việc c&oacute; thể l&agrave;m h&agrave;i l&ograve;ng những người xung quanh chưa chắc đ&atilde; l&agrave;m c&aacute;c em hạnh ph&uacute;c.</p>\r\n\r\n<p>L&agrave;m c&ocirc;ng việc g&igrave; sau c&ugrave;ng vẫn l&agrave; một lựa chọn c&aacute; nh&acirc;n m&agrave; c&aacute;c em phải chịu tr&aacute;ch nhiệm cho bản th&acirc;n m&igrave;nh. H&atilde;y c&acirc;n nhắc v&agrave; đưa ra sự lựa chọn s&aacute;ng suốt.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Duy Trần</strong></p>', 'Coworking Space, sinh viên, thi đại học, chọn ngành, cho thuê phòng học', '2018-06-21 12:54:46', '2018-06-24 11:50:37'),
(7, 'Yahoo và những kí ức đầu tiên về mạng xã hội', 'yahoo-va-nhung-ki-uc-dau-tien-ve-mang-xa-hoi', 'ZIKZAK Coworking Space - Yahoo Messenger sẽ chính thức ngừng cung cấp dịch vụ từ ngày 17/7 tới đây. Mặc dù từ lâu cái tên Yahoo đã không còn xuất hiện nhiều trong cuộc sống hàng ngày nhưng thông tin này cũng gợi lên trong lòng thế hệ đầu tiên của Việt Nam được tiếp cận với Internet nhiều sự tiếc nuối.', 'zz_2018-06-21_1529612546.jpeg', '<p>Ng&agrave;y 19 th&aacute;ng 11 năm 1997 đ&aacute;nh dấu thời điểm Việt Nam lần đầu ti&ecirc;n ho&agrave; v&agrave;o mạng Internet c&ugrave;ng thế giới. V&agrave; rất nhanh ch&oacute;ng, Yahoo! Messenger sau đ&oacute; trở th&agrave;nh phương tiện li&ecirc;n lạc chủ yếu của thế hệ thanh ni&ecirc;n 7X, 8X v&agrave; phần n&agrave;o đ&oacute; cả đời đầu của 9X m&agrave; b&acirc;y giờ trẻ lắm c&ugrave;ng sắp chạm ngưỡng tuổi băm.</p>\r\n\r\n<p>C&oacute; thể n&oacute;i Yahoo đ&atilde; lớn l&ecirc;n c&ugrave;ng với cả một thế hệ. Trong k&iacute; ức của thế hệ 9X đời đầu đổ về trước, Yahoo Mesenger l&agrave; một cuộc c&aacute;ch mạng trong c&aacute;ch thức li&ecirc;n lạc, l&agrave; nơi mọi người t&igrave;m đến, l&agrave;m quen v&agrave; tr&ograve; chuyện với nhau. Yahoo đ&atilde; tạo được một cộng đồng người d&ugrave;ng khổng lồ tại Việt Nam thời điểm ấy, v&agrave; &lsquo;nick chat&rsquo; l&agrave; thứ được c&aacute;c thanh ni&ecirc;n hỏi nhau chứ kh&ocirc;ng phải số điện thoại như b&acirc;y giờ. Khi m&agrave; điện thoại th&ocirc;ng minh chưa ra đời, điện thoại di động l&agrave; một điều vẫn c&ograve;n rất xa xỉ trong khi điện thoại b&agrave;n th&igrave; lại kh&ocirc;ng được ri&ecirc;ng tư lắm, giới trẻ ng&agrave;y đ&oacute; li&ecirc;n lạc với nhau tất tật qua Yahoo! Messenger. Sẽ c&oacute; rất nhiều người ho&agrave;i niệm c&aacute;i cảm gi&aacute;c chờ đợi một nick ai đ&oacute; hiện l&ecirc;n ở g&oacute;c m&agrave;n h&igrave;nh. V&agrave; thậm ch&iacute;, c&aacute;c c&ocirc; cậu học tr&ograve; ng&agrave;y ấy c&ograve;n cố t&igrave;nh đăng nhập rồi lại tho&aacute;t ra li&ecirc;n tục để g&acirc;y ch&uacute; &yacute; với người m&agrave; m&igrave;nh th&iacute;ch.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/local/storage/app/image/images/Tin%20t%E1%BB%A9c/yahoo-chat.jpg\" style=\"height:301px; width:500px\" /></p>\r\n\r\n<p>Th&ecirc;m v&agrave;o đ&oacute;, bộ icon sinh động c&ugrave;ng những hiệu ứng khi chat l&agrave; những thứ m&agrave; những người d&ugrave;ng Yahoo chắc chắn kh&ocirc;ng bao giờ qu&ecirc;n. Những icon như &lsquo;:)&rsquo;, &lsquo;=))&rsquo;, &lsquo;:*&rsquo;, ... vẫn c&ograve;n c&oacute; sức sống cho đến tận ng&agrave;y nay. Nhiều 9X vẫn tiếp tục sử dụng những icon n&agrave;y khi giao tiếp tr&ecirc;n mạng x&atilde; hội d&ugrave; những h&igrave;nh ảnh đ&oacute; đ&atilde; kh&ocirc;ng c&ograve;n hiện ra như ng&agrave;y trước nữa.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/local/storage/app/image/images/Tin%20t%E1%BB%A9c/icon-yahoo-chat.jpg\" style=\"height:173px; width:308px\" /></p>\r\n\r\n<p>Th&ecirc;m một điều nữa m&agrave; mọi người sẽ rất nhớ về Yahoo! Messenger l&agrave; tiếng BUZZ!!!, m&agrave; đến nay vẫn chưa c&oacute; ứng dụng n&agrave;o c&oacute; hiệu ứng tương tự c&oacute; thể th&agrave;nh c&ocirc;ng được như vậy. Mỗi khi bạn BUZZ!!! ai đ&oacute; trong Yahoo! Messenger, khung chat của người đ&oacute; sẽ rung l&ecirc;n với một &acirc;m thanh kh&ocirc;ng thể nhầm lẫn v&agrave; họ sẽ ngay lập tức ch&uacute; &yacute; đến bạn.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/local/storage/app/image/images/Tin%20t%E1%BB%A9c/ky-uc-yahoo.jpg\" style=\"height:333px; width:500px\" /></p>\r\n\r\n<p>Khoảng những năm 2005-2006, Yahoo tiếp tục cho ra mắt dịch vụ Blog360. C&oacute; thể coi đ&acirc;y l&agrave; mạng x&atilde; hội đầu ti&ecirc;n ở Việt Nam, lần đầu ti&ecirc;n những người trẻ c&oacute; một chỗ để b&agrave;y tỏ suy nghĩ v&agrave; thể hiện c&aacute;i t&ocirc;i của m&igrave;nh. Kh&aacute;c với những mạng x&atilde; hội b&acirc;y giờ như Facebook, Instagram hay Twitter thi&ecirc;n về cập nhật trạng th&aacute;i hay chia sẻ h&igrave;nh ảnh, Blog360 của Yahoo c&oacute; chiều s&acirc;u hơn rất nhiều khi khuyến kh&iacute;ch người sử dụng viết ra những suy nghĩ của m&igrave;nh. Ng&agrave;y đ&oacute;, viết blog đ&atilde; trở th&agrave;nh một tr&agrave;o lưu, một l&agrave;n s&oacute;ng văn ho&aacute; đối với thanh ni&ecirc;n Việt Nam. V&agrave; với nhiều người, Blog360 thậm ch&iacute; c&ograve;n l&agrave; một dạng nhật k&iacute; mở.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/local/storage/app/image/images/Tin%20t%E1%BB%A9c/yahoo%20dong%20cua.jpg\" style=\"height:831px; width:924px\" /></p>\r\n\r\n<p>Những năm đầu thế kỉ 21 thực sự l&agrave; thời cực thịnh của Yahoo ở Việt Nam v&agrave; thanh ni&ecirc;n l&uacute;c đấy kh&ocirc;ng ai nghĩ rằng m&igrave;nh c&oacute; thể sống thiếu Yahoo! Messenger v&agrave; Blog360. Nhưng sự chậm thay đổi của Yahoo đ&atilde; l&agrave;m sụp đổ cả một đế chế. Người sử dụng t&igrave;m đến với Yahoo v&igrave; những tiện &iacute;ch m&agrave; Yahoo mang lại l&agrave; một cuộc c&aacute;ch mạng v&agrave; cũng rời bỏ Yahoo v&igrave; một l&iacute; do tương tự. Với sự h&igrave;nh th&agrave;nh của Facebook v&agrave; sự phổ biến của điện thoại di động c&ugrave;ng những ứng dụng nhắn tin, Yahoo đ&atilde; kh&ocirc;ng th&iacute;ch ứng kịp v&agrave; trở n&ecirc;n cũ kĩ.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/local/storage/app/image/images/Tin%20t%E1%BB%A9c/hoai-niem-yahoo.jpg\" style=\"height:1000px; width:1000px\" /></p>\r\n\r\n<p>C&agrave;ng gần đến ng&agrave;y Yahoo! Messenger bị khai tử th&igrave; lại c&agrave;ng c&oacute; nhiều những chia sẻ ho&agrave;i niệm lại thời xa xưa. Những kỉ niệm đẹp c&oacute; lẽ l&agrave; điều tuyệt vời nhất m&agrave; Yahoo đ&atilde; mang lại cho cả một thế hệ thanh ni&ecirc;n của Việt Nam.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Duy Trần</strong></p>', 'yahoo ngừng dịch vụ, ứng dụng chat yahoo, văn phòng ảo, văn phòng đại diện, zikzak coworking space', '2018-06-21 13:13:12', '2018-06-24 11:54:51'),
(8, 'Những vấn đề gặp phải khi trở thành tân sinh viên', 'nhung-van-de-gap-phai-khi-tro-thanh-tan-sinh-vien', 'ZIKZAK Coworking Space – Chỉ ít bữa trước đây, học sinh lớp 12 trên cả nước phải trải qua một kì thi đại học cực kì căng thẳng và đầy mệt mỏi. Nhưng mọi thứ vẫn chưa dừng lại khi bạn đã thi đỗ vào trường mình mong muốn. Bước chân vào môi trường đại học cuộc sống bình thường của bạn sẽ thay đổi hẳn.\r\nHẳn là các bạn sẽ rất háo hức khi trở thành sinh viên, vì khi đó các bạn sẽ được trải nghiệm những điều mới mẻ. Háo hức chờ mong là không sai, nhưng bên cạnh đó các bạn còn phải chuẩn bị tinh thần để thích nghi với một cuộc sống đại học đầy những thách thức và cám dỗ.', 'zz_2018-07-02_1530563754.jpeg', '<p style=\"margin-left:0cm; margin-right:0cm\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Đ&acirc;y l&agrave; một số vấn đề m&agrave; một t&acirc;n sinh vi&ecirc;n n&ecirc;n sẵn s&agrave;ng để đối mặt:</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:16px\"><a href=\"http://zikzak-co.vn/news-detail/nhung-dieu-cac-si-tu-nen-can-nhac-khi-lua-chon-nganh-hoc-cho-minh\" target=\"_self\">--Xem th&ecirc;m: Những điều sĩ tử cần c&acirc;n nhắc khi lựa chọn ng&agrave;nh học cho m&igrave;nh</a></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\">&nbsp;</p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>M&ocirc;i trường sống thay đổi</strong></span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Năm đầu ti&ecirc;n lu&ocirc;n l&agrave; năm kh&oacute; khăn nhất trong đời sinh vi&ecirc;n. Kh&ocirc;ng bạn b&egrave;, kh&ocirc;ng người quen, phong c&aacute;ch học tập ở đại học cũng kh&aacute;c. Ở đại học bạn sẽ gặp rất nhiều bạn b&egrave; đến từ khắp nơi tr&ecirc;n cả nước. Văn h&oacute;a kh&aacute;c nhau, ngữ &acirc;m kh&aacute;c nhau sẽ l&agrave;m bạn rất kh&oacute; m&agrave; th&iacute;ch nghi được ngay. Điều n&agrave;y sẽ l&agrave;m bạn tốn &iacute;t thời gian để th&iacute;ch nghi với m&ocirc;i trường sống v&agrave; con người xung quanh. Bạn phải trải l&ograve;ng m&igrave;nh ra, xung quanh thay đổi th&igrave; bản th&acirc;n bạn cũng n&ecirc;n tự điểu chỉnh để phụ hợp với n&oacute;. Khi đ&oacute; cuộc sống đại học sẽ trở n&ecirc;n th&uacute; vị hơn.</span></span></p>\r\n\r\n<ol start=\"2\">\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Nỗi nhớ cha mẹ</strong></span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Hầu hết mọi sinh vi&ecirc;n đi học xa nh&agrave; th&igrave; nỗi nhớ cha mẹ lu&ocirc;n khắc khoải trong l&ograve;ng. ZikZak khuy&ecirc;n bạn đừng n&ecirc;n để nỗi nhớ đ&oacute; lấn &aacute;t cả &yacute; ch&iacute; học tập của m&igrave;nh. V&igrave; với tốc độ ph&aacute;t triển của khoa học điện tử hiện nay th&igrave; với những ứng dụng video call tr&ecirc;n <a href=\"http://www.facebook.com\" target=\"_blank\">Facebook</a>, Skype, Zalo,&hellip; đều c&oacute; thể biến nỗi nhớ ấy trở n&ecirc;n nhỏ dần đi. Chỉ với một cuộc video call l&agrave; bạn đ&atilde; c&oacute; thể gặp được bố mẹ m&igrave;nh.</span></span></p>\r\n\r\n<ol start=\"3\">\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Tập sống tự lập</strong></span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Khi ở nh&agrave;, ba mẹ cưng bạn như cưng trứng, hứng như hứng hoa, mọi việc đều c&oacute; ba mẹ l&agrave;m gi&uacute;p bạn. Nhưng khi bạn đ&atilde; đi học xa nh&agrave; th&igrave; bạn phải tự trang bị cho m&igrave;nh những kỹ năng cần thiết để bắt đầu một cuộc sống một m&igrave;nh. Nếu kh&ocirc;ng bắt đầu sớm th&igrave; khi đối mặt với những vấn đề như chi ti&ecirc;u h&agrave;ng th&aacute;ng, h&oacute;a đơn điện nước, tiền nh&agrave;,&hellip; th&igrave; bạn sẽ thấy l&uacute;ng t&uacute;ng v&agrave; kh&ocirc;ng biết n&ecirc;n giải quyết như thế n&agrave;o. Bạn h&atilde;y bỏ một ch&uacute;t thời gian để nghi&ecirc;n cứu về vấn đề n&agrave;y.</span></span></p>\r\n\r\n<ol start=\"4\">\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>N&acirc;ng cao tinh thần tự gi&aacute;c học tập</strong></span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Kh&aacute;c với những năm c&ograve;n học ở trung học, thầy c&ocirc;, cha mẹ lu&ocirc;n đốc th&uacute;c bạn phải học, phải học v&agrave; phải học. Nhưng đại học th&igrave; kh&aacute;c, sẽ chẳng c&oacute; trả b&agrave;i đầu giờ, sẽ kh&ocirc;ng c&oacute; kiểm tra b&agrave;i tập mỗi ng&agrave;y, kiểm tra 15&rsquo; hay một tiết g&igrave; cũng kh&ocirc;ng nốt. Tuy nhi&ecirc;n đừng thấy vậy m&agrave; mừng, thầy c&ocirc; kh&ocirc;ng hối bạn l&agrave;m b&agrave;i tập, kh&ocirc;ng nhắc bạn l&agrave;m b&agrave;i, nhưng họ sẽ bất chợt kiểm tra bạn bất cứ l&uacute;c n&agrave;o. Cho n&ecirc;n h&atilde;y tập t&iacute;nh tự gi&aacute;c c&agrave;ng sớm bao nhi&ecirc;u th&igrave; c&agrave;ng c&oacute; lợi cho bạn bấy nhi&ecirc;u. Khi bạn rớt m&ocirc;n th&igrave; cha mẹ sẽ phải tốn tiền đ&oacute;ng tiền học lại cho bạn đấy.</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:16px\"><a href=\"http://zikzak-co.vn/news-detail/yahoo-va-nhung-ki-uc-dau-tien-ve-mang-xa-hoi\" target=\"_self\">Xem th&ecirc;m: Yahoo v&agrave; những k&yacute; ức về mạng x&atilde; hội</a></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\">&nbsp;</p>\r\n\r\n<ol start=\"5\">\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>Sống chung với người kh&aacute;c</strong></span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Bạn c&ugrave;ng ph&ograve;ng, vấn đề cũng kh&aacute; nan giải, kh&ocirc;ng phải ai sống chung cũng hợp t&iacute;nh với nhau. Ngay cả sống với người nh&agrave; m&agrave; thi thoảng c&ograve;n c&atilde;i nhau th&igrave; sống với chung với những người xa lạ trong một căn ph&ograve;ng k&iacute; t&uacute;c x&aacute; th&igrave; thật sự l&agrave; một cực h&igrave;nh. L&uacute;c n&agrave;y bạn n&ecirc;n ph&aacute;t huy năng lực giải quyết xung đột của m&igrave;nh. Nguy&ecirc;n tắc phải lu&ocirc;n ghi nhớ l&agrave; &ldquo;M&igrave;nh t&ocirc;n trọng người ta, người ta sẽ t&ocirc;n trọng m&igrave;nh.&rdquo;</span></span></p>\r\n\r\n<ol start=\"6\">\r\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>T&agrave;i ch&iacute;nh</strong></span></span></li>\r\n</ol>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Tiền nh&agrave;, tiền ăn h&agrave;ng th&aacute;ng, tiền ti&ecirc;u vặt,&hellip; đủ thứ c&aacute;c loại tiền sẽ l&agrave;m c&aacute;c bạn sinh vi&ecirc;n đi&ecirc;u đứng. Nhưng kh&ocirc;ng sao, hiện nay nhiều nơi vui chơi giải tr&iacute;, rạp phim, qu&aacute;n ăn,&hellip; hay c&oacute; những dịch vụ khuyến m&atilde;i d&agrave;nh cho sinh vi&ecirc;n. C&aacute;c bạn n&ecirc;n tận dụng những cơ hội n&agrave;y để vừa tiết kiệm được tiền vừa được vui chơi. Nhưng đừng qu&aacute; mải m&ecirc; ăn chơi m&agrave; hao ph&iacute; tiền bạc của cha mẹ nh&eacute;.</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;--ZikZak--</span></span></p>', 'tân sinh viên, khó khăn, đại học', '2018-07-02 13:35:53', '2018-07-02 17:05:19'),
(9, 'Bảng xếp hạng thương hiệu danh giá nhất hành tinh năm 2018', 'bang-xep-hang-thuong-hieu-danh-gia-nhat-hanh-tinh-nam-2018', 'Brand Finance – hãng định giá thương hiệu nổi tiếng nước Anh mới đây vừa công bố danh sách xếp hạng top 10 thương hiệu giá trị nhất thế giới và bản đồ các thương hiệu nổi bật, đại diện cho các quốc gia năm 2018. Các ông lớn đã có những bước chuyển mình như thế nào trong bước tiến thương mại, việc tăng trưởng thứ hạng về giá trị cũng như độ phủ thương hiệu đến khách hàng mục tiêu của họ thay đổi như thế nào? Hãy cùng Zikzak coworkingspace tìm hiểu nhé!', 'zz_2018-07-23_1532314826.jpeg', '<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-size:13.0pt\"><span style=\"color:#231f20\">H&atilde;ng tư vấn định gi&aacute; Brand Finance đ&atilde; dựa tr&ecirc;n c&aacute;c ti&ecirc;u ch&iacute; cốt l&otilde;i như: đầu tư marketing, đầu tư của cổ đ&ocirc;ng v&agrave; kết quả hoạt động kinh doanh của từng c&ocirc;ng ty để x&aacute;c định hiệu quả gi&aacute; trị của mỗi thương hiệu mang lại. Đứng đầu danh s&aacute;ch l&agrave; thương hiệu Amazon của tỷ ph&uacute; gi&agrave;u nhất thế giới Jeff Bezos.</span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11px\"><img src=\"/local/storage/app/image/images/image-20180723170643-1.jpeg\" style=\"float:left; height:478px; width:738px\" /></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:center\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><em><span style=\"font-size:13.0pt\"><span style=\"color:#1d2129\">Bản đồ thương hiệu đại diện nổi bật nhất cho c&aacute;c quốc gia năm 2018</span></span></em></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:12pt\"><strong><span style=\"font-size:13.0pt\"><span style=\"color:#231f20\">Amazon -</span></span></strong><span style=\"font-size:13.0pt\"><span style=\"color:#231f20\"> <strong>Gi&aacute; trị thương hiệu: 150,8 tỷ USD</strong></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-size:13.0pt\"><span style=\"color:#231f20\">Với gi&aacute; trị thương hiệu hiện tại l&agrave;: 150,8 tỷ USD, t</span></span><span style=\"font-size:13.0pt\"><span style=\"color:#231f20\">ăng 47% so với năm 2017, Amazon l&agrave; c&ocirc;ng ty thương mại điện tử lớn nhất thế giới t&iacute;nh theo doanh thu v&agrave; vốn ho&aacute;. Ngo&agrave;i ra, c&ocirc;ng ty n&agrave;y c&ograve;n ph&aacute;t triển mạnh trong mảng cơ sở hạ tầng điện to&aacute;n đ&aacute;m m&acirc;y, điện tử, cũng như ph&aacute;t trực tiếp video v&agrave; &acirc;m nhạc. Năm 2017, Amazon th&acirc;u t&oacute;m chuỗi cửa h&agrave;ng thực phẩm Whole Foods với gi&aacute; 13,7 tỷ USD &ndash; thương vụ g&acirc;y chấn động thị trường b&aacute;n lẻ truyền thống. Thương vụ n&agrave;y cho thấy sự quan t&acirc;m v&agrave; mở rộng của Amazon từ thế giới số sang cửa h&agrave;ng truyền thống.</span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:12pt\"><strong><span style=\"font-size:13.0pt\"><span style=\"color:#231f20\">Samsung &ndash; Gi&aacute; trị thương hiệu: 92,3 tỷ USD&nbsp;</span></span></strong></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-size:13.0pt\"><span style=\"color:#231f20\">Samsung, c&ocirc;ng ty dẫn đầu thế giới trong lĩnh vực kinh doanh điện tử v&agrave; c&ocirc;ng nghệ đ&atilde; tăng 2 bậc trong danh s&aacute;ch <em>The most valuable Brand 2018</em> v&agrave; trở th&agrave;nh một trong những c&ocirc;ng ty quyền lực nhất trong giới n&agrave;y. Ngo&agrave;i c&aacute;c d&ograve;ng diện thoại Galaxy, Samsung c&ograve;n cung cấp m&aacute;y t&iacute;nh bảng, TV, đồ gia dụng, thiết bị an ninh&hellip;</span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:12pt\"><strong><span style=\"font-size:13.0pt\"><span style=\"color:#231f20\">ICBC - Gi&aacute; trị thương hiệu: 59,2 tỷ USD&nbsp;</span></span></strong></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-size:13.0pt\"><span style=\"color:#231f20\">Từ năm 2008, tỷ trọng gi&aacute; trị c&aacute;c thương hiệu to&agrave;n cầu của Trung Quốc đ&atilde; tăng từ 3% l&ecirc;n 15%. 2018 l&agrave; năm thứ 2 li&ecirc;n tiếp ICBC (Ng&acirc;n h&agrave;ng Thương mại v&agrave; C&ocirc;ng nghiệp Trung Quốc) giữ thứ hạng cao trong danh s&aacute;ch thương hiệu gi&aacute; trị nhất thế giới. </span></span><span style=\"font-size:13.0pt\"><span style=\"background-color:white\"><span style=\"color:black\">Được biết trong năm 2018, c&oacute; 14 thương hiệu từ Trung Quốc đ&atilde; g&oacute;p mặt trong Top 100 so với năm 2006 khi chỉ c&oacute; 1 thương hiệu l&agrave; China Mobile. Tổng gi&aacute; trị c&aacute;c thương hiệu Trung Quốc đ&atilde; tăng 47% từng năm, gấp đ&ocirc;i so với sự tăng trưởng của c&aacute;c thương hiệu từ Mỹ (tăng 23%).</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:13.0pt\"><span style=\"color:#1d2129\">C&aacute;c &ocirc;ng lớn c&ograve;n lại như: Mercedes-Benz của Đức (43,9 tỷ USD), Toyota Nhật Bản (43,7 tỷ USD) v&agrave; Royal Dutch Shell (39,4 tỷ USD) được xếp lần lượt ở c&aacute;c vị tr&iacute; 4, 5 v&agrave; 6. Theo sau đ&oacute;, c&aacute;c thương hiệu nằm trong danh s&aacute;ch khoảng từ 4 tỷ đến 25 tỷ đ&ocirc; la, bao gồm c&aacute;c t&ecirc;n tuổi nổi tiếng như Nestl&eacute; (19,4 tỷ đ&ocirc; la), Zara (17,5 tỷ đ&ocirc; la) v&agrave; RBC (13,8 tỷ đ&ocirc; la). Mặc d&ugrave; nhỏ hơn rất nhiều so với Amazon, nhưng đ&acirc;y vẫn l&agrave; những thương hiệu mang tầm quốc tế kh&aacute; lớn.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:13.0pt\"><span style=\"background-color:white\"><span style=\"color:black\">Ngo&agrave;i ra, một bảng xếp hạng danh gi&aacute; kh&aacute;c đến từ <strong><em>BrandZ </em></strong></span></span></span><strong><em><span style=\"font-size:13.0pt\"><span style=\"background-color:white\">Top 100 thương hiệu gi&aacute; trị nhất to&agrave;n cầu 2018</span></span></em></strong><span style=\"font-size:13.0pt\"><span style=\"background-color:white\"> vừa được WPP v&agrave; Kantar Millward Brown c&ocirc;ng bố. Để c&oacute; kết quả xếp hạng n&agrave;y, c&ocirc;ng ty nghi&ecirc;n cứu thị trường Millward Brown đ&atilde; sử dụng cơ sở dữ liệu BrandZ từ c&ocirc;ng ty mẹ, tập đo&agrave;n truyền th&ocirc;ng h&agrave;ng đầu thế giới WPP.</span></span> Theo bảng xếp hạng, <span style=\"font-size:13.0pt\"><span style=\"color:black\">trong top 10 danh s&aacute;ch, c&oacute; tới 8 thương hiệu l&agrave; những c&ocirc;ng ty về c&ocirc;ng nghệ hoặc cũng li&ecirc;n quan đến c&ocirc;ng nghệ.&nbsp;</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><img src=\"/local/storage/app/image/images/image-20180723170643-2.jpeg\" style=\"height:420px; width:740px\" /></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><strong><span style=\"font-size:13.0pt\"><span style=\"color:black\">Google</span></span></strong><span style=\"font-size:13.0pt\"><span style=\"color:black\">&nbsp;v&agrave;&nbsp;<strong>Apple</strong>&nbsp;tiếp tục duy tr&igrave; 2 vị tr&iacute; đầu ti&ecirc;n khi gi&aacute; trị thương hiệu lần lượt tăng th&ecirc;m 23% đạt 302,1 tỷ USD v&agrave; 28% đạt 300,6 tỷ USD.&nbsp;<strong>Amazon</strong>&nbsp;vượt qua&nbsp;<strong>Microsoft</strong>&nbsp;giữ vị tr&iacute; thứ 3 với mức tăng trưởng 49% đạt 207,6 tỷ USD. Trong khi đ&oacute;,&nbsp;<strong>Tencent</strong>&nbsp;đ&atilde; tiến l&ecirc;n 3 bậc so với năm ngo&aacute;i, vượt qua&nbsp;<strong>Facebook</strong>&nbsp;v&agrave; giữ vị tr&iacute; thứ 5 khi gi&aacute; trị thương hiệu tăng th&ecirc;m 65% đạt 179 tỷ USD.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:13.0pt\"><span style=\"background-color:white\"><span style=\"color:black\">C&aacute;c thương hiệu quốc gia từ những khu vực kh&aacute;c tr&ecirc;n thế giới (như Ấn Độ v&agrave; Indonesia) cũng c&oacute; sự tăng trưởng cao. C&aacute;c thương hiệu ng&acirc;n h&agrave;ng của ch&acirc;u &Aacute; (ngo&agrave;i trừ Trung Quốc) cũng g&oacute;p mặt trong Bảng xếp hạng Top 100 BrandZ khi gi&aacute; trị tăng trưởng th&ecirc;m 14%, đạt tổng cộng 147 tỷ USD.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><img src=\"/local/storage/app/image/images/image-20180723170643-3.png\" style=\"height:437px; width:768px\" /></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:center\"><span style=\"font-size:12pt\"><em><span style=\"font-size:13.0pt\"><span style=\"color:#231f20\">Top 50 danh s&aacute;ch gi&aacute; trị thương hiệu từ BandZ, năm 2018</span></span></em></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"color:#000000\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:13.0pt\"><span style=\"background-color:white\">B&agrave;&nbsp;</span></span><strong>Doreen Wang, Gi&aacute;m đốc to&agrave;n cầu của BrandZ thuộc tập đo&agrave;n&nbsp;Kantar Millward Brown&rsquo;s</strong>, cho biết:&nbsp;<em>&ldquo;Những thương hiệu đang nắm phần thắng trong kỷ nguy&ecirc;n c&ocirc;ng nghệ marketing th&ocirc;ng minh như Amazon v&agrave; Tencent l&agrave; những thương hiệu đặt người d&ugrave;ng l&agrave;m trọng t&acirc;m trong mọi kế hoạch của họ. Đ&acirc;y l&agrave; những c&ocirc;ng ty sử dụng c&ocirc;ng nghệ để hiểu r&otilde; nhu cầu của người d&ugrave;ng v&agrave; &aacute;p dụng những nghi&ecirc;n cứu c&oacute; được x&acirc;y dựng hệ sinh th&aacute;i c&aacute;c dịch vụ nhằm đ&aacute;p ứng những nhu cầu kh&aacute;c nhau về trải nghiệm liền mạch giữa c&aacute;c nền tảng của người ti&ecirc;u d&ugrave;ng&rdquo;.</em></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:right\"><strong><span style=\"color:#000000\"><span style=\"font-size:12pt\"><span style=\"background-color:white\">Thanh Huỳnh</span></span></span></strong></p>', 'BrandFinance, Amazon, Apple, Samsung, The most valuable Brands, ICBC, Google, Facebook, Microsoft, Tencent, Mercedes-Benz, BandZ', '2018-07-22 20:00:26', '2018-07-29 18:17:55');
INSERT INTO `news` (`news_id`, `news_name`, `news_slug`, `news_summary`, `news_image`, `news_content`, `tag`, `created_at`, `updated_at`) VALUES
(10, 'TỶ PHÚ MARK CUBAN: “LẤY BẰNG MBA LÀ ĐIỀU KHÔNG CẦN THIẾT”', 'ty-phu-mark-cuban-lay-bang-mba-la-dieu-khong-can-thiet', 'Tuy xuất thân từ một gia đình lao động phổ thông nghèo, Mark Cuban đã sớm thể hiện năng kiếu kiếm tiền bằng việc buôn bán đồ vặt từ nhỏ và không ngừng nổ lực lập nghiệp. Đến nay, ngôi sao Shark Tank đã trở thành một trong những tỷ phú xuất sắc của thế giới với khối tài sản 3,7 tỷ USD.', 'zz_2018-07-30_1532913448.jpeg', '<h2 style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"color:black\">Khởi nghiệp chỉ với 60 USD</span></strong></span></span></h2>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:18px\"><span style=\"background-color:white\"><span style=\"color:black\">Ng&agrave;y 4/7/1982 Mark Cuban tốt nghiệp Đại học Indiana &ndash; chuy&ecirc;n ng&agrave;nh Quản Trị Kinh Doanh ở tuổi 23, chỉ với vỏn vẹn 60 USD trong t&uacute;i.</span> <span style=\"color:black\">&Ocirc;ng chuyển đến một căn hộ sống c&ugrave;ng v&agrave;i người bạn, họ thậm ch&iacute; cho biết: &quot;Cuban đ&atilde; đến với ch&uacute;ng t&ocirc;i trong t&igrave;nh trạng hầu như ho&agrave;n to&agrave;n rỗng t&uacute;i&quot;. Ngo&agrave;i việc kh&ocirc;ng c&oacute; tiền, Cuban cũng phải &ocirc;m nợ học ph&iacute; h&agrave;ng chục ng&agrave;n USD như nhiều sinh vi&ecirc;n kh&aacute;c.</span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:18px\"><span style=\"background-color:white\"><span style=\"color:black\">Sau khi từ bỏ thậm ch&iacute; bị đuổi khỏi c&ocirc;ng việc 3 lần li&ecirc;n tiếp, Cuban nhận ra đ&atilde; đến l&uacute;c phải tự th&agrave;nh lập c&ocirc;ng ty của ri&ecirc;ng m&igrave;nh. </span></span></span></p>\r\n\r\n<blockquote>\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:center\"><strong><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:28px\"><span style=\"background-color:white\"><span style=\"color:black\">&quot;Khi đ&oacute; t&ocirc;i chẳng c&oacute; g&igrave; cả, v&igrave; vậy cũng chẳng c&oacute; g&igrave; để mất. Điều đ&oacute; khiến t&ocirc;i nỗ lực hết m&igrave;nh&rdquo;</span></span></span></span></strong></p>\r\n</blockquote>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><img src=\"/local/storage/app/image/images/image-20180730152059-1.jpeg\" style=\"height:455px; width:730px\" /></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:center\"><span style=\"font-size:20px\"><span style=\"background-color:white\"><span style=\"color:black\">Tỷ ph&uacute; Mark Cuban khởi nghiệp chỉ với 60 USD</span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:18px\"><span style=\"background-color:white\"><span style=\"color:black\">Khi l&agrave;m c&ocirc;ng việc đầu ti&ecirc;n c&oacute; d&ugrave;ng đến c&ocirc;ng nghệ, Cuban nhận ra m&igrave;nh v&ocirc; c&ugrave;ng y&ecirc;u th&iacute;ch n&oacute;. &Ocirc;ng đ&atilde; tự học lập tr&igrave;nh v&agrave; học li&ecirc;n tiếp trong 8 giờ/ ng&agrave;y m&agrave; kh&ocirc;ng cần nghỉ ngơi, d&ugrave; chỉ l&agrave; 10 ph&uacute;t. <span style=\"background-color:white\">Nhờ những kiến thức trong lĩnh vực m&aacute;y t&iacute;nh, Cuban quyết định th&agrave;nh lập c&ocirc;ng ri&ecirc;ng c&oacute; t&ecirc;n MicroSolutions. Chuy&ecirc;n lắp đặt mạng LAN cho c&aacute;c doanh nghiệp ở địa phương v&agrave; bu&ocirc;n b&aacute;n c&aacute;c sản phẩm c&ocirc;ng nghệ.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:18px\"><span style=\"background-color:white\"><span style=\"background-color:white\"><span style=\"color:black\">MicroSolutions dần dần trở th&agrave;nh một c&ocirc;ng ty c&oacute; doanh thu h&agrave;ng năm l&ecirc;n tới 30 triệu USD. Sau đ&oacute;, Cuban đ&atilde; b&aacute;n lại c&ocirc;ng ty n&agrave;y cho CompuServe với gi&aacute; 6 triệu USD v&agrave;o năm 1990. Bản th&acirc;n &ocirc;ng nhận được 2 triệu USD từ thương vụ n&agrave;y.</span></span> </span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:18px\"><span style=\"background-color:white\"><span style=\"background-color:white\"><span style=\"color:black\">Nhận thấy khả năng thay đổi thế giới Internet, năm 1995, Cuban dốc to&agrave;n lực th&agrave;nh lập c&ocirc;ng ty Broadcast.com. Chỉ v&agrave;i th&aacute;ng sau khi được ni&ecirc;m yết tr&ecirc;n thị trường chứng kho&aacute;n, Broadcast.com đ&atilde; c&aacute;n mốc 200 USD/cổ phiếu. </span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><img src=\"/local/storage/app/image/images/image-20180730152059-2.jpeg\" style=\"height:430px; width:730px\" /></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:center\"><span style=\"font-size:20px\"><span style=\"background-color:white\"><span style=\"background-color:white\"><span style=\"color:black\">Cuban b&aacute;n lại Broadcast.com cho Yahoo với c&aacute;i gi&aacute; cao ngất trời 5,9 tỷ USD</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:20px\"><span style=\"background-color:white\"><strong><span style=\"background-color:white\"><span style=\"color:black\">Trở th&agrave;nh gi&aacute;m khảo Shark Tank nổi tiếng</span></span></strong></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:18px\"><span style=\"background-color:white\"><span style=\"background-color:white\"><span style=\"color:black\">Mark Cuban được biết đến với vai tr&ograve; l&agrave; gi&aacute;m khảo của Shark Tank, một trong những chương tr&igrave;nh truyền h&igrave;nh thực tế d&agrave;nh cho c&aacute;c nh&agrave; đầu tư th&agrave;nh c&ocirc;ng nhất của đ&agrave;i ABC. Nơi kết nối c&aacute;c doanh nh&acirc;n khởi nghiệp s&aacute;ng tạo với c&aacute;c nh&agrave; đầu tư mạo hiểm (shark &ndash; c&aacute; mập). Đ&acirc;y l&agrave; chương tr&igrave;nh c&oacute; tỷ suất người xem cao nhất v&agrave;o tối thứ 6 tại Mỹ với đối tượng kh&aacute;n giả trong độ tuổi 18-49.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:18px\"><span style=\"background-color:white\"><span style=\"background-color:white\"><span style=\"color:black\">Sau khi trở n&ecirc;n gi&agrave;u c&oacute;, để thỏa m&atilde;n niềm đam m&ecirc; b&oacute;ng rổ từ nhỏ, Mark Cuban đ&atilde; bỏ ra 280 triệu USD để sở hữu ri&ecirc;ng đội b&oacute;ng rổ Dallas Mavericks. &Ocirc;ng kh&ocirc;ng tiếc tiền mua hẳn một chuy&ecirc;n cơ 48 triệu USD cho đội b&oacute;ng v&agrave; sẵn s&agrave;ng đầu tư để mang lại sự thoải m&aacute;i nhất cho c&aacute;c cầu thủ.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><img src=\"/local/storage/app/image/images/image-20180730152059-3.jpeg\" style=\"height:487px; width:730px\" /></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:18px\"><span style=\"background-color:white\"><span style=\"background-color:white\"><span style=\"color:black\">&Ocirc;ng cũng l&agrave; đại gia chơi ng&ocirc;ng kh&eacute;t tiếng khi sở hữu h&agrave;ng loạt t&agrave;i sản c&oacute; gi&aacute; trị kh&aacute;c bao gồm: </span></span><span style=\"color:black\">một biệt thự đắt gi&aacute; bậc nhất Dallas trị gi&aacute; 17,2 triệu USD, gồm 10 ph&ograve;ng ngủ, 13 ph&ograve;ng tắm v&agrave; 4 nh&agrave; để xe c&ugrave;ng bể bơi v&agrave; s&acirc;n tennis, một nh&agrave; nghỉ tuyệt đẹp tr&ecirc;n 2.000 m2; du thuyền với đầy đủ tiện nghi sang trọng với mức gi&aacute; 41 triệu USD<span style=\"background-color:white\">. Ngo&agrave;i ra, Cuban c&ograve;n sắm cho m&igrave;nh v&agrave; gia đ&igrave;nh chiếc phi cơ ri&ecirc;ng, trong đ&oacute; nổi bật nhất l&agrave; chiếc Gulfstream V với gi&aacute; 40 triệu USD v&agrave; &nbsp;một chiếc Boeing 767</span>.</span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:18px\"><span style=\"background-color:white\"><span style=\"background-color:white\"><span style=\"color:black\">Hiện tại, Mark Cuban l&agrave; chủ của một tập đo&agrave;n chuy&ecirc;n kinh doanh trong lĩnh vực giải tr&iacute; v&agrave; truyền th&ocirc;ng. Hệ thống 58 rạp chiếu b&oacute;ng mang t&ecirc;n Landmark Theather của Cuban thuộc loại lớn nhất ở Mỹ.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><img src=\"/local/storage/app/image/images/image-20180730152059-4.jpeg\" style=\"height:487px; width:730px\" /></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:center\"><span style=\"font-size:20px\"><span style=\"background-color:white\"><span style=\"background-color:white\"><span style=\"color:black\">Hệ thống cụm rạp Landmark Theater</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:20px\"><span style=\"background-color:white\"><strong><span style=\"background-color:white\"><span style=\"color:black\">Tr&ecirc;n con đường th&agrave;nh c&ocirc;ng, kh&ocirc;ng cần thiết phải c&oacute; bằng MBA</span></span></strong></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:18px\"><span style=\"background-color:white\"><span style=\"color:black\">Gần đ&acirc;y, Mark Cuban đ&atilde; ph&aacute;t ng&ocirc;n g&acirc;y tranh c&atilde;i, &ocirc;ng cho rằng tr&ecirc;n con đường đến th&agrave;nh c&ocirc;ng, c&oacute; một bước m&agrave; nhiều người đ&atilde; đi nhưng thực chất lại kh&ocirc;ng cần thiết, đ&oacute; l&agrave; lấy bằng MBA (Bằng Thạc sĩ Quản trị kinh doanh).</span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:18px\"><span style=\"background-color:white\"><span style=\"color:black\">&ldquo;T&ocirc;i l&agrave; người kh&ocirc;ng ủng hộ lấy bằng MBA. T&ocirc;i ủng hộ đi học đại học nhưng kh&ocirc;ng ủng hộ lấy MBA&rdquo;. &Ocirc;ng Cuban đ&atilde; n&oacute;i trước Hội nghị c&aacute;c nh&agrave; l&atilde;nh đạo trường trung học, một sự kiện d&agrave;nh cho học sinh trung học của tổ chức Turning Point USA tại thủ đ&ocirc; Washington của Mỹ v&agrave;o h&ocirc;m thứ ba 24/7/2018.</span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><img src=\"/local/storage/app/image/images/image-20180730152059-5.jpeg\" style=\"height:438px; width:730px\" /></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:18px\"><span style=\"background-color:white\"><span style=\"color:black\">&Ocirc;ng Cuban đ&atilde; tham gia lớp tiền đại học tại Đại học Indiana v&agrave; học c&aacute;c lớp MBA tại đ&oacute;, &ocirc;ng cho biết chỉ trong v&ograve;ng năm đầu ti&ecirc;n, &ocirc;ng đ&atilde; học gần hết c&aacute;c lớp MBA, nhưng cuối c&ugrave;ng &ocirc;ng đ&atilde; kh&ocirc;ng ho&agrave;n th&agrave;nh bằng n&agrave;y.</span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:18px\"><span style=\"background-color:white\"><span style=\"color:black\">Theo &ocirc;ng, c&oacute; nhiều lựa chọn kh&aacute;c rẻ tiền hơn. B&ecirc;n cạnh đ&oacute;, kinh nghiệm l&agrave;m việc thực tế qu&yacute; gi&aacute; hơn rất nhiều. &Ocirc;ng cho rằng c&oacute; thể học c&aacute;c lớp học tương đương với MBA tr&ecirc;n Internet v&agrave; nếu ki&ecirc;n tr&igrave; th&igrave; c&oacute; thể c&oacute; kiến thức tương đương với ph&iacute; tổn nhỏ hơn nhiều.</span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:18px\"><span style=\"background-color:white\"><span style=\"color:black\">Đ&acirc;y kh&ocirc;ng phải lần đầu ti&ecirc;n &ocirc;ng Cuban ch&ecirc; bai &yacute; tưởng lấy bằng MBA. Năm 2012, &ocirc;ng cũng đ&atilde; viết tr&ecirc;n trang Reddit rằng &ocirc;ng xem việc học MBA l&agrave; một sự l&atilde;ng ph&iacute; tiền bạc, nếu như một người thiếu kiến thức về vấn đề g&igrave; đ&oacute; th&igrave; c&oacute; rất nhiều kh&oacute;a học tr&ecirc;n mạng; đặc biệt &ocirc;ng kh&ocirc;ng ưu ti&ecirc;n ai đ&oacute; khi tuyển dụng chỉ v&igrave; họ c&oacute; bằng MBA.</span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><img src=\"/local/storage/app/image/images/image-20180730152059-6.jpeg\" style=\"height:453px; width:730px\" /></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:center\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">Cuban cho rằng c&oacute; nhiều lựa chọn tiết kiệm chi ph&iacute; hơn l&agrave; học MBA</span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"color:#000000; font-family:Times New Roman,serif\"><span style=\"font-size:17.3333px\"><strong>Thanh Huỳnh</strong>&nbsp;</span></span></p>', 'Mark Cuban, Shark Tank, MBA, Yahoo', '2018-07-29 18:17:27', '2018-07-29 18:36:13'),
(11, 'Làm thế nào để bài dự thi được chấp nhận', 'lam-the-nao-de-bai-du-thi-duoc-chap-nhan', '“Cuộc thi XStartup” đã trải qua tuần đầu tiên, BTC đã nhận được một số bài dự thi của các đội thi. Tuy nhiên qua quá trình xem xét và theo dõi các bài dự thi, BTC rút ra những điểm cần lưu ý để các đội thi chú ý và kiểm tra lại bài dự thi của mình nhé!', 'zz_2018-10-01_1538388650.jpeg', '<p><strong>Các cá nh&acirc;n chỉ được đăng ký tham gia 01 đ&ocirc;̣i thi</strong></p>\r\n\r\n<p dir=\"ltr\">M&ocirc;̃i cá nh&acirc;n chỉ được đăng ký thành vi&ecirc;n trong m&ocirc;̣t dự án, tránh các trường hợp các bạn tạo các tài khoản khác nhau đ&ecirc;̉ tham gia nhi&ecirc;̀u dự án.</p>\r\n\r\n<p dir=\"ltr\">Trường hợp này n&ecirc;́u BTC phát hi&ecirc;̣n sẽ hủy tư cách dự thi của thí sinh đó.</p>\r\n\r\n<p dir=\"ltr\"><strong>C&aacute;c th&agrave;nh vi&ecirc;n trong đội đăng k&yacute; đầy đủ t&agrave;i khoản tr&ecirc;n website</strong></p>\r\n\r\n<p dir=\"ltr\">Các thành vi&ecirc;n c&acirc;̀n đăng ký tài khoản trước khi được nhóm trưởng th&ecirc;m vào nhóm dự thi của mình.</p>\r\n\r\n<p dir=\"ltr\">BTC c&ocirc;ng nh&acirc;̣n những cá nh&acirc;n dự thi hợp l&ecirc;̣ là những cá nh&acirc;n đã đăng ký tr&ecirc;n website, những th&ocirc;ng tin đăng ký là đúng sự th&acirc;̣t. N&ecirc;n các đ&ocirc;̣i thi lưu ý, khi nhóm có sự thay đ&ocirc;̉i v&ecirc;̀ thành vi&ecirc;n c&acirc;̀n phải đi&ecirc;̀u chỉnh th&ocirc;ng tin tr&ecirc;n trang nhóm của mình.</p>\r\n\r\n<p dir=\"ltr\"><strong>Gửi video v&agrave; bản t&oacute;m tắt &yacute; tưởng</strong></p>\r\n\r\n<p dir=\"ltr\">Các đ&ocirc;̣i dự thi gửi Video và Bản tóm tắt ý tưởng c&acirc;̀n phải đ&acirc;̀y đủ n&ocirc;̣i dung y&ecirc;u c&acirc;̀u của BTC.</p>\r\n\r\n<p dir=\"ltr\"><em>Bản tóm tắt ý tưởng kinh doanh:</em>&nbsp;N&ocirc;̣i dung đ&acirc;̀y đủ bao g&ocirc;̀m: Tóm tắt ý tương, Quá trình hình thành ý tưởng, Giới thi&ecirc;̣u v&ecirc;̀ sản ph&acirc;̉m và dịch vụ, Lĩnh vực/ngành ngh&ecirc;̀ kinh doanh, Tri&ecirc;̉n vọng tương lai của Ý tưởng.</p>\r\n\r\n<p dir=\"ltr\">BTC đã chu&acirc;̉n bị m&ocirc;̣t bản m&acirc;̃u đ&ecirc;̉ các bạn có th&ecirc;̉ hình dung v&ecirc;̀ Hình thức và N&ocirc;̣i dung của Bản tóm tắt. Tuy nhi&ecirc;n, BTC khuy&ecirc;́n khích các đ&ocirc;̣i thi có th&ecirc;̉ &nbsp;phát tri&ecirc;̉n và th&ecirc;̉ hi&ecirc;̣n ý tưởng của mình hơn nữa đ&ecirc;̉ BTC có th&ecirc;̉ nắm rõ th&ecirc;m v&ecirc;̀ ý tưởng kinh doanh của các đ&ocirc;̣i thi</p>\r\n\r\n<p dir=\"ltr\"><em>Video</em>: Nội dung video đ&acirc;̀y đủ th&ocirc;ng tin như Giới thiệu nh&oacute;m, Giới thi&ecirc;̣u th&agrave;nh vi&ecirc;n, Giới thi&ecirc;̣u ý tưởng, T&iacute;nh khả thi, t&iacute;nh vận h&agrave;nh, t&iacute;nh thực tế của &yacute; tưởng.</p>\r\n\r\n<p dir=\"ltr\">Và các đ&ocirc;i thi c&acirc;̀n lưu ý, giới hạn Video từ 3 - 5 phút, n&ecirc;n các đ&ocirc;̣i thi c&acirc;n nhắc đ&ecirc;̉ x&acirc;y dựng n&ocirc;̣i dung phù hợp nhé.</p>\r\n\r\n<p dir=\"ltr\"><strong>&Yacute; tưởng kinh doanh chưa đạt giải của các cuộc thi kh&aacute;c, chưa được đăng ký kinh doanh</strong></p>\r\n\r\n<p dir=\"ltr\">Các ý tưởng dự thi được coi là hợp l&ecirc;̣ là các ý tưởng chưa từng đạt giải cu&ocirc;̣c thi khác; cá ý tưởng mang tính tự phát cá nh&acirc;n chưa được đăng ký kinh doanh.</p>\r\n\r\n<p dir=\"ltr\">Trong trường hợp, ý tưởng đã từng tham gia các cu&ocirc;̣c thi khởi nghi&ecirc;̣p khác và đoạt giải nhưng có n&ocirc;̣i dung khác hẳn so với sản ph&acirc;̉m dự thi trước đó thì trong Bản tóm tắt ý tưởng, đ&ocirc;̣i thi phải chỉ ra sự khách bi&ecirc;̣t v&ecirc;̀ định hướng và ý tưởng so với sản ph&acirc;̉m dự thi trước đó.</p>\r\n\r\n<p dir=\"ltr\">Trong các trường hợp BTC phát hi&ecirc;̣n ra gian l&acirc;̣n, BTC có th&ecirc;̉ hủy tư cách dự thi của đ&ocirc;̣i thi đó.</p>', 'cuộc thi, khởi nghiệp', '2018-10-01 03:10:49', '2018-10-01 03:10:49'),
(12, 'Phát động cuộc thi khởi nghiệp XStartup', 'phat-dong-cuoc-thi-khoi-nghiep-xstartup', '“Cuộc thi XStartup” đã trải qua tuần đầu tiên, BTC đã nhận được một số bài dự thi của các đội thi. Tuy nhiên qua quá trình xem xét và theo dõi các bài dự thi, BTC rút ra những điểm cần lưu ý để các đội thi chú ý và kiểm tra lại bài dự thi của mình nhé!', 'zz_2018-10-01_1538389660.jpeg', '<p>Nhằm gi&uacute;p c&aacute;c bạn trẻ tr&ecirc;n quy m&ocirc; cả nước khởi nghiệp trong thời kỳ c&ocirc;ng nghiệp h&oacute;a, hiện đại h&oacute;a, qua đ&oacute; th&uacute;c đẩy tinh thần khởi nghiệp quốc gia. Cuộc thi khởi nghiệp XStartup được tổ chức bởi Zikzak co-working Space trực thuộc tập đo&agrave;n CGroup đ&atilde; được ph&aacute;t động.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><iframe frameborder=\"0\" height=\"100%\" scrolling=\"no\" src=\"https://www.youtube.com/embed/2iQhx8B4KCc\" width=\"400px\"></iframe></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đ&acirc;y l&agrave; cơ hội tốt để c&aacute;c dự &aacute;n khởi nghiệp c&oacute; thể nhận được sự hỗ trợ, đồng h&agrave;nh để hiện thực h&oacute;a ước mơ khởi nghiệp của m&igrave;nh. Ngo&agrave;i ra, đ&acirc;y cũng ch&iacute;nh l&agrave; hoạt động thiết thực của Ban tổ chức nhằm đưa phong tr&agrave;o khởi nghiệp trẻ ph&aacute;t triển mạnh mẽ hơn.</p>\r\n\r\n<p>Trong dịp n&agrave;y, c&aacute;c th&iacute; sinh đến từ nhiều tỉnh, th&agrave;nh phố tr&ecirc;n cả nước sẽ chia sẻ &yacute; tưởng khởi nghiệp s&aacute;ng tạo của m&igrave;nh, song song với đ&oacute; l&agrave; những kh&oacute; khăn v&agrave; thuận lợi của từng dự &aacute;n sẽ được c&aacute;c chuy&ecirc;n gia cố vấn chương tr&igrave;nh giải đ&aacute;p.</p>\r\n\r\n<p>Với th&ocirc;ng điệp ph&aacute; bỏ mọi giới hạn v&agrave; khơi dậy sức mạnh đến từ t&agrave;i năng tiềm t&agrave;ng của mỗi c&aacute; nh&acirc;n. Khởi nghiệp đổi mới s&aacute;ng tạo d&aacute;m nghĩ, d&aacute;m l&agrave;m v&agrave; l&agrave;m được đ&oacute; l&agrave; mong muốn lớn nhất đến từ ph&iacute;a Ban tổ chức c&ugrave;ng c&aacute;c nh&agrave; t&agrave;i trợ muốn thấy được th&ocirc;ng qua cuộc thi XStartup.</p>\r\n\r\n<p>Vậy l&agrave; cuộc thi XStartup đ&atilde; ch&iacute;nh thức được khởi động từ ng&agrave;y 24/8 v&agrave; Chung kết sẽ diễn ra v&agrave;o ng&agrave;y 24/12. Cuộc thi dự kiến sẽ c&oacute; h&agrave;ng ngh&igrave;n người quan t&acirc;m v&agrave; theo d&otilde;i, trong đ&oacute; c&oacute; nhiều &yacute; tưởng dự &aacute;n tiềm năng gửi về đăng k&yacute; tham gia.</p>\r\n\r\n<p>Ở cuộc thi n&agrave;y, đối tượng tham gia sẽ l&agrave; c&aacute;c bạn trẻ c&oacute; độ tuổi từ 18 tuổi trở l&ecirc;n. Mỗi th&iacute; sinh sẽ kh&ocirc;ng bị giới hạn về số lượng &yacute; tưởng nhưng phải đảm bảo dự &aacute;n c&oacute; tiềm năng thương mại cao, kh&ocirc;ng vi phạm c&aacute;c luật định về sở hữu tr&iacute; tuệ. C&aacute;c sản phẩm, dự &aacute;n đ&atilde; từng đoạt giải tại c&aacute;c cuộc thi khởi nghiệp sẽ kh&ocirc;ng được tham gia. Mọi sai phạm quy định khi ph&aacute;t hiện, sẽ bị hủy quyền dự thi của th&iacute; sinh đăng k&yacute;.</p>\r\n\r\n<p>Lu&ocirc;n lu&ocirc;n c&oacute; những &yacute; tưởng s&aacute;ng tạo kh&ocirc;ng ngừng nghỉ, lu&ocirc;n sẵn s&agrave;ng tinh thần kinh doanh, ph&aacute;t triển &yacute; tưởng v&igrave; mục ti&ecirc;u phục vụ cho cộng đồng, x&atilde; hội đ&oacute; l&agrave; những g&igrave; cần c&oacute; ở thế hệ trẻ hiện nay. V&agrave; XStartup sẽ l&agrave; nơi khởi nguồn đưa những ước mơ ho&agrave;i b&atilde;o của bạn trở th&agrave;nh hiện thực. Ngay từ b&acirc;y giờ c&aacute;c bạn trẻ h&atilde;y chuẩn bị thật tốt dự &aacute;n của m&igrave;nh để gửi tới cuộc thi XStartup, h&atilde;y biết nắm bắt cơ hội &ldquo;v&agrave;ng&rdquo; đến từ Ban tổ chức cuộc thi trao tặng cho bạn.</p>', 'cuộc thi', '2018-10-01 03:27:40', '2018-10-01 03:27:40'),
(13, 'THÔNG BÁO GIA HẠN THỜI GIAN NỘP HỒ SƠ', 'thong-bao-gia-han-thoi-gian-nop-ho-so', 'BTC nhận được rất nhiều tin nhắn từ các bạn với mong muốn xin gia hạn thời gian nộp bài để các bạn hoàn thiện và cho ra những sản phẩm hoàn hảo hơn. Cập nhật thông tin để dành thời gian chuẩn bị bài dự thi tốt nhất nào!!!', 'zz_2018-10-02_1538471200.jpeg', '<p dir=\"ltr\" style=\"margin-left:0px; margin-right:0px; text-align:left\">Từ ng&agrave;y 10/09/2018, BTC Cuộc thi XStartup ch&iacute;nh thức nhận b&agrave;i dự thi của c&aacute;c nh&oacute;m tham dự.</p>\r\n\r\n<p dir=\"ltr\" style=\"margin-left:0px; margin-right:0px; text-align:left\">Trong gần một th&aacute;ng qua, rất nhiều b&agrave;i dự thi chất lượng đ&atilde; gửi về Cuộc thi qua website&nbsp;<a href=\"http://xstartup.zikzak-co.vn/\" style=\"box-sizing: border-box; font-size: 14px; color: rgb(0, 123, 255); margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; text-decoration: none; background-color: transparent;\">http://xstartup.zikzak-co.vn</a></p>\r\n\r\n<p dir=\"ltr\" style=\"margin-left:0px; margin-right:0px; text-align:left\">Tuy vậy, rất nhiều nh&oacute;m dự thi bỡ ngỡ bởi lần đầu đến với s&acirc;n chơi d&agrave;nh cho c&aacute;c startup v&agrave; mong muốn BTC gia hạn thời gian nộp b&agrave;i để ho&agrave;n thiện v&agrave; cho ra những sản phẩm ho&agrave;n hảo hơn.</p>\r\n\r\n<p dir=\"ltr\" style=\"margin-left:0px; margin-right:0px; text-align:left\">&nbsp;</p>\r\n\r\n<p dir=\"ltr\" style=\"margin-left:0px; margin-right:0px; text-align:left\">Để đ&aacute;p lại mong muốn của c&aacute;c th&iacute; sinh, BTC quyết định gia hạn thời gian nộp hồ sơ, cụ thể timeline mới của c&aacute;c v&ograve;ng như sau:</p>\r\n\r\n<p dir=\"ltr\" style=\"margin-left:0px; margin-right:0px; text-align:left\">V&ograve;ng 1 - &Yacute; tưởng: 10/09 -&nbsp;20/10</p>\r\n\r\n<p dir=\"ltr\" style=\"margin-left:0px; margin-right:0px; text-align:left\">V&ograve;ng 2 - Lan tỏa: 25/10 - 31/10</p>\r\n\r\n<p dir=\"ltr\" style=\"margin-left:0px; margin-right:0px; text-align:left\">V&ograve;ng 3 - B&ugrave;ng nổ:</p>\r\n\r\n<ul style=\"margin-left:0px; margin-right:0px\">\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\" style=\"margin-left:0px; margin-right:0px\">12/11 - B&aacute;n kết miền Bắc</p>\r\n	</li>\r\n	<li dir=\"ltr\">\r\n	<p dir=\"ltr\" style=\"margin-left:0px; margin-right:0px\">15/11 - B&aacute;n kết miền Nam</p>\r\n	</li>\r\n</ul>\r\n\r\n<p dir=\"ltr\" style=\"margin-left:0px; margin-right:0px; text-align:left\">V&ograve;ng 4 - Nh&acirc;n tố X: 24/11 - 24/12&nbsp;</p>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:left\"><img alt=\"\" class=\"zb\" src=\"http://xstartup.zikzak-co.vn/local/resources/uploads/userfiles/images/mail%20th%C3%AD%20sinh%20%2B%20%C4%91%C4%83ng%20landing%20page-01%20(2).jpg\" style=\"border:0px none; box-sizing:border-box; color:var(--text-4); display:block; font-size:14px; margin:auto; max-width:100%; padding:0px; vertical-align:middle\" /><br />\r\n&nbsp;</p>\r\n\r\n<p dir=\"ltr\" style=\"margin-left:0px; margin-right:0px; text-align:left\">Như vậy, c&aacute;c bạn c&oacute; th&ecirc;m 15 ng&agrave;y để chuẩn bị ho&agrave;n thiện Bản t&oacute;m tắt dự &aacute;n v&agrave; Video giới thiệu!!!</p>\r\n\r\n<p dir=\"ltr\" style=\"margin-left:0px; margin-right:0px; text-align:left\">Hy vọng với th&ocirc;ng tin tr&ecirc;n, BTC sẽ nhận th&ecirc;m được nhiều b&agrave;i dự thi chất lượng!!!!</p>\r\n\r\n<p dir=\"ltr\" style=\"margin-left:0px; margin-right:0px; text-align:left\"><em>Ch&uacute;c c&aacute;c bạn thi tốt!!!</em></p>', 'XStartup, cuoc thi, khoi nghiep', '2018-10-02 02:06:39', '2018-10-02 02:06:39'),
(14, 'Phó TGĐ Tập đoàn CGroup không học đại học vẫn khởi nghiệp thành công', 'pho-tgd-tap-doan-cgroup-khong-hoc-dai-hoc-van-khoi-nghiep-thanh-cong', 'XStartup là cuộc thi tìm kiếm những gương mặt trẻ có khả năng kinh doanh, có những ý tưởng khởi nghiệp mới lạ mang tính đột phá. Hiện tại XStartup đã “hé lộ” thành phần Ban giám khảo khá đặc biệt.', 'zz_2018-10-03_1538555787.jpeg', '<p>XStartup cuộc thi khởi nghiệp thu h&uacute;t kh&aacute; nhiều sự quan t&acirc;m của c&aacute;c bạn trẻ. Đ&acirc;y l&agrave; s&acirc;n chơi kinh doanh để c&aacute;c th&iacute; sinh đến từ mọi miền Tổ quốc khẳng định t&agrave;i năng, &yacute; tưởng s&aacute;ng tạo của m&igrave;nh để thuyết phục Hội đồng chuy&ecirc;n m&ocirc;n. Ng&agrave;y 03/09/2018 cuộc thi đ&atilde; ch&iacute;nh thức khởi động, tr&ecirc;n fanpage của cuộc thi v&agrave; tr&ecirc;n c&aacute;c website đ&atilde; nhận được rất nhiều sự quan t&acirc;m của c&aacute;c bạn trẻ gửi về. Điều n&agrave;y chứng tỏ lực lượng thanh ni&ecirc;n trẻ đ&atilde; thực sự ch&uacute; &yacute; v&agrave; coi trọng vấn đề khởi nghiệp, định hướng tương lai.</p>\r\n\r\n<p><img alt=\"\" src=\"http://xstartup.zikzak-co.vn/local/resources/uploads/userfiles/images/Giam-khao-Tran-Quan-Vinh-cuoc-thi-xstartup.jpg\" /></p>\r\n\r\n<p>Ph&oacute; tổng Gi&aacute;m đốc Tập đo&agrave;n CGroup khởi nghiệp th&agrave;nh c&ocirc;ng d&ugrave; kh&ocirc;ng học đại học đ&oacute; l&agrave; anh Trần Quang Vinh một trong những vị Ban gi&aacute;m khảo trẻ của cuộc thi XStartup. Giữ vị tr&iacute; Ph&oacute; trưởng ban tổ chức cuộc thi anh Trần Quang Vinh đ&atilde; c&oacute; những chia sẻ đến c&aacute;c th&iacute; sinh: Cụm từ &ldquo;khởi nghiệp&rdquo; chắc hẳn đ&atilde; qu&aacute; quen thuộc đối với ch&uacute;ng ta, nhất l&agrave; trong thời đại c&ocirc;ng nghiệp h&oacute;a, hiện đại h&oacute;a đất nước đang đổi mới từng ng&agrave;y. Lực lượng lao động trẻ của nước ta rất đ&ocirc;ng đảo, hơn nữa lại rất tư duy, s&aacute;ng tạo. Thế nhưng trong qu&aacute; tr&igrave;nh khởi nghiệp, c&aacute;c bạn trẻ gặp kh&ocirc;ng &iacute;t kh&oacute; khăn từ sự thiếu kinh nghiệm thực tiễn, thiếu vốn đầu tư, thiếu sự nh&igrave;n nhận đ&aacute;nh gi&aacute; từ những người trong nghề. Việc Tập đo&agrave;n CGroup đem đến cho c&aacute;c bạn một cuộc thi XStartup như giống trao tận tay cơ hội khởi nghiệp v&agrave; chỉ chờ bạn d&aacute;m khẳng định &yacute; tưởng của m&igrave;nh l&agrave; đ&uacute;ng đắn, mang lợi &iacute;ch thiết thực. Bản th&acirc;n t&ocirc;i mong muốn hỗ trợ c&aacute;c c&aacute; nh&acirc;n, nh&oacute;m khởi nghiệp, đặc biệt l&agrave; c&aacute;c bạn trẻ c&oacute; tham vọng &ldquo;vươn ra thế giới&rdquo; với tr&iacute; tuệ Việt. Ngay sau khi được giới thiệu về cuộc thi, t&ocirc;i đ&atilde; rất quan t&acirc;m v&agrave; muốn được l&agrave; một phần trong chương tr&igrave;nh n&agrave;y.</p>\r\n\r\n<p>Anh Trần Quang Vinh l&agrave; một trong những tấm gương khởi nghiệp th&agrave;nh c&ocirc;ng, hiện tại anh đang giữ chức vị Ph&oacute; tổng Gi&aacute;m đốc Tập đo&agrave;n CGroup. Để c&oacute; được vị tr&iacute; như ng&agrave;y h&ocirc;m nay, vị Ph&oacute; tổng trẻ sinh năm 1987 đ&atilde; phải trải qua con đường g&acirc;y dựng sự nghiệp đầy ch&ocirc;ng gai. C&oacute; nhiều c&aacute;ch để khởi nghiệp v&agrave; ch&agrave;ng trai trẻ kh&ocirc;ng chọn con đường theo học đại học. Ngay từ khi tốt nghiệp lớp 12 anh đ&atilde; tự m&agrave;y m&ograve; t&igrave;m hiểu lĩnh vực thiết kế đồ họa v&igrave; đ&oacute; l&agrave; sở th&iacute;ch c&aacute; nh&acirc;n, sau đ&oacute; anh đ&atilde; t&igrave;m tới c&aacute;c trung t&acirc;m để học v&agrave; cũng kh&ocirc;ng ngại học việc ở nhiều c&ocirc;ng ty cho d&ugrave; mức lương kh&ocirc;ng cao. Anh Trần Quang Vinh chia sẻ: Đừng thấy c&aacute;i lợi trước mắt m&agrave; qu&ecirc;n đi lợi &iacute;ch thiết thực l&acirc;u d&agrave;i, đ&oacute; ch&iacute;nh l&agrave; l&yacute; do t&ocirc;i ki&ecirc;n tr&igrave; theo đuổi, học việc mặc d&ugrave; c&oacute; thể kiếm được c&aacute;c c&ocirc;ng việc kh&aacute;c c&oacute; thu nhập cao hơn l&uacute;c bấy giờ. N&oacute;i kh&ocirc;ng phải cổ vũ cho c&aacute;c bạn trẻ l&agrave; kh&ocirc;ng cần học đại học cũng c&oacute; thể th&agrave;nh c&ocirc;ng nhưng đ&oacute; cũng kh&ocirc;ng phải l&agrave; con đường duy nhất để c&aacute;c bạn c&oacute; thể vươn tới th&agrave;nh c&ocirc;ng, quan trọng l&agrave; sự khi&ecirc;m tốn, chăm chỉ kh&ocirc;ng ngừng học hỏi học thầy, học bạn, học x&atilde; hội.</p>\r\n\r\n<p>Ở thời điểm hiện tại anh Trần Quang Vinh đang giữ chức vụ Ph&oacute; tổng Gi&aacute;m đốc Tập đo&agrave;n CGroup. Để c&oacute; được vị thế x&atilde; hội như ng&agrave;y h&ocirc;m nay, vị Ph&oacute; tổng trẻ đ&atilde; trải qua kh&ocirc;ng &iacute;t ch&ocirc;ng gai, đ&atilde; từng lu&acirc;n chuyển c&ocirc;ng việc ở nhiều c&ocirc;ng ty ở nhiều vị tr&iacute; kh&aacute;c nhau. Ch&iacute;nh v&igrave; vậy anh hiểu r&otilde; được những kh&oacute; khăn m&agrave; c&aacute;c bạn trẻ bước đầu khởi nghiệp thường gặp phải. C&oacute; một c&acirc;u n&oacute;i m&agrave; vị Ban gi&aacute;m khảo trẻ n&agrave;y rất t&acirc;m đắc: &ldquo;N&ecirc;́u bạn kh&ocirc;ng thử bạn sẽ kh&ocirc;ng bao giờ bi&ecirc;́t được mình có th&ecirc;̉ làm được những gì&rdquo;. Quả thực l&agrave; như vậy d&ugrave; trong bất kỳ ho&agrave;n cảnh n&agrave;o, ch&uacute;ng ta đừng vội nghĩ rằng bản th&acirc;n m&igrave;nh kh&ocirc;ng thể, nếu như bạn chưa thực sự sẵn s&agrave;ng để bắt đầu. Đ&oacute; cũng ch&iacute;nh l&agrave; lời nhắn nhủ t&acirc;m can của vị Ph&oacute; tổng muốn gửi tới c&aacute;c bạn trẻ.</p>\r\n\r\n<p><img alt=\"\" src=\"http://xstartup.zikzak-co.vn/local/resources/uploads/userfiles/images/Tran-Quang-Vinh-Pho-tong-Giam-doc-tap-doan-CGroup.jpg\" /></p>\r\n\r\n<p>Hơn ai h&ecirc;́t, đến với chương tr&igrave;nh khởi nghiệp với XStartup anh Vinh hi&ecirc;̉u rõ sự nghi&ecirc;m túc của m&ocirc;̃i đ&ocirc;̣i chơi, bằng t&acirc;m huy&ecirc;́t của mình, vị Ph&oacute; tổng trẻ sẽ đồng h&agrave;nh c&ugrave;ng những giám khảo khác tại vòng 01 c&ocirc;ng t&acirc;m chọn ra 60 đ&ocirc;̣i thi xu&acirc;́t sắc nh&acirc;́t.</p>', 'giám khảo, cuộc thi, khởi nghiệp', '2018-10-03 01:36:27', '2018-10-03 01:36:27'),
(15, 'Bỏ dở việc du học, 9x trẻ khởi nghiệp thành công trở thành CEO Tập đoàn Zent Group', 'bo-do-viec-du-hoc-9x-tre-khoi-nghiep-thanh-cong-tro-thanh-ceo-tap-doan-zent-group', 'Từ bỏ ngành học nhiều triển vọng tại một ngôi trường danh tiếng của Hàn Quốc, 9x trẻ Vũ Văn Thương trở về Việt Nam khởi nghiệp bất chấp muôn vàn khó khăn để có được thành công đáng tự hào. Đó là câu chuyện khởi nghiệp đầy chông gai của một vị BGK trong cuộc thi XStartup.', 'zz_2018-10-20_1540002878.jpeg', '<p>XStartup cuộc thi khởi nghiệp thu h&uacute;t kh&aacute; nhiều sự quan t&acirc;m của c&aacute;c bạn trẻ. Đ&acirc;y l&agrave; s&acirc;n chơi kinh doanh để c&aacute;c th&iacute; sinh đến từ mọi miền Tổ quốc khẳng định t&agrave;i năng, &yacute; tưởng s&aacute;ng tạo của m&igrave;nh để thuyết phục Hội đồng chuy&ecirc;n m&ocirc;n. Mới đ&acirc;y BTC cuộc thi đ&atilde; h&eacute; lộ danh t&iacute;nh của một vị nằm trong danh s&aacute;ch Ban gi&aacute;m khảo cuộc thi tại v&ograve;ng 1, đ&oacute; l&agrave; anh Vũ Văn Thương CEO trẻ tuổi thuộc Tập đo&agrave;n Zent Group.</p>\r\n\r\n<p><img alt=\"\" src=\"http://xstartup.zikzak-co.vn/local/resources/uploads/userfiles/images/vu-van-thuong.jpg\" /></p>\r\n\r\n<p>Anh Vũ Văn Thương l&agrave; cựu du học sinh H&agrave;n Quốc, hiện tại anh đang giữ chức vụ Gi&aacute;m đốc điều h&agrave;nh của Zent Group. Zent Group l&agrave; một trong những Tập đo&agrave;n hoạt động đa lĩnh vực như: Gi&aacute;o dục, phần mềm, nh&acirc;n sự v&agrave; Startup với thế mạnh về gi&aacute;o dục c&ugrave;ng c&aacute;c kh&oacute;a học về lập tr&igrave;nh v&agrave; c&ocirc;ng nghệ th&ocirc;ng tin.</p>\r\n\r\n<p>Để c&oacute; được th&agrave;nh c&ocirc;ng như ng&agrave;y h&ocirc;m nay vị CEO trẻ tuổi đ&atilde; phải trải qua con đường khởi nghiệp kh&aacute; ch&ocirc;ng gai. Từng theo học một ng&agrave;nh kh&aacute; Hot tại H&agrave;n Quốc, cơ hội việc l&agrave;m cũng kh&aacute; ổn thế nhưng anh ch&agrave;ng n&agrave;y đ&atilde; c&oacute; một quyết định kh&aacute; t&aacute;o bạo đ&oacute; l&agrave; tạm dừng việc du học trở về Việt Nam khởi nghiệp. Chia sẻ về l&yacute; do đi đến quyết định n&agrave;y CEO Vũ Văn Thương n&oacute;i: &ldquo;T&ocirc;i lu&ocirc;n quan niệm rằng cuộc sống của m&igrave;nh lu&ocirc;n phải c&oacute; hai điều &ldquo;l&agrave;m c&aacute;i m&igrave;nh th&iacute;ch&rdquo; v&agrave; &ldquo;phải tận dụng thế mạnh của m&igrave;nh&rdquo;. T&ocirc;i vốn l&agrave; một người lu&ocirc;n muốn &ldquo;thử&rdquo;, thử l&agrave;m nhiều việc, thử nhiều điều kh&aacute;c nhau kh&ocirc;ng chỉ để trải nghiệm m&agrave; c&ograve;n l&agrave; t&igrave;m ra những thứ bản th&acirc;n thật sự th&iacute;ch m&agrave; trước giờ chưa biết. Khi đi học, một ng&agrave;y t&ocirc;i &ocirc;m m&aacute;y t&iacute;nh đến mười mấy giờ đồng hồ, thế nhưng bản th&acirc;n lại l&agrave; người th&iacute;ch bay nhảy. N&ecirc;n khi về Việt Nam gặp lại người bạn c&ugrave;ng l&agrave;m việc trước kia hai đứa đ&atilde; c&ugrave;ng nhau nghĩ tới việc khởi nghiệp. V&agrave; thật may mắn t&ocirc;i đ&atilde; lựa chọn đ&uacute;ng người c&oacute; tầm c&oacute; t&acirc;m để c&ugrave;ng g&acirc;y dựng sự nghiệp. Đ&oacute; c&oacute; thể l&agrave; quyết định mạo hiểm nhưng liều lĩnh mới tạo n&ecirc;n tuổi trẻ m&agrave;&rdquo;.</p>\r\n\r\n<p><img alt=\"\" src=\"http://xstartup.zikzak-co.vn/local/resources/uploads/userfiles/images/vu-van-thuong-ceo-zentgroup.jpg\" /></p>\r\n\r\n<p>Cũng giống như c&aacute;c bạn trẻ bước đầu &ldquo;chập chững&rdquo; khởi nghiệp vị CEO trẻ tuổi cũng gặp kh&ocirc;ng &iacute;t kh&oacute; khăn. Nhất l&agrave; khi bản th&acirc;n l&agrave; một du học sinh c&oacute; nhiều cơ hội để c&oacute; thể lập nghiệp ở nước ngo&agrave;i, l&agrave; sự h&atilde;nh diện của gia đ&igrave;nh v&agrave; bạn b&egrave; thế nhưng lại bỏ ngang việc học trở về nước để lập nghiệp. Ch&iacute;nh điều đ&oacute; đ&atilde; tạo n&ecirc;n &aacute;p lực kh&ocirc;ng &iacute;t cho vị Gi&aacute;m khảo trẻ tuổi n&agrave;y. Thế nhưng anh Vũ Văn Thương đ&atilde; khẳng định lựa chọn của m&igrave;nh kh&ocirc;ng hề sai, những th&agrave;nh c&ocirc;ng hiện tại đ&atilde; n&oacute;i l&ecirc;n tất cả. Khi được hỏi c&oacute; bao giờ anh cảm thấy hối tiếc về việc bỏ dở ng&agrave;nh học tại H&agrave;n Quốc kh&ocirc;ng? CEO Tập đo&agrave;n Zent Group chia sẻ: &ldquo;T&ocirc;i nghĩ rằng c&aacute;i g&igrave; cũng phải c&oacute; gi&aacute; của n&oacute;, đ&ocirc;i l&uacute;c cũng thấy tiếc, nhưng t&ocirc;i nghĩ l&agrave; những g&igrave; được nhiều hơn l&agrave; mất. B&acirc;y giờ nếu được chọn lại th&igrave; t&ocirc;i vẫn lựa chọn việc ở Việt Nam v&agrave; khởi nghiệp&rdquo;.</p>\r\n\r\n<p>N&oacute;i về cuộc thi khởi nghiệp XStartup anh Vũ Văn Thương chia sẻ: &ldquo;Tuy hệ sinh th&aacute;i khởi nghiệp của Việt Nam đi sau c&aacute;c nước, thế nhưng nếu c&oacute; bước đi đột ph&aacute;, tập hợp c&aacute;c startup v&agrave;o chung một s&acirc;n chơi, được hỗ trợ nhanh ch&oacute;ng th&igrave; startup Việt sẽ gi&agrave;nh thắng lợi&rdquo;. V&agrave; XStartup l&agrave; một s&acirc;n chơi như vậy, n&oacute;i đ&uacute;ng hơn l&agrave; một cơ hội được trao tặng c&ocirc;ng bằng cho tất cả c&aacute;c bạn trẻ. Việc của c&aacute;c bạn l&agrave; h&atilde;y biến những đam m&ecirc;, những kh&aacute;t vọng đ&oacute; trở th&agrave;nh hiện thực bằng c&aacute;ch mạnh dạn đăng k&yacute; tham dự cuộc thi. Khi biết đến cuộc thi v&agrave; c&oacute; lời mời l&agrave;m BGK v&ograve;ng 1 t&ocirc;i đ&atilde; kh&ocirc;ng ngần ngại đồng &yacute; lu&ocirc;n. Bởi t&ocirc;i muốn đồng h&agrave;nh c&ugrave;ng chương tr&igrave;nh để c&oacute; thể gi&uacute;p đỡ một phần n&agrave;o đ&oacute; sức của m&igrave;nh cho c&aacute;c bạn trẻ.</p>\r\n\r\n<p>Hiện nay c&oacute; rất nhiều bạn trẻ Startup, thậm ch&iacute; c&ograve;n trở th&agrave;nh một tr&agrave;o lưu. Nhưng những Startup th&agrave;nh c&ocirc;ng v&agrave; hoạt động ổn định l&agrave; rất &iacute;t. CEO Vũ Văn Thương cho rằng: Thất bại l&agrave; một gia vị của th&agrave;nh c&ocirc;ng v&agrave; c&aacute;c bạn trẻ đừng ngại vấn đề thất bại. H&atilde;y coi s&acirc;n chơi XStartup l&agrave; một bước đệm vững chắc cho sự nghiệp kinh doanh sau n&agrave;y, một cuộc thi rất đ&aacute;ng để c&aacute;c bạn trẻ dạt d&agrave;o nhiệt huyết thử sức.</p>\r\n\r\n<p><strong>Thanh Hương</strong></p>', 'cuộc thi, zent group, khởi nghiệp', '2018-10-19 19:34:38', '2018-10-19 19:34:38'),
(16, '[Zent Group]Kết nối đam mê cho tân sinh viên IT', 'zent-groupket-noi-dam-me-cho-tan-sinh-vien-it', 'Vào ngày 21/10 vừa qua Zent Education Group kết hợp cùng Zikzak co-working Space đã tổ chức sự kiện “Tân sinh viên IT - Kết nối đam mê”. Đây là sự kiện được các bạn tân sinh viên IT ở cả hai tỉnh thành phố lớn nhất cả nước là Hà Nội và Tp. Hồ Chí Minh háo hức trông đợi.', 'zz_2018-10-24_1540377212.jpeg', '<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">&ldquo;T&acirc;n sinh vi&ecirc;n IT - Kết nối đam m&ecirc;&rdquo; l&agrave; sự kiện thường ni&ecirc;n được Zent Education Group tổ chức v&agrave;o đầu mỗi năm học. Chương tr&igrave;nh nhằm tạo s&acirc;n chơi gi&uacute;p c&aacute;c bạn t&acirc;n sinh vi&ecirc;n chuy&ecirc;n ng&agrave;nh IT ở hai miền Nam- Bắc c&oacute; dịp giao lưu cũng như học hỏi những kinh nghiệm trong những ng&agrave;y đầu bước ch&acirc;n v&agrave;o giảng đường đại học. </span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\"><img alt=\"\" src=\"/local/storage/app/image/images/Zent/44602347_1167780136724865_7393549998337556480_n.jpg\" style=\"height:500px; width:667px\" /></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">Tại chương tr&igrave;nh, c&aacute;c bạn t&acirc;n sinh vi&ecirc;n đ&atilde; được gặp gỡ, giao lưu v&agrave; chia sẻ kinh nghiệm với c&aacute;c anh chị cựu sinh vi&ecirc;n IT, hiện nay l&agrave; những t&ecirc;n tuổi k&igrave; cựu trong lĩnh vực C&ocirc;ng nghệ như anh Vũ Văn Trương &ndash; Gi&aacute;m đốc điều h&agrave;nh Zent Education Group, anh Đo&agrave;n C&ocirc;ng Chung &ndash; Tổng gi&aacute;m đốc điều h&agrave;nh CGROUP, chị Trần Diệu Ni &ndash; Cựu sinh vi&ecirc;n trường Đại Học Khoa Học Tự Nhi&ecirc;n&hellip; B&ecirc;n cạnh việc chia sẻ những kiến thức, kinh nghiệm học tập c&aacute;c c&aacute;c anh chị đi trước c&ograve;n gi&uacute;p c&aacute;c bạn t&acirc;n sinh vi&ecirc;n hiểu th&ecirc;m về những định hướng nghề cũng như những kinh nghiệm qu&yacute; b&aacute;u tr&ecirc;n con đường trở th&agrave;nh lập tr&igrave;nh vi&ecirc;n chuy&ecirc;n nghiệp.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">Ngo&agrave;i ra, v&agrave;o cuối chương tr&igrave;nh, c&aacute;c bạn t&acirc;n sinh vi&ecirc;n c&ograve;n được c&aacute;c anh chị đưa đi tham quan c&aacute;c doanh nghiệp, c&aacute;c m&ocirc; h&igrave;nh thực tế trong lĩnh vực c&ocirc;ng nghệ để gi&uacute;p c&aacute;c bạn được mục sở thị v&agrave; học hỏi th&ecirc;m những kiến thức, kinh nghiệm bổ &iacute;ch.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\"><img alt=\"\" src=\"/local/storage/app/image/images/Zent/44593725_1167779750058237_4706523805470687232_n.jpg\" style=\"height:500px; width:667px\" /></span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">Với mục đ&iacute;ch tạo ra s&acirc;n chơi bổ &iacute;ch cho c&aacute;c bạn t&acirc;n sinh vi&ecirc;n hai miền Nam-Bắc, &ldquo;T&acirc;n sinh vi&ecirc;n IT &ndash; Kết nối đam m&ecirc;&rdquo; được tổ chức tại địa điểm trang ho&agrave;ng theo hướng rất trẻ v&agrave; năng động l&agrave; ZikZak co-working space (kh&ocirc;ng gian l&agrave;m việc chung) thuộc CGROUP do anh Đo&agrave;n C&ocirc;ng Chung s&aacute;ng lập. Đ&acirc;y l&agrave; một trong những m&ocirc; h&igrave;nh kh&ocirc;ng gian l&agrave;m việc chung đang thịnh h&agrave;nh tr&ecirc;n d&ograve;ng chảy khởi nghiệp của Việt Nam n&oacute;i ri&ecirc;ng v&agrave; của thế giới n&oacute;i chung.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">Ngo&agrave;i việc được học hỏi những kinh nghiệm, kiến thức qu&yacute; b&aacute;u từ c&aacute;c anh chị đi trước, khi tham gia chương tr&igrave;nh n&agrave;y, c&aacute;c bạn t&acirc;n sinh vi&ecirc;n nhận được DVD t&agrave;i liệu học tập c&ugrave;ng Cẩm nang t&acirc;n sinh vi&ecirc;n IT được ph&aacute;t h&agrave;nh đặc biệt d&agrave;nh ri&ecirc;ng cho c&aacute;c bạn. Tuyệt vời hơn khi tham gia chương tr&igrave;nh, c&aacute;c bạn c&ograve;n được tiếp cận l&agrave;m quen cũng như c&oacute; cơ hội giao lưu c&aacute;c chuy&ecirc;n gia trong ng&agrave;nh IT, mở ra cơ hội tiềm năng về việc l&agrave;m trong tương lai. </span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><img alt=\"\" src=\"/local/storage/app/image/images/Zent/44719450_1167780206724858_4704166263562174464_n.jpg\" style=\"height:500px; width:667px\" /></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">Th&ocirc;ng qua chương tr&igrave;nh, đại diện Zent Education Group cũng như Zikzak co-working Space mong rằng c&aacute;c bạn t&acirc;n sinh vi&ecirc;n sẽ c&oacute; th&ecirc;m &nbsp;định hướng r&otilde; r&agrave;ng hơn đối với nghề nghiệp trong tương lai. Đồng thời buổi gặp gỡ cũng tiếp th&ecirc;m lửa đam m&ecirc; cho c&aacute;c t&acirc;n sinh vi&ecirc;n tr&ecirc;n h&agrave;nh tr&igrave;nh chinh phục cũng như nu&ocirc;i dưỡng ước mơ c&ocirc;ng nghệ.</span></span></span></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:right\"><em><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Tahoma&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">Zikzak Coworking Space</span></span></span></span></span></em></p>', 'cuộc thi, tân sinh viên, IT, Zent group', '2018-10-24 03:33:32', '2018-10-24 03:33:32');
INSERT INTO `news` (`news_id`, `news_name`, `news_slug`, `news_summary`, `news_image`, `news_content`, `tag`, `created_at`, `updated_at`) VALUES
(17, 'ZIKZAK CO-WORKING SPACE VÀ CÁI CHUYỆN TỰ HỌC!!', 'zikzak-co-working-space-va-cai-chuyen-tu-hoc', 'Tự học là một trong những kỹ năng cốt lõi để thành công trong học tập. Mỗi người có một cách tự học riêng và mang đến hiệu quả cho bản thân. Tuy nhiên, nếu bạn cảm thấy việc tự học chưa có tiến triển, hoặc bản thân chưa tìm ra cách học phù hợp thì hãy cùng Zikzak co-working space tham khảo cách để tự học thành công dưới đây nhé!', 'zz_2018-10-29_1540787865.jpeg', '<p style=\"text-align:justify\"><strong><span style=\"font-size:13pt\"><em>1. Đề ra mục ti&ecirc;u cụ thể v&agrave; lập kế hoạch r&otilde; r&agrave;ng nhằm đạt được mục ti&ecirc;u</em></span></strong></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Như bạn đ&atilde; biết, khi l&agrave;m một việc g&igrave; đ&oacute; cũng cần c&oacute; mục ti&ecirc;u cụ thể r&otilde; r&agrave;ng v&agrave; bản kế hoạch r&otilde; r&agrave;ng gi&uacute;p bạn dễ d&agrave;ng đạt đến mục ti&ecirc;u đề ra. Việc tự học cũng kh&ocirc;ng ngoại lệ. Bạn muốn nhanh ch&oacute;ng cải thiện việc học tất cả c&aacute;c m&ocirc;n? Hoặc cụ thể hơn l&agrave; đạt điểm cao hơn trong m&ocirc;n H&oacute;a tại k&igrave; thi sắp tới?... Tất cả những g&igrave; bạn cần l&agrave;m l&agrave; biết r&otilde; bản th&acirc;n muốn đạt đến điều g&igrave;, v&agrave; đặt ra mục ti&ecirc;u c&agrave;ng r&otilde; r&agrave;ng c&agrave;ng tốt. B&ecirc;n cạnh đ&oacute; việc l&ecirc;n kế hoạch một c&aacute;ch khoa học gi&uacute;p bạn nhanh ch&oacute;ng đạt được mục ti&ecirc;u.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><img alt=\"Mục tiêu cụ thể của bản thân\" src=\"/local/storage/app/image/images/target.jpg\" style=\"border-style:solid; border-width:0px; height:700px; margin:0px; width:700px\" /></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><em><span style=\"font-size:17.3333px\">Đặt ra mục ti&ecirc;u r&otilde; r&agrave;ng cho bản th&acirc;n</span></em></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><img alt=\"Kế hoạch giúp đạt được mục tiêu trong việc tự học nhanh chóng\" src=\"/local/storage/app/image/images/plan.png\" style=\"border-style:solid; border-width:0px; height:500px; margin:0px; width:700px\" /> </span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><em><span style=\"font-size:17.3333px\">L&ecirc;n kế hoạch khoa học, ph&ugrave; hợp</span></em></p>\r\n\r\n<p style=\"text-align:justify\"><strong><span style=\"font-size:13pt\"><em>2. Bạn phải ki&ecirc;n tr&igrave; v&agrave; nhẫn nại</em></span></strong></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Điều quan trọng g&oacute;p phần v&agrave;o việc bạn c&oacute; th&agrave;nh c&ocirc;ng trong việc tự học hay kh&ocirc;ng l&agrave; bạn ph&aacute;i ki&ecirc;n tr&igrave;. Nếu như bạn đ&atilde; c&oacute; mục ti&ecirc;u v&agrave; kế hoạch cụ thể để thực hiện mục ti&ecirc;u, nhưng bạn sẽ kh&ocirc;ng bao giờ đạt được ước muốn nếu bạn kh&ocirc;ng bắt tay v&agrave;o l&agrave;m, hoặc chỉ l&agrave;m v&agrave;i ba h&ocirc;m rồi để mặc kế hoạch v&agrave; mục ti&ecirc;u của bạn đ&oacute;ng bụi. Điều bạn cần l&agrave;m l&agrave; thường xuy&ecirc;n nhắc nhở bản th&acirc;n về mục ti&ecirc;u ban đầu. Điều n&agrave;y kh&ocirc;ng những gi&uacute;p bạn giữ lửa để duy tr&igrave; đường đến mục ti&ecirc;u m&agrave; c&ograve;n gi&uacute;p bạn kh&ocirc;ng bị đi chệch hướng trong qu&aacute; tr&igrave;nh hiện thực h&oacute;a mục ti&ecirc;u của m&igrave;nh.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><img alt=\"Kiên trì không từ bỏ mục tiêu đề ra\" src=\"/local/storage/app/image/images/hqdefault.jpg\" style=\"border-style:solid; border-width:0px; height:500px; margin:0px; width:700px\" /></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><em><span style=\"font-size:13pt\">H&atilde;y ki&ecirc;n tr&igrave; nhẫn nại, đừng bao giờ từ bỏ</span></em></p>\r\n\r\n<p style=\"text-align:justify\"><strong><span style=\"font-size:13pt\"><em>3. Kh&ocirc;ng ngừng kỷ luật bản th&acirc;n</em></span></strong></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Trong qu&aacute; tr&igrave;nh tự học, c&oacute; nhiều l&uacute;c bạn cảm thấy mệt mỏi v&agrave; ch&aacute;n nản. Bạn hay tự nhủ với bản th&acirc;n rằng &ldquo;Th&ocirc;i để mai m&igrave;nh học b&ugrave; cho h&ocirc;m nay cũng được&rdquo;; &ldquo;30 ph&uacute;t nữa m&igrave;nh chắc chắn sẽ học sau khi chơi nốt v&aacute;n game n&agrave;y&rdquo;.... Bạn tự đưa ra l&yacute; do để b&agrave;o chữa cho việc kh&ocirc;ng tu&acirc;n thủ theo kế hoạch bạn đề ra ban đầu, dần d&agrave; bạn qu&ecirc;n mất mục ti&ecirc;u v&agrave; kh&ocirc;ng c&ograve;n th&oacute;i quen tự học nữa, hậu quả l&agrave; bạn thử rất nhiều phương ph&aacute;p tự học nhưng kh&ocirc;ng c&aacute;ch n&agrave;o hiệu quả cả. </span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Điều bạn cần l&agrave;m ở đ&acirc;y l&agrave; tự đưa ra h&igrave;nh phạt cho bản th&acirc;n mỗi l&uacute;c vi phạm kế hoạch đề ra. V&iacute; dụ: Đến giờ học b&agrave;i m&ocirc;n Tiếng Anh, nhưng bạn mải chơi game n&ecirc;n trễ 5 ph&uacute;t. Bạn tự phạt m&igrave;nh 10k đ&oacute;ng heo, hoặc phải khao người kh&aacute;c ăn một chầu ch&egrave; v&igrave; lỗi của m&igrave;nh...Những h&igrave;nh phạt nho nhỏ tr&ecirc;n sẽ gi&uacute;p bạn tr&aacute;nh được t&igrave;nh trạng <em>tự b&agrave;o chữa </em>v&agrave; kỷ luật bản th&acirc;n được tốt hơn. Gi&uacute;p bạn nhanh ch&oacute;ng th&agrave;nh c&ocirc;ng trong phương ph&aacute;p tự học của ch&iacute;nh m&igrave;nh.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><img alt=\"Có kỷ luật riêng đối với bản thân (ví dụ: hình phạt riêng...) \" src=\"/local/storage/app/image/images/rule(2).png\" style=\"height:500px; width:700px\" /></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><em><span style=\"font-size:13pt\">H&atilde;y đưa ra một luật ri&ecirc;ng v&agrave; bắt bản th&acirc;n phải tu&acirc;n theo</span></em></p>\r\n\r\n<p style=\"text-align:justify\"><strong><span style=\"font-size:13pt\"><em>4. Một số phương ph&aacute;p hỗ trợ bạn tự học hiệu quả</em></span></strong></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><em><strong>Tự kiểm tra kiến thức:</strong> </em>Bạn h&atilde;y tự hệ thống lại những th&agrave;nh quả m&agrave; bản th&acirc;n đạt được sau mỗi lần tự học. V&igrave; người kh&aacute;c kh&ocirc;ng thể l&uacute;c n&agrave;o cũng k&egrave; k&egrave; để kiểm tra bạn phải kh&ocirc;ng n&agrave;o?! Điều bạn cần l&agrave;m l&agrave; h&atilde;y th&agrave;nh thật với bản th&acirc;n, kh&ocirc;ng gian lận khi tự kiểm tra m&igrave;nh. Th&ocirc;ng qua h&agrave;nh động n&agrave;y, bạn sẽ biết được m&igrave;nh chắc kiến thức chỗ n&agrave;o, nơi n&agrave;o m&igrave;nh bị hổng l&agrave; cần phải trau dồi th&ecirc;m.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><em><strong>Học c&aacute;ch ghi nhớ</strong></em><strong>:</strong> Mỗi người sẽ c&oacute; một c&aacute;ch ghi nhớ kh&aacute;c nhau, c&oacute; người nghe th&igrave; nhớ l&acirc;u hơn đọc v&agrave; viết, hoặc c&oacute; những người xem video sẽ nhớ l&acirc;u hơn, hoặc c&oacute; người được chỉ dạy sẽ nhanh nhớ hơn... D&ugrave; l&agrave; c&aacute;ch n&agrave;o bạn cũng n&ecirc;n thử tất cả để t&igrave;m ra c&aacute;ch nhớ kiến thức ph&ugrave; hợp với bản th&acirc;n, gi&uacute;p bạn nhớ nhanh v&agrave; l&acirc;u phai.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><em><strong>Chọn lọc kiến thức:</strong> </em>Bạn n&ecirc;n giới hạn những v&ugrave;ng kiến thức để thu nạp, v&igrave; kiến thức nh&acirc;n loại l&agrave; v&ocirc; tận, nếu kh&ocirc;ng chọn lọc kiến thức, bạn rất dễ rơi v&agrave;o t&igrave;nh trạng lan man v&igrave; mớ kiến thức hỗn độn. V&igrave; vậy h&atilde;y học c&aacute;ch thu nạp kiến thức c&oacute; chọn lọc nh&eacute;.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><strong><em>Thường xuy&ecirc;n &ocirc;n lại kiến thức cũ:</em></strong> Thường xuy&ecirc;n &ocirc;n lại kiến thức cũ gi&uacute;p bạn cũng cố được kiến thức đ&atilde; học cũng như nhớ lại những kiến thức quan trọng nhưng v&ocirc; t&igrave;nh bị bỏ qu&ecirc;n. Việc thường xuy&ecirc;n &ocirc;n lại kiến thức cũng gi&uacute;p bạn ti&ecirc;t kiệm thời gian v&agrave;o những ng&agrave;y thi cuối k&igrave; hoặc đến những sự kiện quan trọng cần d&ugrave;ng đến những kiến thức n&agrave;y.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">B&ecirc;n cạnh những phương ph&aacute;p n&ecirc;u tr&ecirc;n, m&ocirc;i trường xung quanh khi bạn ngồi học cũng c&oacute; tầm ảnh hưởng đến việc học của bạn. Những nơi c&oacute; kh&ocirc;ng gian đẹp v&agrave; y&ecirc;n tĩnh gi&uacute;p k&iacute;ch th&iacute;ch sự s&aacute;ng tạo cũng như tinh thần tự học của bạn được duy tr&igrave;. Đừng b&oacute; buộc kh&ocirc;ng gian tự học của bạn tại b&agrave;n học chật hẹp ở nh&agrave;, h&atilde;y ra qu&aacute;n cafe hoặc c&aacute;c thư viện c&oacute; kh&ocirc;ng gian tho&aacute;ng v&agrave; đẹp... Zikzak co-working space l&agrave; một gợi &yacute; tuyệt vời gi&agrave;nh cho bạn, với kh&ocirc;ng gian được thiết kế theo hướng trẻ v&agrave; năng động nhằm k&iacute;ch th&iacute;ch sự s&aacute;ng tạo trong bạn, cộng với kh&ocirc;ng gian y&ecirc;n tĩnh, Zikzak co-working space xứng đ&aacute;ng trở th&agrave;nh nơi tự học ưa th&iacute;ch của c&aacute;c bạn! </span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><img alt=\"Không gian rộng rãi, yên tính phù hợp với việc tự học\" src=\"/local/storage/app/image/images/kg.jpg\" style=\"height:500px; width:700px\" /></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><em><span style=\"font-size:13pt\">Kh&ocirc;ng gian rộng r&atilde;i, y&ecirc;n tĩnh</span></em></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><img alt=\"Thiết kế mới lạ kích thích sự sáng tạo\" src=\"/local/storage/app/image/images/WALL.jpg\" style=\"height:500px; width:700px\" /></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><em><span style=\"font-size:13pt\">Thiết kế mới lạ, k&iacute;ch th&iacute;ch sự s&aacute;ng tạo trong bạn</span></em></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Đăng k&iacute; trải nghiệm miễn ph&iacute; ngay xem Zikzak n&oacute;i đ&uacute;ng kh&ocirc;ng nh&eacute;: <a href=\"http://zikzak-co.vn/experience\" style=\"color:#0563c1; text-decoration:underline\">http://zikzak-co.vn/experience</a></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Zikzak co-working space hy vọng b&agrave;i viết n&agrave;y c&oacute; &iacute;ch đến bạn v&agrave; ch&uacute;c c&aacute;c bạn học tốt!</span></p>', 'Zikzak co-working space, Không gian tự học, Cách tự học hiệu quả', '2018-10-26 20:57:22', '2018-11-01 22:17:08'),
(18, '5 cách tiết kiệm chi phí cho Startup khi bắt đầu khởi nghiệp', '5-cach-tiet-kiem-chi-phi-cho-startup-khi-bat-dau-khoi-nghiep', 'Một trong những khó khăn lớn nhất mà các Startup gặp phải khi bắt đầu xây dựng những chiến lược phát triển doanh nghiệp, công ty của mình đó là kinh phí. Để “đứa con” của mình phát triển được tốt, chắc chắn, Startup phải biết đến những cách tiết kiệm chi phí dưới đây.', 'zz_2018-10-29_1540783083.jpeg', '<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><strong>1. Tiếp thị th&ocirc;ng tin qua mạng x&atilde; hội</strong></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Facebook &ndash; Một trong những nền tảng mạng x&atilde; hội đang ph&aacute;t triển cực mạnh ở tr&ecirc;n khắp thế giới. C&ugrave;ng với những trang mạng x&atilde; hội xung quanh kh&aacute;c, đ&acirc;y l&agrave; nơi lưu truyền những th&ocirc;ng tin cực lớn đến với nhiều người d&ugrave;ng.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Ch&iacute;nh v&igrave; sự ph&aacute;t triển n&agrave;y, nhiều nh&agrave; kinh doanh, startup đ&atilde; lựa chọn mạng x&atilde; hội l&agrave; k&ecirc;nh tiếp thị th&ocirc;ng tin chủ yếu của doanh nghiệp, c&ocirc;ng ty m&igrave;nh. Bằng những phương ph&aacute;p thường d&ugrave;ng như: chạy Google Ads, từ kh&oacute;a SEO...</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><img alt=\"Mạng xã hội là kênh truyền thông hiệu quả và tiết kiệm cho các startup\" src=\"/local/storage/app/image/images/facebook.jpg\" style=\"height:480px; width:720px\" /></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:13pt\"><em>Mạng x&atilde; hội l&agrave; k&ecirc;nh truyền th&ocirc;ng hiệu quả v&agrave; tiết kiệm cho c&aacute;c startup</em></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">C&oacute; thể dễ d&agrave;ng nhận thấy, việc tiếp thị th&ocirc;ng tin qua mạng x&atilde; hội sẽ một phần n&agrave;o đ&oacute; l&agrave;m giảm đi kinh ph&iacute; hoạt động, quảng b&aacute; sản phẩm của c&aacute;c startup. V&agrave; quan trọng hơn nữa, qua mạng x&atilde; hội, người d&ugrave;ng sẽ tiếp cận được với sản phẩm, dịch vụ của doanh nghiệp, c&ocirc;ng ty nhiều hơn v&agrave; hiệu quả hơn rất nhiều.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><strong>2. Tập trung v&agrave;o việc lưu giữ kh&aacute;ch h&agrave;ng cũ</strong></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">So s&aacute;nh giữa khoảng chi ph&iacute; đầu tư v&agrave;o việc t&igrave;m kiếm kh&aacute;ch h&agrave;ng mới v&agrave; lưu giữ kh&aacute;ch h&agrave;ng cũ, c&aacute;c startup dễ d&agrave;ng nhận ra việc giữ lại kh&aacute;ch h&agrave;ng cũ sẽ gi&uacute;p cho họ tiết kiệm được chi ph&iacute; ph&aacute;t sinh nhiều hơn.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Sỡ dĩ như vậy l&agrave; bởi, với những kh&aacute;ch h&agrave;ng cũ, khi họ đ&atilde; dần quen thuộc với sản phẩm, dịch vụ của c&aacute;c c&ocirc;ng ty, doanh nghiệp th&igrave; sẽ c&oacute; xu hướng d&ugrave;ng th&ecirc;m nhiều lần nữa. Hầu hết mọi người đều ngại sử dụng những c&aacute;i mới, khi đ&oacute; họ sẽ cần phải c&oacute; thời gian để l&agrave;m quen v&agrave; tiếp nhận những c&aacute;i mới, v&agrave; thời gian sẽ tr&ocirc;i qua l&atilde;ng ph&iacute;.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Tập trung giữ lại kh&aacute;ch h&agrave;ng cũ bằng những ưu đ&atilde;i dịch vụ đặc biệt d&agrave;nh cho họ. V&agrave; cho họ thấy rằng, dịch vụ v&agrave; sản phẩm của doanh nghiệp, c&ocirc;ng ty bạn l&agrave; ph&ugrave; hợp với họ, khuyến kh&iacute;ch họ tiếp tục sử dụng nhiều thời gian hơn nữa.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">V&agrave;, kh&aacute;ch h&agrave;ng cũ l&agrave; những cầu nối v&ocirc; c&ugrave;ng hiệu quả trong việc đưa thị trường dịch vụ, sản phẩm của startup vươn xa hơn, khi họ giới thiệu với những người thuộc mối quan hệ của họ.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><strong>3. Hợp t&aacute;c tiếp thị với một b&ecirc;n thứ ba</strong></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Ban đầu, khi nghĩ đến, điều đầu ti&ecirc;n ch&uacute;ng ta sẽ thấy ngay đ&oacute; l&agrave; tốn th&ecirc;m một số kinh ph&iacute; để hợp t&aacute;c với b&ecirc;n thứ ba. Thế nhưng, nh&igrave;n xa hơn nữa, c&aacute;c startup sẽ nhận thấy được những hiệu quả tuyệt vời của việc hợp t&aacute;c n&agrave;y.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Chẳng hạn như khi hợp t&aacute;c với một k&ecirc;nh ph&aacute;t thanh n&agrave;o đ&oacute;, c&aacute;c MC nhắc nhiều lần cho th&iacute;nh giả của m&igrave;nh nghe về sản phẩm, dịch vụ của c&aacute;c c&ocirc;ng ty, doanh nghiệp th&igrave; hiển nhi&ecirc;n, th&iacute;nh giả th&iacute;ch th&uacute; th&igrave; họ sẽ tự t&igrave;m tiếp với dịch vụ, sản phẩm của startup. Tương tự, th&ocirc;ng qua k&ecirc;nh truyền h&igrave;nh hay c&aacute;c phương tiện truyền th&ocirc;ng kh&aacute;c cũng vậy.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><img alt=\"\" src=\"/local/storage/app/image/images/pano.jpg\" style=\"height:480px; width:720px\" /></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:13pt\"><em>Sử dụng pano để quảng c&aacute;o thương hiệu l&agrave; chiến lược maketing tiết kiệm chi ph&iacute;</em></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Hợp t&aacute;c với b&ecirc;n thứ ba để quảng b&aacute; thương hiệu, dịch vụ v&agrave; sản phẩm đến nhiều người kh&aacute;c th&ocirc;ng qua banner, pano cũng l&agrave; một c&aacute;ch hiệu quả m&agrave; được nhiều doanh nghiệp tin d&ugrave;ng.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><strong>4. Đảm bảo về chất lượng của c&aacute;c thiết bị</strong></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Điều n&agrave;y l&agrave; dễ hiểu, văn ph&ograve;ng l&agrave;m việc l&agrave; nơi c&oacute; rất nhiều những thiết bị để phục vụ cho c&ocirc;ng việc, như b&agrave;n ghế, tủ, m&aacute;y t&iacute;nh, đ&egrave;n, điện, wifi... Với nhiều thiết bị như vậy, c&aacute;c startup phải đảm bảo được chất lượng sử dụng của ch&uacute;ng.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Th&agrave; l&agrave; khi mới bắt đầu, bỏ hẳn ra một số kinh ph&iacute; lớn để sử dụng những thiết bị c&oacute; chất lượng tốt, l&acirc;u bền v&agrave; hiệu quả c&ograve;n hơn d&ugrave;ng những thiết bị rẻ tiền, k&eacute;m chất lượng rồi cứ v&agrave;i th&aacute;ng lại thay, lại sửa chữa đủ thứ.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Những chi ph&iacute; thay mới, thợ sửa nhiều lần như vậy tổng lại sẽ l&ecirc;n con số kh&aacute; lớn. V&igrave; vậy, h&atilde;y tập trung sử dụng những thiết bị tốt ngay từ ban đầu để kh&ocirc;ng phải bỏ ra một khoảng chi ph&iacute; sửa chữa kh&aacute;c kh&ocirc;ng đ&aacute;ng.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Ngo&agrave;i ra, c&aacute;c startup cũng n&ecirc;n dự tr&ugrave; một &iacute;t kinh ph&iacute; cho việc bảo tr&igrave; chất lượng những thiết bị phục vụ cho c&ocirc;ng việc tại văn ph&ograve;ng.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><strong>5. Sử dụng văn phỏng ảo, văn ph&ograve;ng cho thu&ecirc;</strong></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Một vấn đề cũng kh&aacute; lớn trong việc chi ti&ecirc;u kinh ph&iacute; của c&aacute;c doanh nghiệp, c&ocirc;ng ty vừa v&agrave; nhỏ hiện tại. Đ&ocirc;i khi, đ&acirc;y cũng l&agrave; vấn đề của một số c&ocirc;ng ty lớn. Đ&oacute; l&agrave; chi ph&iacute; để sử dụng trong việc chi trả những khoản ph&iacute; li&ecirc;n quan đến mặt bằng x&acirc;y dựng c&ocirc;ng ty, doanh nghiệp hay văn ph&ograve;ng.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Biện ph&aacute;p hiệu quả nhất để khắc phục kh&oacute; khăn n&agrave;y d&agrave;nh cho những startup chưa c&oacute; nhiều kinh ph&iacute;, đ&oacute; l&agrave; sử dụng những dịch vụ cho thu&ecirc; văn ph&ograve;ng ảo hiện nay. Loại h&igrave;nh dịch vụ n&agrave;y đ&atilde; xuất hiện kh&aacute; l&acirc;u tại thị trường Việt Nam, tuy vậy nhưng cũng c&ograve;n kh&aacute; mới mẻ n&ecirc;n nhiều người vẫn chưa biết đến.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Với việc thu&ecirc; văn ph&ograve;ng ảo l&agrave;m văn ph&ograve;ng của doanh nghiệp hay c&ocirc;ng ty, c&aacute;c startup sẽ tiết kiệm được một khoản ph&iacute; kh&aacute; lớn trong việc chi trả những kinh ph&iacute; cho việc n&agrave;y.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><img alt=\"Không gian ZikZak Coworking Space\" src=\"/local/storage/app/image/images/zikzak.jpg\" style=\"height:480px; width:720px\" /></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:13pt\"><em>Kh&ocirc;ng gian ZikZak Coworking Space</em></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><strong>ZikZak Coworking Space</strong> l&agrave; một đơn vị chuy&ecirc;n cung cấp dịch vụ cho thu&ecirc; văn ph&ograve;ng vừa nhắc đến. Với nhiều g&oacute;i d&agrave;nh cho nhiều đối tượng kh&aacute;c nhau, c&aacute;c startup c&oacute; thể linh hoạt chọn cho m&igrave;nh những lựa chọn ph&ugrave; hợp, từ c&aacute; nh&acirc;n, nh&oacute;m người, hay cả tập thể doanh nghiệp, c&ocirc;ng ty th&igrave; ZikZak Coworking Space vẫn đ&aacute;p ứng được nhu cầu.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Truy cập <a href=\"http://zikzak-co.vn\" style=\"color:blue; text-decoration:underline\">http://zikzak-co.vn</a> để biết th&ecirc;m nhiều th&ocirc;ng tin chi tiết. </span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><strong>ZikZak Coworking Space</strong> - Kh&ocirc;ng gian l&agrave;m việc hiệu quả v&agrave; s&aacute;ng tạo.</span></p>', 'zikzak, coworkingspace, zikzakcoworkingspace', '2018-10-28 20:18:04', '2018-10-28 21:39:21'),
(19, 'MÁCH NHỎ NHỮNG MẸO GIÚP BẠN CHỌN ĐƯỢC MỘT CO-WORKING SPACE PHÙ HỢP', 'mach-nho-nhung-meo-giup-ban-chon-duoc-mot-co-working-space-phu-hop', 'Bạn là một cá nhân, bạn có một startup riêng, hoặc bạn đang sở hữu một nhóm freelancer chuyên nghiệp. Bạn cũng đã biết đến mô hình co-working space. Nhưng bạn vẫn chưa có sự lựa chọn tối ưu cho riêng mình?! Zikzak co-working space sẽ mách nhỏ cho bạn những “tips” dưới đây nhằm giúp bạn lựa chọn được một co-working space ưng ý!', 'zz_2018-10-31_1540955586.png', '<p style=\"text-align:justify\"><strong><span style=\"font-size:13pt\">1. Lựa chọn vị tr&iacute; co-working space ph&ugrave; hợp </span></strong></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Giống như những văn ph&ograve;ng của c&aacute;c c&ocirc;ng ty truyền thống, khi chọn một co-working space để l&agrave;m việc, bạn cũng n&ecirc;n ch&uacute; &yacute; đến vị tr&iacute; m&agrave; co-working space tọa lạc. Bạn n&ecirc;n t&igrave;m co-working space đặt gần c&aacute;c trục đường ch&iacute;nh, dễ d&agrave;ng lưu th&ocirc;ng qua c&aacute;c quận hoặc đi v&agrave;o trung t&acirc;m th&agrave;nh phố. Ngo&agrave;i ra bạn cần lưu &yacute; tr&aacute;nh c&aacute;c đoạn đường thường xuy&ecirc;n ngập lụt, kh&ocirc;ng ai muốn m&igrave;nh đến nơi l&agrave;m việc m&agrave; l&uacute;c n&agrave;o người cũng &ldquo;tắm&rdquo; nước mưa lẫn nước cống đ&uacute;ng kh&ocirc;ng n&agrave;o?! </span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">B&ecirc;n cạnh đ&oacute; việc chọn lựa vị tr&iacute; co-working space ph&ugrave; hợp cũng tạo điều kiện thuận lợi nếu bạn muốn &ldquo;chi&ecirc;u mộ&rdquo; th&ecirc;m nh&acirc;n t&agrave;i v&agrave;o nh&oacute;m. V&igrave; khi một co-working space c&oacute; vị tr&iacute; đắc địa, ắc hẳn những nh&oacute;m start-up hoặc freelancer kh&aacute;c cũng chọn. V&agrave; tại đ&acirc;y cơ hội kết nối của bạn l&agrave; kh&ocirc;ng giới hạn!</span></p>\r\n\r\n<p style=\"text-align:justify\"><strong><span style=\"font-size:13pt\">2. H&atilde;y &ldquo;stress&rdquo; về kh&ocirc;ng gian tại co-working space</span></strong></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Kh&ocirc;ng gian l&agrave;m việc c&oacute; vai tr&ograve; quan trọng t&aacute;c động đến t&acirc;m l&yacute; l&agrave;m việc của bạn. Khi l&agrave;m việc, việc vận dụng tr&iacute; &oacute;c nhiều khiến ch&uacute;ng ta mệt mỏi. V&agrave; đ&ograve;i hỏi một kh&ocirc;ng gian l&agrave;m việc c&oacute; thể thư gi&atilde;n v&agrave; k&iacute;ch th&iacute;ch &oacute;c s&aacute;ng tạo nhằm nạp th&ecirc;m năng lượng để ho&agrave;n th&agrave;nh c&ocirc;ng việc. </span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:13pt\"><img alt=\"\" src=\"/local/storage/app/image/images/5.jpg\" style=\"height:480px; width:720px\" /></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:13pt\">Kh&ocirc;ng gian gi&uacute;p thư gi&atilde;n đầu &oacute;c, nạp năng lượng để tiếp tục l&agrave;m việc&nbsp;</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Mỗi kh&ocirc;ng gian của co-working space đều mang một đặc trưng ri&ecirc;ng. Điều bạn cần l&agrave;m l&agrave; h&atilde;y chọn cho m&igrave;nh một kh&ocirc;ng gian cảm thấy thoải m&aacute;i v&agrave; ph&ugrave; hợp với c&aacute; t&iacute;nh của bản th&acirc;n, đồng thời h&atilde;y đảm bảo rằng ở nơi đ&oacute; &oacute;c s&aacute;ng tạo trong bạn được khơi nguồn ngo&agrave;i việc bạn c&oacute; thể thư gi&atilde;n sau những ph&uacute;t gi&acirc;y l&agrave;m việc mệt mỏi.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Co-working space tại Zikzak l&agrave; &yacute; tưởng kh&ocirc;ng tồi đối với những ai muốn t&igrave;m một m&ocirc;i trường c&oacute; thiết kế theo lối năng động, trẻ trung, k&iacute;ch th&iacute;ch được tinh thần l&agrave;m việc của bản th&acirc;n lu&ocirc;n ở mức cao!</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Ngo&agrave;i ra, tại c&aacute;c co-working space, bạn đừng bỏ qua cơ hội giao lưu kết nối với c&aacute;c startup, freelancer kh&aacute;c. Đ&acirc;y l&agrave; nơi tuyệt vời để bạn c&oacute; thể chia sẻ, ph&aacute;t triển &yacute; tưởng cũng như t&igrave;m được cho m&igrave;nh những đồng đội ph&ugrave; hợp!</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:13pt\"><img alt=\"\" src=\"/local/storage/app/image/images/8.jpg\" style=\"height:480px; width:720px\" /></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:13pt\">Kh&ocirc;ng gian được thiết kế theo lối trẻ, năng động gi&uacute;p bạn c&oacute; thể chia sẻ, kết nối c&ugrave;ng c&aacute;c startup/freelancer kh&aacute;c.</span></p>\r\n\r\n<p style=\"text-align:justify\"><strong><span style=\"font-size:13pt\">3. H&atilde;y ch&uacute; &yacute; đến trang thiết bị tại co-working space</span></strong></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Nếu c&oacute; cơ hội, h&atilde;y đi &ldquo;ngh&iacute;a&rdquo; qua co-working space trước khi quyết định thu&ecirc; bạn nh&eacute;! </span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Điều bạn cần lưu &yacute; trong chuyến đi n&agrave;y đầu ti&ecirc;n l&agrave; về trang thiết bị được trang bị tại đ&acirc;y. H&atilde;y xin pass wifi v&agrave; check thử bằng điện thoại; Bạn h&atilde;y y&ecirc;u cầu bật điều h&ograve;a xem c&ograve;n hoạt động ổn kh&ocirc;ng, c&oacute; đủ độ lạnh v&agrave; g&acirc;y tiếng ồn qu&aacute; nhiều hay kh&ocirc;ng. V&igrave; bạn biết đấy, tiếng ồn c&oacute; thể g&acirc;y t&aacute;c động xấu đến năng suất l&agrave;m việc của bạn; Tiếp theo co-working space cần đảm bảo đủ độ s&aacute;ng cho bạn l&agrave;m việc. Bạn sẽ rất kh&oacute; chịu nếu b&oacute;ng đ&egrave;n cứ li&ecirc;n tục chớp nh&aacute;y phải kh&ocirc;ng n&agrave;o?! Chưa kể đến việc thị lực của bạn c&oacute; thể bị ảnh hưởng v&igrave; l&agrave;m việc trong một m&ocirc;i trường kh&ocirc;ng cung cấp đủ độ s&aacute;ng. </span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:13pt\"><img alt=\"\" src=\"/local/storage/app/image/images/16.jpg\" style=\"height:480px; width:720px\" /></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:13pt\">Co-working space được&nbsp;trang bị đầy đủ trang thiết bị</span>.</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">B&ecirc;n cạnh đ&oacute;, t&ograve;a nh&agrave; chứa co-working space cần c&oacute; một hệ thống cung cấp điện ổn định. Việc mất điện sẽ dẫn đến rối loạn trong hệ thống th&ocirc;ng kh&iacute; hoặc ngưng trệ qu&aacute; tr&igrave;nh truyền tải dữ liệu m&aacute;y t&iacute;nh - g&acirc;y mất dữ liệu v&agrave; tốn nhiều thời gian kh&ocirc;i phục. Hệ thống m&aacute;y t&iacute;nh c&agrave;ng lớn, thời gian kh&ocirc;i phục c&agrave;ng l&acirc;u sau khi chịu ảnh hưởng từ rối loạn của hệ thống điện, l&agrave;m giảm năng suất l&agrave;m việc v&agrave; tăng chi ph&iacute; sửa chữa.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:13pt\"><img alt=\"\" src=\"/local/storage/app/image/images/19.jpg\" style=\"height:480px; width:720px\" /></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:13pt\">Kh&ocirc;ng gian mở cung cấp &aacute;nh s&aacute;ng tự nhi&ecirc;n, ngo&agrave;i &aacute;nh s&aacute;ng từ đ&egrave;n điện được trang bị sẵn tại&nbsp;Zikzak co-working space.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Ngo&agrave;i ra bạn cần xem x&eacute;t c&aacute;c trang thiết bị kh&aacute;c trong ph&ograve;ng như b&agrave;n l&agrave;m việc, ghế, tủ, m&aacute;y vi t&iacute;nh&hellip; H&atilde;y đảm bảo rằng ch&uacute;ng c&ograve;n hoạt động tốt v&agrave; trong thời gian bảo h&agrave;nh. V&igrave; Zikzak c&aacute; l&agrave; bạn kh&ocirc;ng muốn mất th&ecirc;m chi ph&iacute; để sửa chữa những trang thiết bị n&agrave;y trong khi đ&atilde; bỏ ra một khoảng chi ph&iacute; để thu&ecirc; một co-working space trước đ&oacute; đ&uacute;ng kh&ocirc;ng?</span></p>\r\n\r\n<p style=\"text-align:justify\"><strong><span style=\"font-size:13pt\">4. H&atilde;y t&igrave;m hiểu về an ninh v&agrave; chất lượng quản l&iacute; tại co-working space m&agrave; bạn muốn thu&ecirc;</span></strong></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">B&ecirc;n cạnh một co-working space được trang bị tốt, bạn cũng cần một co-working space đảm bảo được an ninh để c&oacute; thể y&ecirc;n t&acirc;m ho&agrave;n th&agrave;nh tốt c&ocirc;ng việc. H&atilde;y d&ograve; hỏi những người xung quanh khu vực bạn đang muốn thu&ecirc; để biết được t&igrave;nh h&igrave;nh an ninh như thế n&agrave;o, v&agrave; h&atilde;y cố gắng hỏi c&agrave;ng nhiều người c&agrave;ng tốt để thu thập th&ecirc;m nhiều th&ocirc;ng tin v&agrave; tăng t&iacute;nh x&aacute;c thực. Bước n&agrave;y rất quan trọng v&igrave; kh&ocirc;ng ai muốn vừa l&agrave;m việc, vừa phải nơm nớp lo sợ v&igrave; m&igrave;nh đang nguy hiểm về t&agrave;i sản hoặc t&iacute;nh mạng đ&uacute;ng kh&ocirc;ng n&agrave;o? </span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Ngo&agrave;i ra, chất lượng quản l&iacute; cũng đ&oacute;ng một vai tr&ograve; quan trọng gi&uacute;p bạn chọn được một co-working space ph&ugrave; hợp. T&ograve;a nh&agrave; nơi co-working space tọa lạc c&oacute; hệ thống quản l&iacute; tốt sẽ đảm bảo mọi hoạt động của t&ograve;a nh&agrave; diễn ra với chất lượng tốt nhất v&agrave; an to&agrave;n nhất từ đảm bảo an ninh trật tự đến dịch vụ dọn vệ sinh, quản l&iacute; chăm s&oacute;c kh&aacute;ch h&agrave;ng, đối nội đối ngoại&hellip;Khi mọi thứ diễn ra trơn tru, bạn chỉ việc tập trung để ho&agrave;n th&agrave;nh tốt c&ocirc;ng việc của m&igrave;nh!</span></p>\r\n\r\n<p style=\"text-align:justify\"><strong><span style=\"font-size:13pt\">5. H&atilde;y so s&aacute;nh&nbsp;về gi&aacute; cả</span></strong></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Gi&aacute; cả l&agrave; yếu tố kh&ocirc;ng k&eacute;m phần quan trọng gi&uacute;p bạn đưa ra quyết định chọn một co-working space ph&ugrave; hợp. Dĩ nhi&ecirc;n, khi bạn biết đến loại h&igrave;nh co-working space, bạn chắc chắn biết đ&acirc;y l&agrave; loại h&igrave;nh gi&uacute;p start up non trẻ của bạn tiết kiệm chi ph&iacute; một c&aacute;ch tối đa v&agrave; hiệu quả nhất. Nhưng c&oacute; qu&aacute; nhiều đơn vị cung cấp co-working space mọc l&ecirc;n v&agrave; cung cấp cho bạn qu&aacute; nhiều mức gi&aacute; kh&aacute;c nhau. Kết quả l&agrave; bạn rơi v&agrave;o trạng th&aacute;i lan man kh&ocirc;ng biết n&ecirc;n chọn co-working space n&agrave;o cho&nbsp;ph&ugrave; hợp!&nbsp;</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Ở trường hợp n&agrave;y, Zikzak co-working space l&agrave; gợi &yacute; kh&ocirc;ng thể tuyệt vời hơn gi&agrave;nh cho bạn. V&igrave; Zikzak cung cấp đa dạng c&aacute;c g&oacute;i dịch vụ nhằm đ&aacute;p ứng mọi nhu cầu bạn đặt ra. Với mức gi&aacute; kh&ocirc;ng thể hợp l&iacute; hơn: chỉ từ 79k/ng&agrave;y, bạn đ&atilde; t&igrave;m được cho m&igrave;nh một co-working space ph&ugrave; hợp với tất cả c&aacute;c ti&ecirc;u ch&iacute; m&agrave; bạn đặt ra trước đ&oacute;. Ngo&agrave;i ra, nếu bạn muốn thu&ecirc; văn ph&ograve;ng nhưng đang &ldquo;vất vả&rdquo; với c&aacute;c thủ tục th&agrave;nh lập doanh nghiệp như giấy ph&eacute;p kinh doanh, con dấu, hồ sơ k&ecirc; khai thuế ban đầu&hellip; Zikzak sẽ hỗ trợ bạn giải quyết vấn đề hiệu quả với một mức gi&aacute; ph&ugrave; hợp! </span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Nếu c&ograve;n ph&acirc;n v&acirc;n,&nbsp;<a href=\"http://zikzak-co.vn/experience\" style=\"color:#0563c1; text-decoration:underline\">đăng k&iacute; ngay</a> để nhận cơ hội trải nghiệm miễn ph&iacute; ngay nh&eacute;!!</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Zikzak hy vọng những mẹo nhỏ tr&ecirc;n đ&acirc;y sẽ gi&uacute;p &iacute;ch cho bạn trong qu&aacute; tr&igrave;nh t&igrave;m chọn một co-working space ph&ugrave; hợp! </span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Tr&acirc;n trọng!</span></p>', 'co-working space, tips, mẹo tìm coworking space, tìm coworking space ưng ý, coworking giá rẻ, zikzak co-working space', '2018-10-30 20:13:06', '2018-11-15 01:46:58'),
(20, 'Những điều cần thiết để có một cuộc họp startup hiệu quả', 'nhung-dieu-can-thiet-de-co-mot-cuoc-hop-startup-hieu-qua', 'Các cuộc họp xưa nay vốn dĩ luôn là “nỗi ám ảnh” và được cho rằng nó ngốn hết khá nhiều thời gian của mọi người tại văn phòng. Thế nhưng thực sự không phải vậy. Một cuộc họp được tổ chức đúng và hiệu quả sẽ không bao giờ ngốn hết thời gian của bạn đến như vậy.', 'zz_2018-11-02_1541124628.png', '<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Để c&oacute; thể đạt được những hiệu quả nhất định trong qu&aacute; tr&igrave;nh diễn ra cuộc họp, người điều khiển cuộc họp, hay c&aacute;c startup cần phải nắm r&otilde; một số điều quan trọng dưới đ&acirc;y.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><strong>1. Đặt ra mục ti&ecirc;u cuộc họp r&otilde; r&agrave;ng</strong></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Thực sự rất kh&oacute; nếu như c&aacute;c startup tổ chức một cuộc họp th&agrave;nh c&ocirc;ng nhưng lại kh&ocirc;ng biết rằng những điều quan trọng n&agrave;o cần được nhắc đến, cứ đi lan man hết chuyện n&agrave;y đến chuyện kh&aacute;c, đ&ocirc;i khi chỉ l&agrave; những chuyện t&aacute;n gẫu b&acirc;ng quơ.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Vậy n&ecirc;n, trước khi bắt đầu tổ chức một cuộc họp, người thực hiện cần phải đặt ra những mục ti&ecirc;u cụ thể cần được nhắc đến trong cuộc họp. H&atilde;y sắp xếp những mục ti&ecirc;u n&agrave;y theo thứ tự ưu ti&ecirc;n lớn nhất đến nhỏ nhất.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Hơn thế nữa, trong cuộc họp, c&aacute;c startup, người thực hiện cuộc họp cần phải điều khiển sự tập trung của mọi người v&agrave;o mục ti&ecirc;u đang được nhắc đến hoặc cần giải quyết. Kh&ocirc;ng n&ecirc;n thường xuy&ecirc;n nhắc đến những chuyện ngo&agrave;i lề kh&aacute;c.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:13pt\"><img alt=\"\" src=\"/local/storage/app/image/images/goal.png\" style=\"height:480px; width:720px\" /></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:13pt\"><em>C&oacute; mục ti&ecirc;u r&otilde; r&agrave;ng, cuộc họp n&agrave;o cũng sẽ th&agrave;nh c&ocirc;ng</em></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><strong>2. Gửi trước chủ đề cho người tham dự</strong></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Một người đi họp, nhưng lại kh&ocirc;ng biết m&igrave;nh sẽ họp về c&aacute;i g&igrave;, việc g&igrave; th&igrave; chắc chắn t&acirc;m l&yacute; của họ sẽ hoang mang theo. Điều n&agrave;y k&eacute;o theo việc tập trung của họ trong cuộc họp cũng kh&ocirc;ng được hiệu quả, v&igrave; đ&ocirc;i l&uacute;c họ phải suy nghĩ về vấn đề m&agrave; người thực hiện cuộc họp vừa n&oacute;i ra.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Kh&ocirc;ng c&oacute; sự chuẩn bị n&agrave;o l&agrave; thừa. Trước khi bắt đầu cuộc họp, h&atilde;y gửi đến những người c&oacute; mặt trong cuộc họp những th&ocirc;ng tin quan trọng v&agrave; cần thiết nhất để họ chuẩn bị trước, hoặc &iacute;t nhất l&agrave; họ biết cuộc họp sẽ diễn biến như thế n&agrave;o với những nội dung g&igrave;.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><strong>3. Mời đ&uacute;ng đối tượng</strong></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">L&agrave; người chủ tr&igrave; cuộc họp, h&atilde;y mời đ&uacute;ng những người cần cho cuộc họp. Chỉ c&oacute; những người li&ecirc;n quan đến những nội dung của c&aacute;c mục ti&ecirc;u đặt ra mới tập trung tiếp thu v&agrave; đ&oacute;ng g&oacute;p cho buổi họp th&agrave;nh c&ocirc;ng được.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">C&ograve;n những người kh&ocirc;ng li&ecirc;n quan đến c&ocirc;ng việc, đến mục ti&ecirc;u của cuộc họp th&igrave; chắc chắn v&agrave;o ph&ograve;ng họp chỉ l&agrave;m ph&iacute; thời gian của họ m&agrave; th&ocirc;i. Đ&ocirc;i khi họ c&ograve;n ngơ ng&aacute;c đến dự buổi họp rồi kh&ocirc;ng hiểu họ c&oacute; mặt ở đ&oacute; để l&agrave;m g&igrave;, chuy&ecirc;n m&ocirc;n của họ đ&oacute;ng g&oacute;p được g&igrave; cho mục ti&ecirc;u của cuộc họp.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Đ&acirc;y cũng l&agrave; nguy&ecirc;n nh&acirc;n dẫn đến một số người sẽ mất tập trung khi buổi họp đang diễn ra, v&igrave; nội dung đang b&agrave;n đến kh&ocirc;ng li&ecirc;n quan g&igrave; đến họ.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><strong>4. Bắt đầu v&agrave; kết th&uacute;c đ&uacute;ng giờ</strong></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Kh&ocirc;ng chỉ l&agrave; những người tham dự cuộc họp, ngay cả những người tổ chức cuộc họp cũng n&ecirc;n r&egrave;n luyện th&oacute;i quen qu&yacute; trọng giờ giấc v&agrave; biết đặt ra giới hạn. Đừng bao giờ bắt đầu muộn cuộc họp v&igrave; bất kỳ một l&yacute; do g&igrave;, kể cả việc chờ người tham dự. Đ&oacute;ng cửa v&agrave; bắt đầu cuộc họp để những người đi trễ cảm thấy &aacute;i ngại v&igrave; h&agrave;nh động n&agrave;y. V&agrave; đ&acirc;y cũng l&agrave; c&aacute;ch giữ kh&ocirc;ng kh&iacute; bắt đầu cuộc họp khi mọi người tại đ&acirc;y đ&atilde; ở tư thế sẵn s&agrave;ng.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:13pt\"><img alt=\"\" src=\"/local/storage/app/image/images/ontime.png\" style=\"height:480px; width:720px\" /></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:17.3333px\"><em>Đ&uacute;ng giờ bắt đầu v&agrave; kết th&uacute;c l&agrave; điều m&agrave; hầu hết tất cả mọi người đều mong muốn</em></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Bắt đầu cuộc họp đ&uacute;ng giờ quan trọng th&igrave; việc kết th&uacute;c cuộc họp đ&uacute;ng giờ cũng quan trọng kh&ocirc;ng k&eacute;m. Startup, người tổ chức cuộc họp cần phải chia khoảng thời gian sao cho ph&ugrave; hợp với từng nội dung v&agrave; mục ti&ecirc;u đề ra. H&atilde;y cố gắng giải quyết ch&uacute;ng nhanh nhất c&oacute; thể, để thời gian c&ograve;n lại mọi người c&oacute; thể b&agrave;n bạc với nhau v&agrave; đưa ra những hướng giải quyết.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Theo chia sẻ của c&aacute;c chuy&ecirc;n gia, thời gian mặc định của những cuộc họp tối đa kh&ocirc;ng qu&aacute; 60 ph&uacute;t. Nhưng đ&oacute; chưa phải l&agrave; điều quan trọng nhất. Điều quan trọng ở đ&acirc;y đ&oacute; l&agrave;, h&atilde;y tổ chức cuộc họp g&oacute;i gọn trong 30 &ndash; 45 ph&uacute;t đầu ti&ecirc;n. 15 ph&uacute;t c&ograve;n lại để d&agrave;nh cho thời gian thảo luận, hoặc nghỉ ngơi sau cuộc họp.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><strong>5. B&aacute;m s&aacute;t chủ đề trong cuộc họp</strong></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Trong những cuộc họp với số lượng người đ&ocirc;ng, th&igrave; việc &ldquo;b&agrave;n ra&rdquo; l&agrave; kh&ocirc;ng thể n&agrave;o tr&aacute;nh khỏi. Trong v&agrave;i trường hợp thảo luận qu&aacute; s&ocirc;i nổi hoặc do người tham gia đang c&oacute; nhiều mối bận t&acirc;m, cuộc họp sẽ bị chệch mục ti&ecirc;u với v&ocirc; v&agrave;ng &yacute; kiến, phản biện v&agrave; đề xuất đi xa khỏi vấn đề đặt ra ban đầu.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">L&uacute;c n&agrave;y, ai tr&ograve; của người thực hiện cuộc họp l&agrave; v&ocirc; c&ugrave;ng quan trọng. H&atilde;y cố gắng kh&eacute;o l&eacute;o cắt bớt những luồng &yacute; kiến, những quan điểm lạc đề, v&agrave; nhắc lại mục đ&iacute;ch ch&iacute;nh của buổi họp. Đ&ocirc;i khi bạn cần phải đề nghị mọi người nghi&ecirc;m t&uacute;c để cuộc họp c&oacute; thể &ldquo;đi đến nơi, về đến chốn&rdquo;.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:13pt\"><img alt=\"\" src=\"/local/storage/app/image/images/cu%E1%BB%91i.jpg\" style=\"height:480px; width:720px\" /></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:13pt\"><em>B&aacute;m s&aacute;t chủ đề v&agrave; những vấn đề cần b&agrave;n luận&nbsp;để cuộc họp kh&ocirc;ng đi lệch hướng</em></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><strong>6. Tổng kết ngắn gọn trước khi kết th&uacute;c cuộc họp</strong></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Trước khi kết th&uacute;c cuộc họp, h&atilde;y tổng kết hết tất cả những nội dung vừa được đưa ra để thảo luận, v&agrave; những hướng đi, c&aacute;ch giải quyết đ&atilde; th&ocirc;ng qua. Điều n&agrave;y sẽ gi&uacute;p cho những người tham gia cuộc họp nắm được những g&igrave; vừa diễn ra.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Một cuộc họp kết th&uacute;c m&agrave; kh&ocirc;ng r&uacute;t ra được những kết quả v&agrave; tổng kết th&igrave; đ&oacute; ch&iacute;nh x&aacute;c l&agrave; một cuộc họp v&ocirc; nghĩa, l&agrave;m uổng ph&iacute; thời gian của nhiều người tham gia v&agrave; cả của người tổ chức cuộc họp.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:right\"><span style=\"font-size:13pt\"><strong>ZikZak Co-working Space</strong> - Kh&ocirc;ng gian l&agrave;m việc hiệu quả v&agrave; s&aacute;ng tạo</span></p>', 'cuộc họp, startup, hiệu quả, zikzak, coworkingspace', '2018-11-01 19:10:28', '2018-11-13 19:37:19');
INSERT INTO `news` (`news_id`, `news_name`, `news_slug`, `news_summary`, `news_image`, `news_content`, `tag`, `created_at`, `updated_at`) VALUES
(21, '4 ĐIỀU START-UP NÊN BỎ NẾU MUỐN THÀNH CÔNG', '4-dieu-start-up-nen-bo-neu-muon-thanh-cong', 'Thành công đến với một người không chỉ là những kinh nghiệm, nỗ lực và học hỏi, mà còn phụ thuộc một phần vào những thái độ, nhân cách, hay đơn giản cũng chỉ là những điều nhỏ nhoi cần phải loại bỏ ngay lập tức. Hãy cùng Zikzak tìm hiểu những điều mà start-up nên bỏ nếu muốn thành công thông qua bài viết dưới đây nhé!', 'zz_2018-11-06_1541473850.png', '<p style=\"text-align:justify\"><span style=\"font-size:13pt\"><strong>1. N&ecirc;n &ldquo;vứt&nbsp;ngay&rdquo; <em>ch&iacute; kh&ocirc;ng bền</em></strong></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Một trong những yếu tố quan trọng dẫn tới th&agrave;nh c&ocirc;ng l&agrave; sự ki&ecirc;n tr&igrave;, thứ m&agrave; hiếm người l&agrave;m được. Ki&ecirc;n tr&igrave; c&oacute; thể được h&igrave;nh th&agrave;nh qua qu&aacute; tr&igrave;nh trau dồi v&agrave; r&egrave;n luyện. Thế nhưng kh&ocirc;ng phải ai cũng th&agrave;nh c&ocirc;ng trong qu&aacute; tr&igrave;nh n&agrave;y, v&agrave; điều hiển nhi&ecirc;n rằng trong cuộc sống c&oacute; kh&ocirc;ng &iacute;t những người th&agrave;nh c&ocirc;ng nhờ ki&ecirc;n tr&igrave;, nhưng cũng c&oacute; nhiều số kh&aacute;c lại thất bại v&igrave; bỏ cuộc v&agrave;o ph&uacute;t ch&oacute;t.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">C&oacute; một c&acirc;u đố rằng: &ldquo;Một khu vườn nọ c&oacute; diện t&iacute;ch 50m<sup>2</sup>, biết rằng ng&agrave;y h&ocirc;m sau số lượng củ cải trong vườn sẽ gấp đ&ocirc;i ng&agrave;y h&ocirc;m trước. V&iacute; dụ ng&agrave;y thứ nhất lượng củ cải l&agrave; 1m<sup>2</sup>, sang ng&agrave;y thứ 2 sẽ gấp đ&ocirc;i ng&agrave;y thứ nhất tức l&agrave; 2m<sup>2</sup>. Vậy giả sử đến ng&agrave;y thứ 10 số củ cải sẽ phủ k&iacute;n hết khu vườn, hỏi v&agrave;o ng&agrave;y thứ bao nhi&ecirc;u th&igrave; số củ cải sẽ đạt được nửa diện t&iacute;ch khu vườn?&rdquo;</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Một số người sẽ trả lời ngay rằng l&agrave; ng&agrave;y thứ 5, nhưng c&acirc;u trả lời lại l&agrave; ng&agrave;y thứ 9 v&igrave; &ldquo;số lượng củ cải trong vườn sẽ gấp đ&ocirc;i ng&agrave;y h&ocirc;m trước&rdquo;. Th&ocirc;ng qua c&acirc;u đố n&agrave;y c&agrave;ng khẳng định được tầm quan trọng của việc ki&ecirc;n tr&igrave; c&ugrave;ng một &yacute; ch&iacute; bền bỉ theo đuổi mục ti&ecirc;u. V&igrave; ắc hẳn nếu bạn đ&atilde; đinh ninh đến ng&agrave;y thứ 5 số củ cải sẽ được nửa vườn nhưng kết quả lại kh&ocirc;ng như m&igrave;nh mong muốn v&agrave; bỏ cuộc ngay l&uacute;c n&agrave;y th&igrave; mục ti&ecirc;u một khu vườn đầy củ cải v&agrave;o ng&agrave;y thứ 10 sẽ kh&ocirc;ng bao giờ đến với bạn. H&atilde;y nhớ rằng trước khi đ&oacute;n ch&agrave;o th&agrave;nh c&ocirc;ng th&igrave; bạn cần phải nỗ lực đến tận ph&uacute;t ch&oacute;t, v&iacute; như phải ki&ecirc;n tr&igrave; đến ng&agrave;y thứ 9 như c&acirc;u đố tr&ecirc;n mới nhận được khu vườn đầy củ cải &ndash; l&agrave; th&agrave;nh quả xứng đ&aacute;ng d&agrave;nh cho bạn v&agrave;o ng&agrave;y thứ 10.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Những người kh&ocirc;ng c&oacute; t&iacute;nh ki&ecirc;n tr&igrave; nhẫn nại, &yacute; ch&iacute; kh&ocirc;ng bền bỉ thường rất kh&oacute; th&agrave;nh c&ocirc;ng trong cuộc sống. Khi bắt tay v&agrave;o l&agrave;m một việc g&igrave; đ&oacute;, v&igrave; &yacute; ch&iacute; kh&ocirc;ng vững n&ecirc;n khi gặp một ch&uacute;t kh&oacute; khăn sẽ nhụt ch&iacute; v&agrave; kh&ocirc;ng bước tiếp nữa, khi đ&oacute; th&agrave;nh c&ocirc;ng đối với người n&agrave;y chỉ l&agrave; ảo ảnh, chỉ c&oacute; thể nh&igrave;n từ xa m&agrave; kh&ocirc;ng bao giờ chạm tay đến được. Nếu đang l&agrave; một người <em>kh&ocirc;ng bền ch&iacute;</em>, bạn h&atilde;y tập c&aacute;c th&oacute;i quen để r&egrave;n luyện đức t&iacute;nh ki&ecirc;n tr&igrave; nhẫn nại, v&agrave; h&atilde;y &ldquo;vứt&nbsp;bỏ&rdquo; ch&iacute; kh&ocirc;ng bền v&igrave; h&atilde;y nhớ cho rằng th&agrave;nh quả l&agrave; khu vườn đầy củ cải v&agrave;o ng&agrave;y thứ 10 chỉ đến chừng n&agrave;o bạn ki&ecirc;n tr&igrave; đến được ng&agrave;y thứ 9. </span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:13pt\"><img alt=\"\" src=\"/local/storage/app/image/images/H%C3%ACnh-2.png\" style=\"height:480px; width:720px\" /></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:13pt\">Cần bỏ ngay <em>ch&iacute; kh&ocirc;ng bền</em></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:13pt\"><strong>2. Bỏ ngay chỉ n&oacute;i m&agrave; kh&ocirc;ng l&agrave;m</strong></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">&ldquo;S&ocirc;ng c&agrave;ng s&acirc;u c&agrave;ng tĩnh lặng. L&uacute;a c&agrave;ng ch&iacute;n c&agrave;ng cuối đầu&rdquo;. &nbsp;Quả thật người giỏi thực sự lu&ocirc;n khi&ecirc;m tốn, kẻ thiếu năng lực thường ba hoa. Đừng trở th&agrave;nh chiếc &ldquo;th&ugrave;ng rỗng k&ecirc;u to&rdquo;, nếu thực sự c&oacute; năng lực, bạn h&atilde;y chứng minh n&oacute; bằng h&agrave;nh động, đừng khua m&ocirc;i m&uacute;a m&eacute;p v&agrave; trở th&agrave;nh tr&ograve; hề trong mắt người kh&aacute;c! </span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Nhiều người c&oacute; khả năng ch&eacute;m gi&oacute; về bản th&acirc;n rất si&ecirc;u đẳng, thậm ch&iacute; khiến người kh&aacute;c tin s&aacute;i cổ những điều họ n&oacute;i l&agrave; sự thật. Dĩ nhi&ecirc;n, kh&ocirc;ng thể phủ nhận đ&acirc;y l&agrave; một &ldquo;khả năng&rdquo; kh&ocirc;ng phải ai cũng c&oacute; m&agrave; phải được r&egrave;n luyện rất nhiều. Chuyện sẽ kh&ocirc;ng c&oacute; g&igrave; nếu như bạn l&agrave; một người c&oacute; năng lực thực sự, nhưng sẽ rất buồn cười nếu như bạn chỉ l&agrave; kẻ &ldquo;chỉ biết n&oacute;i m&agrave; kh&ocirc;ng l&agrave;m&rdquo;. Điều g&igrave; sẽ xảy ra nếu l&uacute;c bạn thuyết tr&igrave;nh rất ấn tượng, b&agrave;i n&oacute;i rất hay nhưng khi bắt tay v&agrave;o l&agrave;m th&igrave; bạn lại kh&ocirc;ng l&agrave;m tốt được bất cứ việc g&igrave;?! Cho d&ugrave; việc đ&oacute; l&agrave; việc v&ocirc; c&ugrave;ng đơn giản v&agrave; theo như những g&igrave; bạn khẳng định trước đ&oacute; rằng bản th&acirc;n l&agrave; chuy&ecirc;n gia trong lĩnh vực n&agrave;y?! L&uacute;c đ&oacute; bạn ước g&igrave; m&igrave;nh chưa từng ba hoa qu&aacute; đ&agrave; như vậy th&igrave; sẽ kh&ocirc;ng th&agrave;nh tr&ograve; cười trong mắt mọi người v&agrave; chưa kể uy t&iacute;n c&aacute; nh&acirc;n bị giảm s&uacute;t nghi&ecirc;m trọng chỉ v&igrave; bệnh &ldquo;nổ&rdquo;.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">H&atilde;y trở th&agrave;nh người &quot;lời n&oacute;i đi đ&ocirc;i c&ugrave;ng h&agrave;nh động&quot; bạn nh&eacute;!</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:13pt\"><img alt=\"\" src=\"/local/storage/app/image/images/H%C3%ACnh-3.png\" style=\"height:480px; width:720px\" /></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:13pt\">H&atilde;y lu&ocirc;n để h&agrave;nh động song h&agrave;nh c&ugrave;ng lời n&oacute;i</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><strong>3. </strong><strong>Tự nh&igrave;n nhận được năng lực của bản th&acirc;n</strong></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Một c&ocirc;ng việc, một khởi đầu tốt m&agrave; c&aacute;c startup muốn đạt được th&igrave; việc tự nh&igrave;n nhận được năng lực của bản th&acirc;n cũng l&agrave; một điều đ&aacute;ng ch&uacute; t&acirc;m v&agrave; cực kỳ quan trọng. &ldquo;Ảo tưởng sức mạnh&rdquo;, n&oacute; ch&iacute;nh l&agrave; nguy&ecirc;n nh&acirc;n dẫn đến thất bại của kh&aacute; nhiều người.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Đ&uacute;ng l&agrave; khi khởi nghiệp, ch&uacute;ng ta cần mơ ước đến những mục ti&ecirc;u lớn lao, hơn người. Thế nhưng, c&aacute;c startup thử nghĩ xem: Việc bạn đưa ra qu&aacute; nhiều những dự &aacute;n lớn v&agrave; hầu hết đều quan trọng nhưng kh&ocirc;ng thực hiện được v&igrave; năng lực của bạn c&oacute; giới hạn th&igrave; phải l&agrave;m như thế n&agrave;o? H&atilde;y đ&aacute;nh gi&aacute; đ&uacute;ng bản th&acirc;n khi bắt đầu những dự &aacute;n, những kế hoạch nhất định. Đ&ocirc;i khi, ch&iacute;nh bạn nghĩ m&igrave;nh c&oacute; thể l&agrave;m được, nhưng đến l&uacute;c thực hiện th&igrave; thực sự gặp phải nhiều kh&oacute; khăn rất lớn khiến bạn phải bỏ cuộc, v&agrave; rồi bạn thất bại.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:13pt\"><img alt=\"\" src=\"/local/storage/app/image/images/H%C3%ACnh-4.png\" style=\"height:480px; width:720px\" /></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:13pt\">Ngưng ảo tưởng sức mạnh của bản th&acirc;n v&agrave; tập đ&aacute;nh gi&aacute; đ&uacute;ng năng lực của m&igrave;nh</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Bạn l&agrave; startup đứng ra tạo n&ecirc;n kế hoạch ph&aacute;t triền, bạn l&agrave; người cốt l&otilde;i của dự &aacute;n, nhưng điều đ&oacute; kh&ocirc;ng quan trọng. Quan trọng nhất l&agrave; bạn nh&igrave;n thấy được năng lực thực sự của m&igrave;nh c&ograve;n thiếu s&oacute;t ở những điểm n&agrave;o. Từ những thiếu s&oacute;t đ&oacute;, bạn h&atilde;y học hỏi những người xung quanh, những người c&ugrave;ng thực hiện dự &aacute;n với bạn. Bạn đứng đầu, nhưng chưa chắc bạn giỏi nhất. H&atilde;y cố gắng trao đổi v&agrave; học hỏi qua lại những c&aacute;c đồng nghiệp của bạn. C&oacute; như vậy, startup mới th&agrave;nh c&ocirc;ng được.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><strong>4. </strong><strong>Sĩ diện</strong></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Quan trọng hơn cả những điều đ&atilde; n&oacute;i ở tr&ecirc;n, startup một khi đ&atilde; mang &ldquo;căn bệnh Sĩ&rdquo; n&agrave;y trong người th&igrave; chắc chắn sẽ kh&oacute; c&oacute; thể m&agrave; th&agrave;nh c&ocirc;ng được. Muốn th&agrave;nh c&ocirc;ng, h&atilde;y tập gạt bỏ những sĩ diện sang b&ecirc;n cạnh.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">C&oacute; nhiều người, khi bắt đầu kế hoạch ph&aacute;t triển th&igrave; gặp phải những t&igrave;nh huống thiếu vốn, hay &iacute;t nh&agrave; đầu tư nhưng lại v&igrave; sĩ diện m&agrave; kh&ocirc;ng đứng ra hỏi vay hay gặp gỡ những nh&agrave; đầu tư kh&aacute;c để cầu vốn. Bản th&acirc;n startup th&igrave; cứ tự nhủ sẽ vượt qua được, nhưng chỉ sau một thời gian, c&aacute;c kế hoạch đ&atilde; chết yểu một c&aacute;ch uổng ph&iacute;, chỉ v&igrave; sĩ diện.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Cũng v&igrave; sĩ diện m&agrave; kh&ocirc;ng chấp nhận sự thật, kh&ocirc;ng c&ocirc;ng nhận năng lượng của đối thủ, v&agrave; kết quả của điều n&agrave;y l&agrave; những sự thất bại ngay trước mắt.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:13pt\"><img alt=\"\" src=\"/local/storage/app/image/images/H%C3%ACnh-5.png\" style=\"height:480px; width:720px\" /></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:13pt\">Quả thật, bệnh SĨ rất hay hiện DIỆN</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Khi gặp một ai đ&oacute; ở lứa tuổi m&igrave;nh đang th&agrave;nh c&ocirc;ng rực rỡ tr&ecirc;n c&ugrave;ng một thương trường m&agrave; bạn đang chinh chiến, bạn c&oacute; bĩu m&ocirc;i v&agrave; n&oacute;i: &ldquo;&Ocirc;i, mấy đứa c&oacute; người chống lưng, &ocirc;ng to, b&agrave; lớn đ&oacute; m&agrave; n&oacute;i l&agrave;m g&igrave;?!&rdquo; Ch&iacute;nh sĩ diện bản th&acirc;n đ&atilde; đ&aacute;nh chết sự th&agrave;nh c&ocirc;ng của bạn. Startup h&atilde;y tập chấp nhận sự thật rằng họ l&agrave; người th&agrave;nh c&ocirc;ng, v&agrave; h&atilde;y t&igrave;m hiểu xem những người họ th&agrave;nh c&ocirc;ng bằng c&aacute;ch n&agrave;o để được học hỏi theo. Nhưng đến việc học hỏi theo đối thủ m&agrave; cũng vướn phải sĩ diện th&igrave; thất bại nằm ngay trước mắt thật rồi.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Bạn c&oacute; nghĩ rằng, một người đứng đầu th&igrave; kh&ocirc;ng cần thiết phải l&agrave;m những chuyện động tay ch&acirc;n, đơn giản!? Bạn c&oacute; nghĩ rằng, l&agrave; người đứng đầu th&igrave; n&ecirc;n thực hiện những điều to lớn hơn thế nữa!? Nếu c&oacute;, bạn đ&atilde; sai lầm, v&igrave; ch&iacute;nh sĩ diện của bạn!</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">H&atilde;y bỏ qua những sĩ diện c&aacute; nh&acirc;n v&ocirc; bổ đ&oacute; đi! Một người th&agrave;nh c&ocirc;ng rực rỡ kh&ocirc;ng bao giờ ngại l&agrave;m hết những chuyện vặt v&atilde;nh xung quanh m&igrave;nh. Cho d&ugrave; l&agrave; c&oacute; người thực hiện những c&ocirc;ng việc đ&oacute;, nhưng bạn cũng n&ecirc;n thực hiện những c&ocirc;ng việc đơn giản nhất xung quanh m&igrave;nh, đừng đứng đ&oacute; v&agrave; đợi người kh&aacute;c đến l&agrave;m thay, c&ograve;n bạn chỉ việc đứng v&agrave; ra hiệu lệnh.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:right\"><em><span style=\"font-family:Times New Roman,serif\"><span style=\"font-size:17.3333px\">Theo Zikzak co-working space.</span></span></em></p>', 'startup nên bỏ nếu muốn thành công, zikzak, start-up, chí không bền, chỉ nói không làm, sĩ diện, tự nhìn nhận năng lực của bản thân', '2018-11-05 20:10:49', '2018-11-13 19:31:51'),
(22, 'KHI GẶP KHÓ KHĂN, ĐỪNG VỘI NẢN LÒNG', 'khi-gap-kho-khan-dung-voi-nan-long', 'Trong cuộc sống hối hả ngày nay, bất cứ ai trong chúng ta đều có những khó khăn riêng phải đối mặt. Có bao giờ bạn cảm thấy bản thân đã cố gắng rất nhiều, nhưng vẫn không đạt được điều mình mong muốn chưa? Cố gắng thật nhiều, nhưng khó khăn vẫn cứ bủa vây chờ chực để quật ta ngã quỵ. Khi gặp khó khăn, bạn sẽ xử lý như thế nào?! Bỏ chạy hay đương đầu với nó?!', 'zz_2018-11-09_1541735117.jpeg', '<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Chẳng c&oacute; con đường n&agrave;o l&agrave; đường trải hoa hồng cả! C&oacute; đ&ocirc;i khi bạn gặp phải kh&oacute; khăn c&ugrave;ng cực, nhưng đ&oacute; sẽ biến th&agrave;nh cơ hội nếu bạn nh&igrave;n nhận vấn đề theo một c&aacute;ch kh&aacute;ch quan v&agrave; theo hướng t&iacute;ch cực. Việc bạn cần l&agrave;m l&agrave; ĐỪNG VỘI TỪ BỎ, h&atilde;y tiếp tục theo đuổi ước mơ của m&igrave;nh v&agrave; lấy đ&oacute; l&agrave;m động lực để bước tiếp. Chỉ cần bạn ki&ecirc;n tr&igrave;, kh&oacute; khăn sẽ kh&ocirc;ng c&ograve;n l&agrave; trở ngại đối với bạn nữa v&igrave; giờ đ&acirc;y bạn chỉ nh&igrave;n thấy duy nhất một mục ti&ecirc;u của m&igrave;nh m&agrave; th&ocirc;i.&nbsp;</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:13pt\"><img alt=\"\" src=\"/local/storage/app/image/images/Order_37_FIX-01.png\" style=\"height:480px; width:720px\" /></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-family:Times New Roman,serif\"><span style=\"font-size:17.3333px\"><em>Đừng vội từ bỏ, h&atilde;y tiếp tục bước tiếp v&igrave; th&agrave;nh c&ocirc;ng đang đợi bạn ph&iacute;a trước</em></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Điểm chung của những người th&agrave;nh c&ocirc;ng đ&oacute; l&agrave; sự nỗ lực v&agrave; ki&ecirc;n tr&igrave;, họ kh&ocirc;ng bao giờ từ bỏ ước mơ của m&igrave;nh, họ h&agrave;nh động li&ecirc;n tục để đạt được ước mơ. Khi gặp kh&oacute; khăn, họ vẫn tiếp tục tiến l&ecirc;n chứ kh&ocirc;ng để m&igrave;nh mắc kẹt trong hỗn độn, v&agrave; cứ như thế th&agrave;nh c&ocirc;ng n&agrave;y nối tiếp th&agrave;nh c&ocirc;ng kh&aacute;c đến với họ. Giống như những người th&agrave;nh c&ocirc;ng, h&atilde;y h&agrave;nh động li&ecirc;n tục, v&agrave; vượt qua kh&oacute; khăn một c&aacute;ch bền bỉ, đừng vội nản l&ograve;ng v&igrave; hoa thơm tr&aacute;i ngọt của sự th&agrave;nh c&ocirc;ng đang đợi bạn ở ph&iacute;a trước.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Khi gặp kh&oacute; khăn, h&atilde;y nhẫn nại!</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Khi kh&oacute; khăn ập đến, h&atilde;y b&igrave;nh tĩnh đối mặt với n&oacute;. Vấn đề sẽ kh&ocirc;ng được giải quyết v&agrave; c&agrave;ng rối rắm hơn nếu bạn kh&ocirc;ng giữ được b&igrave;nh tĩnh để giải quyết vấn đề. Cố gắng giữ th&aacute;i độ b&igrave;nh tĩnh đối mặt với vấn đề xảy đến, đừng cuốn qu&yacute;t v&igrave; l&uacute;c n&agrave;y kh&ocirc;ng giải quyết được điều g&igrave;, ngược lại c&ograve;n l&agrave;m mọi thứ tệ hơn. </span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:13pt\"><img alt=\"\" src=\"/local/storage/app/image/images/Order_37_FIX-02.png\" style=\"height:480px; width:720px\" /></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:13pt\"><em>H&atilde;y ki&ecirc;n nhẫn t&igrave;m ra hướng giải quyết kh&oacute; khăn</em></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Việc bạn cần l&agrave;m l&agrave; h&atilde;y giữ th&aacute;i độ b&igrave;nh tĩnh, ki&ecirc;n nhẫn t&igrave;m ra hướng giải quyết kh&oacute; khăn v&agrave; h&atilde;y lu&ocirc;n nh&igrave;n mọi thứ theo hướng t&iacute;ch cực, điều n&agrave;y gi&uacute;p bạn c&oacute; thể giải quyết vấn đề v&agrave; kh&ocirc;ng bị mắc kẹt trong trạng th&aacute;i hoang mang dễ dẫn đến thất bại trước kh&oacute; khăn.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">H&atilde;y cứ theo đuổi đam m&ecirc;, th&agrave;nh c&ocirc;ng sẽ theo đuổi bạn! </span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:13pt\"><img alt=\"\" src=\"/local/storage/app/image/images/Order_37_FIX-03.png\" style=\"height:480px; width:720px\" /></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:13pt\"><em>Keep chasing your dreams, success&nbsp;will follow you!</em></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">H&atilde;y để đam m&ecirc; dẫn lối, trong trường hợp thất bại, ta chẳng c&oacute; g&igrave; hối tiếc. Điều n&agrave;y tưởng như l&agrave; ch&acirc;n l&yacute; của tuổi trẻ. Bởi, một khi kh&ocirc;ng c&oacute; g&igrave; chắc chắn, th&igrave; h&atilde;y chọn thứ kh&ocirc;ng chắc chắn đ&aacute;ng gi&aacute; nhất.&nbsp;Tr&ecirc;n đường theo đuổi ước mơ của ri&ecirc;ng m&igrave;nh, chắc chắn sẽ c&oacute; kh&oacute; khăn. Nhưng đừng để kh&oacute; khăn l&agrave;m biến mất đi ước mơ của bạn, h&atilde;y lu&ocirc;n nh&igrave;n về điều m&agrave; bạn muốn l&agrave;m, người m&agrave; bạn muốn trở th&agrave;nh, ch&iacute;nh niềm đam m&ecirc; sẽ dẫn dắt bạn đến th&agrave;nh c&ocirc;ng.&nbsp;</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:right\"><span style=\"font-size:13pt\"><em>Theo Zikzak co-working space.</em></span></p>', 'khi gặp khó khăn đừng nản lòng, đừng nản chí khi gặp khó khăn, tiếp tục theo đuổi ước mơ, đừng từ bỏ, bình tĩnh giải quyết vấn đề', '2018-11-08 20:45:17', '2018-11-08 21:13:00'),
(23, 'Những giá trị thật mà văn phòng ảo mang lại', 'nhung-gia-tri-that-ma-van-phong-ao-mang-lai', 'Dịch vụ văn phòng ảo hiện nay đã phát triển khá mạnh mẽ trên thị trường. Từ khi chỉ là những dịch vụ mới và nhỏ lẻ, đến nay đã có hàng loạt những dịch vụ cho thuê văn phòng ảo với quy mô lớn và uy tín.', 'zz_2018-11-12_1541996247.png', '<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Sự thật cho thấy, tuy l&agrave; văn ph&ograve;ng ảo, nhưng gi&aacute; trị mang lại từ loại h&igrave;nh dịch vụ n&agrave;y lại ho&agrave;n to&agrave;n thật.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><strong>Văn ph&ograve;ng ảo l&agrave; g&igrave;?</strong></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Văn ph&ograve;ng ảo l&agrave; m&ocirc; h&igrave;nh văn ph&ograve;ng cho thu&ecirc; địa chỉ kinh doanh m&agrave; kh&ocirc;ng cần đến diện t&iacute;ch thực tế. Đến với dịch vụ cho thu&ecirc; văn ph&ograve;ng ảo n&agrave;y, kh&aacute;ch h&agrave;ng sẽ c&oacute; được tất cả những dịch vụ tiện &iacute;ch cần thiết, bao gồm: Địa chỉ giao dịch của doanh nghiệp, số điện thoại cho doanh nghiệp, số fax, nh&acirc;n vi&ecirc;n lễ t&acirc;n, biển hiệu c&ocirc;ng ty, b&aacute;o c&aacute;o thuế... như một doanh nghiệp b&igrave;nh thường.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Đối với những người khi mới bắt đầu lập nghiệp, đặc biệt l&agrave; những startup trẻ, dịch vụ văn ph&ograve;ng ảo n&agrave;y sẽ gi&uacute;p &iacute;ch được cho việc tiết kiệm kinh ph&iacute; kinh doanh v&agrave; vận h&agrave;nh.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:13pt\"><img alt=\"Văn phòng ảo là mô hình đang rất được ưa chuộng hiện nay\" src=\"/local/storage/app/image/images/6.jpg\" style=\"height:459px; width:720px\" /></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><em><span style=\"font-size:13pt\">Văn ph&ograve;ng ảo l&agrave; m&ocirc; h&igrave;nh đang rất được ưa chuộng hiện nay</span></em></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><strong>Gi&aacute; trị thật từ văn ph&ograve;ng ảo</strong></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><strong>Tăng th&ecirc;m niềm tin v&agrave;o sự th&agrave;nh c&ocirc;ng của c&aacute;c startup</strong></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Một điểm chung dễ nhận thấy của c&aacute;c doanh nghiệp, c&aacute;c startup khi vừa lập nghiệp đ&oacute; l&agrave; thiếu sự tự tin để vượt qua những kh&oacute; khăn. Chắc chắn, khi mới bắt đầu bước v&agrave;o những kế hoạch, những dự &aacute;n th&igrave; c&aacute;c chủ doanh nghiệp, startup lu&ocirc;n h&agrave;o hứng thực hiện. Nhưng đến khi gặp phải những kh&oacute; khăn về địa chỉ giao dịch uy t&iacute;n dễ t&igrave;m v&agrave; chuy&ecirc;n nghiệp th&igrave; lại nản l&ograve;ng, v&igrave; thực tế chưa đ&aacute;p ứng được điều n&agrave;y.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Ch&iacute;nh v&igrave; vậy, nhờ c&oacute; đến dịch vụ văn ph&ograve;ng ảo m&agrave; c&aacute;c chủ doanh nghiệp mới th&agrave;nh lập, c&aacute;c startup hay những nh&agrave; khởi nghiệp chưa c&oacute; nhiều vốn sẽ c&oacute; được nhiều sự tự tin hơn trong những dự &aacute;n, những kế hoạch ph&aacute;t triển. Bởi, c&oacute; được một nơi giao dịch cố định v&agrave; chuy&ecirc;n nghiệp th&igrave; tỷ lệ th&agrave;nh c&ocirc;ng sẽ kh&aacute; cao.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:13pt\"><img alt=\"believe\" src=\"/local/storage/app/image/images/4.png\" style=\"height:480px; width:720px\" /></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><strong>Khẳng định được h&igrave;nh ảnh doanh nghiệp</strong></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Hơn thế nữa, điều n&agrave;y sẽ gi&uacute;p cho bạn g&acirc;y được ấn tượng đối với những đối t&aacute;c của m&igrave;nh trong việc gặp gỡ đối t&aacute;c, hay c&aacute;c cuộc họp chẳng hạn. H&igrave;nh ảnh doanh nghiệp của bạn sẽ chuy&ecirc;n nghiệp hơn với dịch vụ văn ph&ograve;ng ảo c&oacute; được địa chỉ đẹp v&agrave; c&aacute;c dịch vụ tiện &iacute;ch thuận lợi.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><strong>Gia tăng vốn đầu tư kinh doanh</strong></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Thực tế cho thấy, với những kinh ph&iacute; đầu tư để c&oacute; được một văn ph&ograve;ng thật khi mới bắt đầu sẽ kh&aacute; kh&oacute; khăn đối với những người vừa mới bắt đầu. Bởi kinh ph&iacute; để chi trả dịch vụ văn ph&ograve;ng kh&ocirc;ng hề nhẹ một t&iacute; n&agrave;o. Đ&acirc;y l&agrave; lu&ocirc;n nỗi &aacute;m ảnh, nỗi lo toan của nhiều doanh nghiệp, startup, kể cả họ l&agrave; một c&ocirc;ng ty, một doanh nghiệp ph&aacute;t triển đ&atilde; l&acirc;u. V&agrave; c&agrave;ng kh&oacute; khăn hơn, khi muốn c&oacute; được một địa chỉ đẹp để thuận lợi cho c&ocirc;ng việc.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Thay v&igrave; d&ugrave;ng tất cả những kinh ph&iacute; bỏ ra ngay từ đầu để thu&ecirc; văn ph&ograve;ng thật v&ocirc; c&ugrave;ng tốn k&eacute;m th&igrave; tại sao c&aacute;c startup, c&aacute;c chủ doanh nghiệp vừa v&agrave; nhỏ lại kh&ocirc;ng tập trung đầu tư v&agrave;o những dự &aacute;n, những kế hoạch trọng t&acirc;m kh&aacute;c, v&agrave; chỉ d&ugrave;ng dịch vụ cho thu&ecirc; văn ph&ograve;ng ảo th&ocirc;i!?</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Đ&acirc;y cũng chỉ l&agrave; c&aacute;ch để c&oacute; thể giải quyết được những kh&oacute; khăn về t&agrave;i ch&iacute;nh m&agrave; c&aacute;c startup, c&aacute;c chủ doanh nghiệp gặp phải khi mới bắt đầu. Về sau, về d&agrave;i, khi đ&atilde; ph&aacute;t triển mạnh mẽ v&agrave; vững chắc, th&igrave; l&uacute;c n&agrave;y nghĩ đến một nơi cố định để l&agrave;m văn ph&ograve;ng cho c&ocirc;ng ty, cho doanh nghiệp cũng chưa muộn.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><strong>Dịch vụ cho thu&ecirc; văn ph&ograve;ng ảo tại ZikZak Co-working Space</strong></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Tại Th&agrave;nh phố Hồ Ch&iacute; Minh, đơn vị ZikZak hiện đang l&agrave; nơi cung cấp những dịch vụ li&ecirc;n quan đến văn ph&ograve;ng ảo d&agrave;nh cho c&aacute;c startup v&agrave; doanh nghiệp muốn sử dụng dịch vụ.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:13pt\"><img alt=\"\" src=\"/local/storage/app/image/images/Order_31-01.jpg\" style=\"height:360px; width:360px\" /><img alt=\"\" src=\"/local/storage/app/image/images/Order_31-02.jpg\" style=\"height:360px; width:360px\" /></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Để biết th&ecirc;m những chi tiết về dịch vụ cho thu&ecirc; văn ph&ograve;ng ảo n&agrave;y tại ZikZak, c&aacute;c startup, c&aacute;c chủ doanh nghiệp vui l&ograve;ng li&ecirc;n hệ theo những c&aacute;ch sau:</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">- <strong>Hotline</strong>: 092 238 30 97</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">- <strong>Địa chỉ</strong>:</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">+ <strong>ZIKZAK1</strong>: Lầu 1-3-6, t&ograve;a nh&agrave; CPHONE, 456 X&ocirc; Viết Nghệ Tĩnh, phường 25, Quận B&igrave;nh Thạnh, Hồ Ch&iacute; Minh</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">+ <strong>ZIKZAK2</strong>: Lầu 29, t&ograve;a nh&agrave; Mường Thanh, 63 Trần Ph&uacute;, th&agrave;nh phố Nha Trang, tỉnh Kh&aacute;nh H&ograve;a.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:right\"><span style=\"font-size:13pt\"><strong>ZikZak Co-working Space</strong> &ndash; Kh&ocirc;ng gian l&agrave;m việc hiệu quả v&agrave; s&aacute;ng tạo</span></p>', 'startup, zikzak, coworkingspace', '2018-11-11 21:17:27', '2018-11-12 20:50:54'),
(24, 'KỶ LUẬT ĐỂ THÀNH CÔNG, BUÔNG THẢ ĐỂ THẤT BẠI', 'ky-luat-de-thanh-cong-buong-tha-de-that-bai', 'Người thành công sống kỷ luật. Kẻ thất bại thường buông thả!', 'zz_2018-11-15_1542269128.jpeg', '<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Đ&atilde; bao lần bạn tự nhủ sẽ dậy sớm hơn sau v&ocirc; số lần dậy muộn?! Đ&atilde; bao lần bạn quyết t&acirc;m thay đổi, kh&iacute; thế cao ng&uacute;t trời như ngọn đồi...hiu hiu gi&oacute; nhưng chỉ duy tr&igrave; được trong thời gian ngắn, sau đ&oacute; lại đ&acirc;u v&agrave;o đấy. V&igrave; bạn chưa đủ kỷ luật, bạn đang sống theo cảm x&uacute;c v&agrave; bạn sẽ kh&ocirc;ng bao giờ th&agrave;nh c&ocirc;ng nếu cứ sống một cuộc sống bu&ocirc;ng thả. Bạn muốn th&agrave;nh c&ocirc;ng, việc ti&ecirc;n quyết cần l&agrave;m l&agrave; kỷ luật bản th&acirc;n.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Người &ocirc;m ch&iacute; lớn thường rất kỷ luật. &ldquo;Kỷ luật ch&iacute;nh l&agrave; tự do&rdquo; &ndash; c&oacute; thể bạn nghĩ kỷ luật thật rập khu&ocirc;n v&agrave; l&agrave;m con người ta mất tự do. Nhưng thực tế lại kh&aacute;c, về l&acirc;u về d&agrave;i, người v&ocirc; kỷ luật sẽ thiếu đi sự tự do m&agrave; bản th&acirc;n c&oacute; được v&igrave; t&iacute;nh bừa b&atilde;i.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:13pt\"><img alt=\"\" src=\"/local/storage/app/image/images/e32dc394ca372a697326.jpg\" style=\"height:480px; width:720px\" /></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:13pt\">Dần d&agrave; người v&ocirc; kỷ luật sẽ mất tự do, thay v&agrave;o đ&oacute; sẽ bị tr&oacute;i buộc bởi v&ocirc; tuyến truyền h&igrave;nh, bởi những giấc ngủ nướng....</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Đa số c&aacute;c bạn trẻ hiện nay thường nghĩ rằng cuộc sống ngắn ngủi lắm n&ecirc;n cứ v&ocirc; tư tận hưởng niềm vui trước mắt. Cứ chơi cho đ&atilde;, cố gắng kỷ luật để l&agrave;m g&igrave;?! Tự l&agrave;m kh&oacute; bản th&acirc;n để l&agrave;m g&igrave;? Mặc cho những lời khuy&ecirc;n ngăn từ c&aacute;c thế hệ đi trước rằng phải cố gắng l&uacute;c trẻ sau n&agrave;y mới an nh&agrave;n m&agrave; tận hưởng c&aacute;i hạnh ph&uacute;c, th&uacute; vui của cuộc đời. Nhưng dường như những lời khuy&ecirc;n răn ấy chỉ c&oacute; t&aacute;c động tới số &iacute;t người, số c&ograve;n lại vẫn giữ lối sống bu&ocirc;ng thả đến khi nhận ra được th&igrave; người kh&aacute;c đ&atilde; chạm đến đ&iacute;ch của sự th&agrave;nh c&ocirc;ng m&igrave;nh mới bắt đầu chạy. Nhưng c&ograve;n đ&acirc;u sức trẻ v&agrave; thời gian để m&agrave; thực hiện c&aacute;i mong ước từ l&acirc;u c&ograve;n bỏ dở chỉ v&igrave; khi xưa thỏa m&atilde;n v&agrave;i c&aacute;i th&uacute; vui tầm thường, tự kiếm l&yacute; do để bao biện cho việc kh&ocirc;ng theo được kỷ luật của ch&iacute;nh bản th&acirc;n m&igrave;nh.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Chứng kiến nhiều bạn b&egrave; người quen trở n&ecirc;n th&agrave;nh c&ocirc;ng, hoặc biết đến người th&agrave;nh c&ocirc;ng c&oacute; tiếng, bạn chỉ trầm trồ khen ngợi m&agrave; qu&ecirc;n mất ph&iacute;a sau &aacute;nh h&agrave;o quang đ&oacute; họ d&agrave;nh bao nhi&ecirc;u l&agrave; sự nỗ lực, cố gắng kỷ luật bản th&acirc;n mỗi ng&agrave;y.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:13pt\"><img alt=\"\" src=\"/local/storage/app/image/images/4407a7a5ae064e581717.jpg\" style=\"height:480px; width:720px\" /></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:13pt\">Chứng kiến th&agrave;nh c&ocirc;ng của người kh&aacute;c, bạn chỉ trầm trồ khen ngợi m&agrave; qu&ecirc;n mất những nỗ lực đằng sau.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Kẻ&nbsp;thất bại vui vẻ với những th&uacute; vui hời hợt tầm thường, trong khi những người th&agrave;nh c&ocirc;ng họ kỷ luật bản th&acirc;n mỗi ng&agrave;y bởi họ biết r&otilde; bản th&acirc;n muốn g&igrave;. Người th&agrave;nh c&ocirc;ng, họ đọc s&aacute;ch mỗi ng&agrave;y, họ dậy sớm mỗi ng&agrave;y... họ buộc m&igrave;nh phải lặp đi lặp lại những th&oacute;i quen tốt mỗi ng&agrave;y. V&agrave; theo một c&aacute;ch tự nhi&ecirc;n, th&agrave;nh c&ocirc;ng n&agrave;y nối tiếp th&agrave;nh c&ocirc;ng kh&aacute;c đến với họ.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Ngược lại, đối những kẻ thất bại, kỷ luật chỉ l&agrave; khẩu hiệu vui miệng tuy&ecirc;n bố, h&ocirc; h&agrave;o trong những gi&acirc;y ph&uacute;t c&oacute; tinh thần cao tột độ, rồi v&agrave;i ng&agrave;y sau đ&oacute; lại trở về như cũ. Họ viện ra đủ thứ l&yacute; do để b&agrave;o chữa cho t&iacute;nh v&ocirc; kỷ luật của bản th&acirc;n. Họ nằm d&agrave;i ở nh&agrave; xem phim thay v&igrave; phải đọc một cuốn s&aacute;ch hay, họ ngủ dậy trễ hơn mọi ng&agrave;y v&igrave; l&yacute; do h&ocirc;m nay thời tiết lạnh hơn n&ecirc;n kh&ocirc;ng muốn dậy tập thể dục,... v&agrave; cứ thế ng&agrave;y ng&agrave;y họ bị đầu độc bởi những th&oacute;i quen xấu &ndash; thứ khiến con người ta thất bại. V&agrave; cũng theo một c&aacute;ch tự nhi&ecirc;n, th&agrave;nh c&ocirc;ng kh&ocirc;ng bao giờ đến với họ.</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:13pt\"><img alt=\"\" src=\"/local/storage/app/image/images/cecdbd7cb4df54810dce.jpg\" style=\"height:480px; width:720px\" /></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-family:Times New Roman,serif\"><span style=\"font-size:17.3333px\">Kỷ luật để th&agrave;nh c&ocirc;ng, bu&ocirc;ng thả để thất bại!</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\">Sự ki&ecirc;n tr&igrave; l&agrave; kỷ luật tuyệt vời, người b&igrave;nh thường trở n&ecirc;n người xuất ch&uacute;ng ch&iacute;nh nhờ sự ki&ecirc;n tr&igrave; l&agrave;m một việc trong thời gian d&agrave;i, v&agrave; người ta gọi đ&oacute; l&agrave; kỷ luật. C&agrave;ng chăm chỉ, c&agrave;ng nỗ lực, c&agrave;ng kỷ luật, c&agrave;ng xuất sắc. V&igrave; vậy, muốn trở th&agrave;nh người th&agrave;nh c&ocirc;ng, h&atilde;y bắt đầu kỷ luật ngay từ h&ocirc;m nay. Đừng để ước mơ bị b&oacute;p nghẹt bởi chăm ấm nệm &ecirc;m mỗi s&aacute;ng m&agrave; kh&ocirc;ng dậy sớm tập thể dục r&egrave;n luyện sức khỏe, đừng để ch&iacute; lớn chết yểu v&igrave; &ldquo;th&ocirc;i để mai rồi t&iacute;nh, h&ocirc;m nay cứ chơi đ&atilde;&rdquo;... H&atilde;y nhớ rằng kỷ luật để th&agrave;nh c&ocirc;ng, bu&ocirc;ng thả để thất bại. Bạn muốn th&agrave;nh c&ocirc;ng hay thất bại, tất cả đều t&ugrave;y thuộc v&agrave;o t&iacute;nh kỷ luật của bạn. Ch&uacute;c c&aacute;c bạn th&agrave;nh c&ocirc;ng!</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:right\"><span style=\"font-size:13pt\"><em>Theo Zikzak co-working space</em></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\">&nbsp;</p>', 'kỷ luật để thành công, buông thả để thất bại, người thành công thường kỷ luật, người thất bại sống buông thả', '2018-11-15 01:05:27', '2018-11-15 01:07:47'),
(25, 'Vì sao nên chọn ZikZak là nơi tổ chức sự kiện, hội thảo?', 'vi-sao-nen-chon-zikzak-la-noi-to-chuc-su-kien-hoi-thao', 'Bạn đang có một sự kiện hay một hội thảo cần tổ chức nhưng không có địa điểm nào để thực hiện vì phải đáp ứng được nhiều yếu tố khác nhau như không gian hay trang thiết bị? Đừng lo, hãy đến với ZikZak. Nơi đây sẽ mang lại cho bạn một buổi sự kiện, một buổi hội thảo thành công và chuyên nghiệp nhất.', 'zz_2018-11-20_1542710690.png', '<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Vậy, v&igrave; sao bạn n&ecirc;n chọn ZikZak l&agrave; nơi tổ chức sự kiện, hội thảo? H&atilde;y c&ugrave;ng xem những l&yacute; do dưới đ&acirc;y nh&eacute;!</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong>1. Đầy đủ tiện nghi v&agrave; dịch vụ chung tiện lợi</strong></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Đến với ZikZak Co-working Space, bạn kh&ocirc;ng cần phải lo đến việc gửi xe ở đ&acirc;u, c&oacute; dịch vụ lễ t&acirc;n hay kh&ocirc;ng, dịch vụ an ninh v&agrave; vệ sinh như thế n&agrave;o... V&igrave; tại ZikZak, những dịch vụ tiện nghi v&agrave; dịch vụ chung như vậy đ&atilde; c&oacute; sẵn hết tất cả. Bạn chỉ cần đến tổ chức sự kiện v&agrave; hội thảo, c&ograve;n lại tất cả về c&aacute;c việc &ldquo;nhỏ nhoi&rdquo; b&ecirc;n cạnh, ZikZak sẽ lo hết cho bạn!</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Kh&ocirc;ng chỉ l&agrave; những dịch vụ chung như thế, những dịch vụ về văn ph&ograve;ng phẩm cũng c&oacute; thể đ&aacute;p ứng được đầy đủ những y&ecirc;u cầu của bạn khi đến với ZikZak. Chẳng hạn như bạn cần phải photo một v&agrave;i mẫu t&agrave;i liệu n&agrave;o đ&oacute;, th&igrave; ZikZak đ&atilde; c&oacute; m&aacute;y photocopy. Hoặc bạn muốn in t&agrave;i liệu vừa kiếm được tr&ecirc;n internet để phục vụ cho buổi sự kiện, cuộc hội thảo, th&igrave; ZikZak đ&atilde; c&oacute; m&aacute;y in.</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><img alt=\"\" src=\"/local/storage/app/image/images/Order_80_Fix-01(1).png\" style=\"height:480px; width:720px\" /></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-family:Times New Roman, serif\"><span style=\"font-size:17.3333px\"><em>Đến với ZikZak, bạn kh&ocirc;ng cần phải lo đến bất cứ điều g&igrave; kh&aacute;c</em></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm\"><span style=\"font-family:&quot;Times New Roman&quot;,serif; font-size:13pt; text-align:justify\">Tất cả đều c&oacute; sẵn ở đ&acirc;y, bạn chỉ cần đến v&agrave; sử dụng!</span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong>2. Kh&ocirc;ng gian tho&aacute;ng m&aacute;t, thoải m&aacute;i v&agrave; hiện đại</strong></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Những buổi sự kiện hay những buổi hội thảo với số lượng kh&aacute; nhiều người th&igrave; cần phải c&oacute; kh&ocirc;ng gian rộng r&atilde;i v&agrave; tho&aacute;ng để mọi người c&oacute; thể thoải m&aacute;i sinh hoạt v&agrave; l&agrave;m việc. Đừng lo lắng, ZikZak lu&ocirc;n đ&aacute;p ứng được nhu cầu của mọi người.</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><img alt=\"\" src=\"/local/storage/app/image/images/Order_80_Fix.png\" style=\"height:480px; width:720px\" /></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><em>Kh&ocirc;ng gian tại ZikZak sẽ tạo điều kiện cho hội thảo, sự kiện đạt được th&agrave;nh c&ocirc;ng</em></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Với kh&ocirc;ng gian rộng v&agrave; tho&aacute;ng c&ugrave;ng những trang thiết bị hiện đại hỗ trợ trong cho việc tổ chức sự kiện, hội thảo của bạn, v&agrave; c&ugrave;ng với sự chuy&ecirc;n nghiệp của m&igrave;nh, ZikZak tin chắc rằng, bạn sẽ đạt được nhiều điều ngo&agrave;i sức mong đợi ngay từ ban đầu.</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong>3. L&agrave; một cộng đồng khởi nghiệp</strong></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">B&ecirc;n cạnh việc bạn được tổ chức sự kiện, hội thảo tại ZikZak, bạn c&ograve;n c&oacute; thể l&agrave;m quen được với nhiều người thuộc cộng đồng khởi nghiệp đầy trẻ trung v&agrave; s&aacute;ng tạo. C&ugrave;ng l&agrave;m việc với họ, c&ugrave;ng học tập v&agrave; trao đổi những &yacute; tưởng, những kinh nghiệm từ họ, t&iacute;ch g&oacute;p lại tất cả, bạn sẽ nhận được rất nhiều điều vượt xa ra tầm nh&igrave;n hạn hẹp của ch&iacute;nh bản th&acirc;n m&igrave;nh.</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Kh&ocirc;ng chỉ vậy, ZikZak c&ograve;n c&oacute; kh&aacute; nhiều những kh&aacute;ch h&agrave;ng đ&atilde; từng l&agrave; người chọn nơi đ&acirc;y l&agrave;m nơi tổ chức c&aacute;c buổi sự kiện, hội thảo của m&igrave;nh. Nếu như lần đầu bạn tổ chức, họ c&oacute; thể gi&uacute;p bạn biết được những điều bạn thiếu s&oacute;t, vung v&eacute;n cho bạn những điều cần thiết nhất để buổi sự kiện, hay buổi hội thảo đạt mức th&agrave;nh c&ocirc;ng cao nhất c&oacute; thể.</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><img alt=\"\" src=\"/local/storage/app/image/images/Order_80_Fix-03(1).png\" style=\"height:480px; width:720px\" /></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><em>Kh&ocirc;ng gian khởi nghiệp bậc nhất tại ZikZak sẽ đồng h&agrave;nh c&ugrave;ng c&aacute;c bạn</em></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong>4. Cần g&igrave; lo chi ph&iacute; &ndash; V&igrave; ZikZak đ&atilde; free!</strong></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Chắc chắn, kh&oacute; khăn lớn nhất m&agrave; những người muốn tổ chức sự kiện hay hội thảo lu&ocirc;n gặp phải đ&oacute; l&agrave; kh&ocirc;ng gian tổ chức sự kiện v&agrave; c&aacute;c trang thiết bị cần thiết. Kh&ocirc;ng những thế, kinh ph&iacute; bỏ ra để c&oacute; được những điều kiện cần n&agrave;y cũng kh&aacute; cao. V&igrave; vậy, những buổi hội thảo, buổi sự kiện sẽ kh&ocirc;ng được th&agrave;nh c&ocirc;ng như những g&igrave; c&aacute;c bạn tưởng tượng ban đầu.</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Hiểu được điều đ&oacute;, ZikZak ra mắt ưu đ&atilde;i hỗ trợ kh&aacute;ch h&agrave;ng muốn tổ chức sự kiện, hội thảo ho&agrave;n to&agrave;n free, từ kh&ocirc;ng gian cho đến việc sắp xếp lại những vật dụng cần thiết.</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Chi tiết về sự kiện, c&aacute;c bạn c&oacute; thể xem tại:</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><a href=\"https://goo.gl/Lz6xp4\" style=\"color:blue; text-decoration:underline\">https://goo.gl/Lz6xp4</a></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Hoặc li&ecirc;n hệ: 092 238 30 97</span></span></p>', 'zikzak, coworkingspace, startup', '2018-11-20 03:44:50', '2018-11-20 03:44:50');
INSERT INTO `news` (`news_id`, `news_name`, `news_slug`, `news_summary`, `news_image`, `news_content`, `tag`, `created_at`, `updated_at`) VALUES
(26, 'KIÊN TRÌ ĐỂ THÀNH CÔNG', 'kien-tri-de-thanh-cong', 'Kiên trì là khả năng đương đầu với căng thẳng và nghịch cảnh. Đức tính này xuất phát từ niềm tin tuyệt đối vào bản thân, và đồng thời vào một phần vĩ đại tồn tại trong mỗi con người.', 'zz_2018-11-22_1542877215.jpeg', '<p>Khả năng&nbsp;<a href=\"http://cafebiz.vn/kien-tri.html\" target=\"_blank\" title=\"kiên trì\">ki&ecirc;n tr&igrave;</a>&nbsp;trước kh&oacute; khăn phụ thuộc phần lớn v&agrave;o c&aacute;ch ch&uacute;ng ta đưa ra c&aacute;c quyết định mỗi ng&agrave;y. B&agrave;i viết b&ecirc;n dưới l&agrave; những chia sẻ v&agrave; kinh nghiệm qu&yacute; b&aacute;u, gi&uacute;p đọc giả kh&aacute;m ph&aacute; ra b&iacute; quyết giữ vững tinh thần v&agrave;o những thời điểm cam go, thử th&aacute;ch nhất.</p>\r\n\r\n<p><strong>Về bản chất, con người thường ngại thay đổi, nhất l&agrave; khi sự thay đổi đến dưới dạng nghịch cảnh hay th&aacute;ch thức.</strong>&nbsp;Tuy nhi&ecirc;n, thay đổi l&agrave; điều kh&ocirc;ng thể tr&aacute;nh khỏi, việc ph&aacute;t triển v&agrave; nu&ocirc;i dưỡng t&iacute;nh ki&ecirc;n tr&igrave; kh&ocirc;ng chỉ gi&uacute;p ch&uacute;ng ta đ&oacute;n nhận thay đổi theo c&aacute;ch t&iacute;ch cực nhất, m&agrave; c&ograve;n gi&uacute;p ta&nbsp;<a href=\"http://cafebiz.vn/hoc-hoi.html\" target=\"_blank\" title=\"học hỏi\">học hỏi</a>, ph&aacute;t triển kh&ocirc;ng ngừng qua những trải nghiệm trong c&ocirc;ng việc v&agrave; cuộc sống.</p>\r\n\r\n<p>Ki&ecirc;n tr&igrave; l&agrave; khả năng đương đầu với căng thẳng v&agrave; nghịch cảnh. Đức t&iacute;nh n&agrave;y xuất ph&aacute;t từ niềm tin tuyệt đối v&agrave;o bản th&acirc;n, v&agrave; đồng thời v&agrave;o một phần vĩ đại tồn tại trong mỗi con người.</p>\r\n\r\n<p>Ki&ecirc;n tr&igrave; kh&ocirc;ng phải l&agrave; đức t&iacute;nh bẩm sinh; bất k&igrave; ai cũng c&oacute; thể học v&agrave; r&egrave;n luyện đức t&iacute;nh qu&yacute; gi&aacute; n&agrave;y th&ocirc;ng qua c&aacute;c phương ph&aacute;p tư duy v&agrave; điều chỉnh h&agrave;nh vi.</p>\r\n\r\n<p>B&ecirc;n dưới l&agrave; 10 c&aacute;ch gi&uacute;p bạn t&ocirc;i luyện, nu&ocirc;i dưỡng l&ograve;ng ki&ecirc;n tr&igrave; l&agrave;m h&agrave;nh trang tr&ecirc;n con đường t&igrave;m kiếm th&agrave;nh c&ocirc;ng v&agrave; hạnh ph&uacute;c.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/local/storage/app/image/images/Order_87_1(1).jpg\" style=\"height:480px; width:720px\" /></p>\r\n\r\n<p style=\"text-align:center\">Người ki&ecirc;n tr&igrave; kh&ocirc;ng lặp lại sai lầm trong qu&aacute; khứ. Họ thừa nhận nguy&ecirc;n nh&acirc;n thất bại v&agrave; d&agrave;nh thời gian chi&ecirc;m nghiệm để học hỏi từ ch&iacute;nh những sai lầm của m&igrave;nh.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/local/storage/app/image/images/Order_87_2(1).jpg\" style=\"height:480px; width:720px\" /></p>\r\n\r\n<p style=\"text-align:center\">Những người c&oacute; &yacute; ch&iacute; bền bỉ thường c&oacute; vẻ ngo&agrave;i hoạt b&aacute;t v&agrave; lạc quan. Họ thường nhắc nhở bản th&acirc;n rằng kh&oacute; khăn chỉ mang t&iacute;nh tạm thời, họ đ&atilde; v&agrave; sẽ tiếp tục vượt qua c&aacute;c chướng ngại ấy bằng c&aacute;ch tập trung v&agrave; đề cao những điều m&igrave;nh c&oacute; thể học được.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/local/storage/app/image/images/Order_87_3(1).jpg\" style=\"height:480px; width:720px\" /></p>\r\n\r\n<p style=\"text-align:center\">Khi ch&uacute;ng ta cao lớn hơn c&aacute;c thử th&aacute;ch hay vấn đề của m&igrave;nh, cuộc sống trở n&ecirc;n nhẹ nh&agrave;ng, dễ thở hơn. Người ki&ecirc;n tr&igrave; lu&ocirc;n biết c&aacute;ch học hỏi kh&ocirc;ng ngừng để trở n&ecirc;n dũng cảm, can trường hơn. Họ &yacute; thức rất r&otilde; chất lượng cuộc sống của ch&uacute;ng ta kh&ocirc;ng được quyết định bởi c&aacute;c yếu tố b&ecirc;n ngo&agrave;i, m&agrave; được định h&igrave;nh từ ch&iacute;nh c&aacute;c yếu tố b&ecirc;n trong.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/local/storage/app/image/images/Order_87_4___(1).jpg\" /></p>\r\n\r\n<p style=\"text-align:center\">Thất bại kh&ocirc;ng phải l&agrave; bị vấp ng&atilde; m&agrave; l&agrave; từ chối đứng dậy sau mỗi c&uacute; ng&atilde;. Người ki&ecirc;n tr&igrave; c&oacute; khả năng th&iacute;ch nghi rất tốt với ho&agrave;n cảnh v&agrave; kh&ocirc;ng bao giờ bỏ cuộc.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/local/storage/app/image/images/Order_87_5____(1).jpg\" style=\"height:480px; width:720px\" /></p>\r\n\r\n<p style=\"text-align:center\">Người ki&ecirc;n tr&igrave; rất ch&uacute; trọng c&aacute;c mối quan hệ của m&igrave;nh. Ch&iacute;nh c&aacute;c mối quan hệ th&acirc;n thiết, bền chặt sẽ l&agrave; chỗ dựa tinh thần vững chắc gi&uacute;p họ vượt qua khủng hoảng.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/local/storage/app/image/images/Order_87_6(1).jpg\" style=\"height:480px; width:720px\" /></p>\r\n\r\n<p style=\"text-align:center\">Người ki&ecirc;n tr&igrave; lu&ocirc;n hướng đến &yacute; nghĩa thực sự của cuộc sống. Họ suy tư v&agrave; tự t&igrave;m lấy cho m&igrave;nh một mục đ&iacute;ch sống r&otilde; r&agrave;ng, thứ gi&uacute;p họ nh&igrave;n nhận cuộc đời m&igrave;nh từ một g&oacute;c nh&igrave;n rộng lớn hơn.</p>\r\n\r\n<p style=\"text-align:center\">Để tồn tại trước s&oacute;ng gi&oacute; ta cần phải c&oacute; đức t&iacute;nh ki&ecirc;n tr&igrave;. Điều n&agrave;y đ&ograve;i hỏi ch&uacute;ng ta phải ch&uacute; &yacute; đến sự hỗn độn trong cuộc sống , lắng nghe cảm x&uacute;c của ch&iacute;nh m&igrave;nh v&agrave; sẵn s&agrave;ng học hỏi từ những cả th&agrave;nh c&ocirc;ng lẫn thất bại.</p>\r\n\r\n<p style=\"text-align:right\"><em>Tổng hợp từ Cafebiz</em></p>', 'kiên trì để thành công, muốn thành công hãy kiên trì', '2018-11-22 02:00:14', '2018-11-22 02:00:14'),
(27, 'Những lợi ích mà không gian làm việc chung mang lại', 'nhung-loi-ich-ma-khong-gian-lam-viec-chung-mang-lai', 'Trong những năm trở lại đây, không gian làm việc chung được giới trẻ đón nhận nồng nhiệt bởi sự tiện lợi, hiện đại và thông minh của nó. Ngoài ra, những lợi ích tuyệt vời mà nó mang lại cũng làm cho nhiều người hài lòng.', 'zz_2018-11-26_1543218925.png', '<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Kh&ocirc;ng gian l&agrave;m việc chung l&agrave; m&ocirc;i trường l&agrave;m việc hiện nay đang rất thịnh h&agrave;nh trong x&atilde; hội, nhất l&agrave; đối với những người th&iacute;ch thay đổi kh&ocirc;ng gian v&agrave; m&ocirc;i trường l&agrave;m việc của m&igrave;nh. Loại h&igrave;nh l&agrave;m việc chung n&agrave;y tạo ra được những hiệu quả l&agrave;m việc đ&aacute;p ứng được nhu cầu của nhiều người.</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Kh&ocirc;ng gian n&agrave;y ph&ugrave; hợp với những người th&iacute;ch một kh&ocirc;ng gian mới mẽ hơn so với văn ph&ograve;ng thường ng&agrave;y. V&agrave; đ&acirc;y cũng l&agrave; nơi l&yacute; tưởng d&agrave;nh cho c&aacute;c freelancer muốn tập trung v&agrave;o l&agrave;m việc.</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong>1. K&iacute;ch th&iacute;ch sự s&aacute;ng tạo v&agrave; l&agrave;m việc hiệu quả</strong></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Theo một khảo s&aacute;t trực tiếp về quan điểm l&agrave;m việc chung, c&oacute; đến 90% số lượng người được khảo s&aacute;t cho rằng họ cảm thấy tự tin hơn rất nhiều khi l&agrave;m việc c&ugrave;ng người kh&aacute;c, v&agrave; sự tự tin ch&iacute;nh l&agrave; một trong những yếu tố quyết định sự th&agrave;nh c&ocirc;ng trong cuộc sống.</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Theo một khảo s&aacute;t kh&aacute;c của tạp ch&iacute; trực tuyến Deskmag, 71% số người được khảo s&aacute;t cho rằng sự s&aacute;ng tạo tăng l&ecirc;n khi l&agrave;m việc tại kh&ocirc;ng gian l&agrave;m việc chung, 64% số lượng người được khảo s&aacute;t cho rằng khả năng của họ được n&acirc;ng cao r&otilde; rệt, đồng thời hiệu quả của c&ocirc;ng việc cũng tăng l&ecirc;n khi l&agrave;m việc tại kh&ocirc;ng gian l&agrave;m việc chung. M&agrave; đối với c&aacute;c bạn trẻ, sự tập trung v&agrave; s&aacute;ng tạo ch&iacute;nh l&agrave; ch&igrave;a kh&oacute;a để ở c&aacute;nh cửa th&agrave;nh c&ocirc;ng.</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><img alt=\"\" src=\"/local/storage/app/image/images/2(1).png\" style=\"height:480px; width:720px\" /></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><em><span style=\"font-family:Times New Roman, serif\"><span style=\"font-size:17.3333px\">Kh&ocirc;ng gian l&agrave;m việc chung k&iacute;ch th&iacute;ch sự s&aacute;ng tạo v&agrave; l&agrave;m việc hiệu quả</span></span></em></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong>2. Mở rộng mạng lưới mối quan hệ c&aacute; nh&acirc;n</strong></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Kh&ocirc;ng gian l&agrave;m việc chung l&agrave; một m&ocirc;i trường l&agrave;m việc mở rộng, với nhiều người thuộc nhiều lĩnh vực kh&aacute;c nhau c&ugrave;ng l&agrave;m việc chung tại văn ph&ograve;ng. Đ&oacute; hầu hết đều l&agrave; những người ch&uacute;ng ta kh&ocirc;ng hề quen biết. Khi được l&agrave;m việc với họ, mối quan hệ của ch&uacute;ng ta sẽ được mở rộng hơn nữa, kh&ocirc;ng chỉ l&agrave; với nhiều người, m&agrave; c&ograve;n l&agrave; nhiều ng&agrave;nh nghề kh&aacute;c nhau.</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Hơn nữa, thay v&igrave; ngồi thui thủi ở nh&agrave; v&agrave; l&agrave;m việc trong buồn ch&aacute;n, th&igrave; đến với kh&ocirc;ng gian l&agrave;m việc chung, bạn sẽ kh&ocirc;ng c&ograve;n cảm thấy buồn b&atilde; v&agrave; c&ocirc; đơn nữa, v&igrave; xung quanh bạn c&oacute; rất nhiều người cũng đang l&agrave;m việc.</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><img alt=\"\" src=\"/local/storage/app/image/images/3.png\" style=\"height:480px; width:720px\" /></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><em>Thay v&igrave; ngồi nh&agrave;, tại sao ch&uacute;ng ta kh&ocirc;ng đến kh&ocirc;ng gian l&agrave;m việc chung để t&igrave;m bạn mới?</em></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">V&agrave;, hiệu ứng l&agrave;m việc li&ecirc;n tục của những người xung quanh cũng sẽ l&agrave;m bạn tập trung v&agrave;o c&ocirc;ng việc của bản th&acirc;n hơn nữa. Bạn c&oacute; chắc rằng, ngồi l&agrave;m việc một m&igrave;nh ở nh&agrave;, bạn c&oacute; tập trung được đủ tốt để ho&agrave;n th&agrave;nh c&ocirc;ng việc?</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong>3. Kh&ocirc;ng gian đẹp với nhiều tiện &iacute;ch chung</strong></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Để k&iacute;ch th&iacute;ch sự s&aacute;ng tạo, c&aacute;c văn ph&ograve;ng chung được thiết kế sao cho người sử dụng cảm thấy c&oacute; sự th&iacute;ch th&uacute;, bắt mắt v&agrave; thoải m&aacute;i s&aacute;ng tạo ra những &yacute; tưởng mới lạ. Những co-working space lu&ocirc;n ch&uacute; trọng đến việc thiết kế sao cho người sử dụng kh&ocirc;ng gian c&oacute; thể vừa d&ugrave;ng n&oacute; để k&iacute;ch th&iacute;ch sự s&aacute;ng tạo, lại vừa c&oacute; thể thư gi&atilde;n sau những giờ l&agrave;m việc căng thẳng.</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><img alt=\"\" src=\"/local/storage/app/image/images/4(1).png\" style=\"height:480px; width:720px\" /></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><em>Kh&ocirc;ng gian l&agrave;m việc chung với nhiều tiện &iacute;ch</em></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">B&ecirc;n cạnh đ&oacute;, những tiện &iacute;ch chung cũng l&agrave; một trong những yếu tố gi&uacute;p người sử dụng chọn co-working space. Những tiện &iacute;ch chung như lễ t&acirc;n, bảo vệ, an ninh hay vệ sinh đều c&oacute; sẵn. Người sử dụng chỉ việc đến v&agrave; l&agrave;m việc, c&ograve;n những điều c&ograve;n lại, kh&ocirc;ng cần phải quan t&acirc;m đến nhiều. V&igrave; tất cả đều đ&atilde; được trang bị đầy đủ.</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><strong>4. Gi&aacute; cả hợp l&iacute;</strong></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Hầu hết c&aacute;c văn ph&ograve;ng l&agrave;m việc chung đều c&oacute; c&aacute;c g&oacute;i dịch vụ ph&ugrave; hợp với nhiều sự lựa chọn của nhiều người. Đ&oacute; c&oacute; thể l&agrave; g&oacute;i ng&agrave;y, g&oacute;i th&aacute;ng... t&ugrave;y v&agrave;o mức độ sử dụng của người d&ugrave;ng kh&ocirc;ng gian chung.</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Hiện tại, tại ZikZak Co-working Space, g&oacute;i dịch vụ th&agrave;nh vi&ecirc;n ng&agrave;y đang c&oacute; gi&aacute; chỉ 79.000 đồng/ng&agrave;y. G&oacute;i th&agrave;nh vi&ecirc;n th&aacute;ng cũng chỉ c&oacute; 1.700.000 đồng/ng&agrave;y. Rất tiện lợi cho sự lựa chọn của người sử dụng dịch vụ.</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><img alt=\"\" src=\"/local/storage/app/image/images/5.png\" style=\"height:480px; width:720px\" /></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:center\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><em>Gi&aacute; cả tại ZikZak Co-working Space ph&ugrave; hợp với nhiều đối tượng sử dụng kh&aacute;c nhau</em></span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Kh&ocirc;ng những thế, ZikZak Co-working Space đang c&oacute; ưu đ&atilde;i lớn d&agrave;nh cho những kh&aacute;ch h&agrave;ng sử dụng dịch vụ Th&agrave;nh vi&ecirc;n Th&aacute;ng.</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Cụ thể: Khi mua 3 th&aacute;ng dịch vụ, kh&aacute;ch h&agrave;ng sẽ được tặng th&ecirc;m 1 th&aacute;ng sử dụng dịch vụ. V&agrave; khi mua 6 th&aacute;ng dịch vụ, kh&aacute;ch h&agrave;ng sẽ được tặng th&ecirc;m đến 3 th&aacute;ng sử dụng dịch vụ. Ngo&agrave;i ra, chi ph&iacute; của g&oacute;i dịch vụ từ 1.700.000 đồng cũng chỉ c&ograve;n 1.200.000 đồng/th&aacute;ng.</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Chi tiết chương tr&igrave;nh ưu đ&atilde;i, kh&aacute;ch h&agrave;ng li&ecirc;n hệ ngay với ch&uacute;ng t&ocirc;i qua:</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Hotline: 092 238 30 97</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">ZIKZAK1: Lầu 1-3-6, t&ograve;a nh&agrave; CPHONE, 456 X&ocirc; Viết Nghệ Tĩnh, phường 25, Quận B&igrave;nh Thạnh, Hồ Ch&iacute; Minh.</span></span></p>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:13pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">ZIKZAK2: Lầu 29, t&ograve;a nh&agrave; Mường Thanh, 63 Trần Ph&uacute;, th&agrave;nh phố Nha Trang, tỉnh Kh&aacute;nh H&ograve;a.</span></span></p>', 'zikzak, cowokringspace', '2018-11-26 00:55:25', '2018-11-26 00:55:25');

