CREATE TABLE `packs` (
  `pack_id` int(10) UNSIGNED NOT NULL,
  `pack_name` varchar(191) COLLATE utf8mb4_unicode_ci NOT NULL,
  `pack_price` varchar(191) COLLATE utf8mb4_unicode_ci NOT NULL,
  `pack_summary` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `pack_detail` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `facility` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `pack_image` varchar(191) COLLATE utf8mb4_unicode_ci NOT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

--
-- Dumping data for table `packs`
