INSERT INTO `packs` (`pack_id`, `pack_name`, `pack_price`, `pack_summary`, `pack_detail`, `facility`, `pack_image`, `created_at`, `updated_at`) VALUES
(1, 'THÀNH VIÊN NGÀY', '79.000VNĐ / ngày', '<p>Kh&aacute;ch h&agrave;ng được lựa chọn chỗ ngồi tại kh&ocirc;ng gian chung m&agrave; kh&ocirc;ng cần đặt chỗ trước v&agrave; được sử dụng&nbsp;c&aacute;c dịch vụ&nbsp;tiện &iacute;ch chung.</p>', '<p>Bạn cần một kh&ocirc;ng gian l&agrave;m việc hiệu quả những l&uacute;c rảnh rỗi, g&oacute;i th&agrave;nh vi&ecirc;n ng&agrave;y ch&iacute;nh l&agrave; sự lựa chọn của bạn.</p>\r\n\r\n<p>Bạn chỉ cần đến với ZikZak lựa chọn chỗ ngồi bạn th&iacute;ch m&agrave; kh&ocirc;ng cần phải đặt chỗ trước.</p>', 'Thời gian sử dụng : 8h-19h|Chỗ ngồi linh hoạt|Trà và cà phê miễn phí|Được sử dụng tiện ích chung (phòng tập gym, phòng bếp..)', 'zz_2018-11-09_1541731540.jpeg', NULL, '2018-11-08 19:45:40'),
(2, 'THÀNH VIÊN LINH HOẠT', '150.000VNĐ / 3 ngày', '<p>D&agrave;nh cho c&aacute;c th&agrave;nh vi&ecirc;n đi l&agrave;m kh&ocirc;ng đầy đủ c&aacute;c ng&agrave;y trong tuần. Với Check, kh&aacute;ch h&agrave;ng c&oacute; 3 ng&agrave;y l&agrave;m việc linh hoạt trong th&aacute;ng v&agrave; được hưởng tiện &iacute;ch.</p>', '<p>Bạn l&agrave; người c&oacute; khung thời gian l&agrave;m việc linh hoạt, g&oacute;i th&agrave;nh vi&ecirc;n linh hoạt ch&iacute;nh l&agrave; những g&igrave; bạn cần.</p>\r\n\r\n<p>Với g&oacute;i linh hoạt, kh&aacute;ch h&agrave;ng c&oacute; 3 ng&agrave;y l&agrave;m việc linh hoạt trong th&aacute;ng v&agrave; sử dụng c&aacute;c dịch vụ của ZikZak.</p>', 'Thời gian sử dụng : 8h-19h|Chỗ ngồi linh hoạt|Trà và cà phê miễn phí|Được sử dụng tiện ích chung (phòng tập gym, phòng bếp..)', 'zz_2018-11-09_1541731646.jpeg', NULL, '2018-11-08 19:47:26'),
(3, 'THÀNH VIÊN THÁNG', '1.200.000VNĐ / Tháng', '<p>D&agrave;nh cho kh&aacute;ch cần kh&ocirc;ng gian l&agrave;m việc thường xuy&ecirc;n h&agrave;ng ng&agrave;y. Với Share, kh&aacute;ch h&agrave;ng được sử&nbsp;dụng chỗ ngồi linh hoạt ở kh&ocirc;ng gian chung.</p>', '<p>Bạn l&agrave; một người y&ecirc;u c&ocirc;ng việc, cần một kh&ocirc;ng gian l&agrave;m việc h&agrave;ng ng&agrave;y tiện nghi v&agrave; hiệu quả.</p>\r\n\r\n<p>Kh&ocirc;ng c&ograve;n g&igrave; tuyệt vời hơn với g&oacute;i th&agrave;nh vi&ecirc;n th&aacute;ng của ZikZak.</p>\r\n\r\n<p>Với g&oacute;i th&aacute;ng, kh&aacute;ch h&agrave;ng được sử dụng chỗ ngồi linh hoạt ở kh&ocirc;ng gian chung.</p>', 'Thời gian sử dụng : 8h-19h|Chỗ ngồi linh hoạt|Trà và cafe miễn phí|Được sử dụng dụng vụ quầy bar|Được sử dụng tiện ích chung (phòng tập gym, phòng bếp..)|Ưu đãi 30% thuê phòng họp|Miễn phí sử dụng máy in (đen trắng) 100 trang/tháng|Ưu đãi 15% sử dụng dịch vụ quầy bar', 'zz_2018-11-09_1541731667.jpeg', NULL, '2018-11-08 19:47:49'),
(4, 'VĂN PHÒNG RIÊNG', '10.000.000VNĐ / Tháng', '<p>Kh&aacute;ch h&agrave;ng được cấp ph&ograve;ng l&agrave;m việc đầy đủ tiện nghi cần thiết (điều h&ograve;a, b&agrave;n l&agrave;m việc c&aacute; nh&acirc;n, ghế, tủ để đồ) cho nh&oacute;m từ 3 đến 15 người.</p>', '<p>Văn ph&ograve;ng l&agrave;m việc ri&ecirc;ng ch&iacute;nh l&agrave; sự lựa chọn ho&agrave;n hảo cho c&aacute;c StartUp, Freelancer.</p>\r\n\r\n<p>Với văn ph&ograve;ng ri&ecirc;ng bạn sẽ được cung cấp ph&ograve;ng l&agrave;m việc ri&ecirc;ng, với đầy đủ tiện nghi (điều h&ograve;a, b&agrave;n l&agrave;m việc c&aacute; nh&acirc;n, tủ để đồ) với sức chứa từ 3 -15 người.</p>', 'Thời gian sử dụng : 8h-19h|Chỗ ngồi cố định|Trà và cafe miễn phí|Được sử dụng dụng vụ quầy bar|Được sử dụng tiện ích chung (phòng tập gym, phòng bếp..)|Có khu vực tiếp khách riêng|Miễn phí sử dụng phòng họp 2h>7h mỗi tuần, sử dụng hết ưu đãi 35% thuê phòng.|Miễn phí in ( đen, trắng) 150trang/tháng|Ưu đãi 20% sử dụng dịch vụ bar', 'zz_2018-11-09_1541731703.png', NULL, '2018-11-08 19:48:23');

