CREATE TABLE `services` (
  `service_id` int(10) UNSIGNED NOT NULL,
  `service_name` varchar(191) COLLATE utf8mb4_unicode_ci NOT NULL,
  `service_description` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `service_price` varchar(191) COLLATE utf8mb4_unicode_ci NOT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

--
-- Dumping data for table `services`
--

INSERT INTO `services` (`service_id`, `service_name`, `service_description`, `service_price`, `created_at`, `updated_at`) VALUES
(1, '<p>G&oacute;i StartUp</p>', '<p><b>Kế toán thuế cơ bản</b></p>\r\n								<ul>\r\n									<li> Thường xuyên ghi chép sổ sách và hoàn tất kê khai thuế hàng tháng (giới hạn số lượng 10 chứng từ hàng tháng)</li>\r\n								</ul>\r\n							Phù hợp với doanh nghiệp nhỏ muốn đáp ứng đủ yêu cầu tối thiểu của cơ quan thuế', '<p><strong>1.000.000VND</strong> / th&aacute;ng</p>', NULL, '2018-05-29 23:55:39'),
(2, '<p>G&oacute;i SME</p>', '<p><strong>Kế to&aacute;n thuế thường xuy&ecirc;n</strong></p>\r\n\r\n<ul>\r\n	<li>Bao gồm g&oacute;i Startup (giới hạn số lượng 30 chứng từ h&agrave;ng th&aacute;ng);</li>\r\n	<li>Xử l&yacute; hồ sơ v&agrave; lập đầy đủ B&aacute;o c&aacute;o T&agrave;i ch&iacute;nh v&agrave; Quyết to&aacute;n thuế h&agrave;ng năm theo quy định;</li>\r\n	<li>Tư vấn v&agrave; lập b&aacute;o c&aacute;o quản trị d&ograve;ng tiền;</li>\r\n	<li>Tư vấn miễn ph&iacute; với chuy&ecirc;n gia CPA với thời lượng 120 ph&uacute;t/th&aacute;ng.</li>\r\n</ul>\r\n\r\n<p>Ph&ugrave; hợp với những Startup v&agrave; doanh nghiệp SME đang tr&ecirc;n đ&agrave; ph&aacute;t triển</p>', '<p>Từ <strong>3.000.000VND</strong> / th&aacute;ng</p>\r\n\r\n<p>(Dựa tr&ecirc;n nhu cầu doanh nghiệp)</p>', NULL, '2018-05-30 00:01:59'),
(3, 'Gói Corporate', '<p><b>Kế toán quản trị</b></p>\r\n								<ul>\r\n									<li> Bao gồm gói SME (giới hạn số lượng 100 chứng từ hàng tháng);</li>\r\n									<li> Trực tiếp giải trình với cơ quan thuế.\r\n									Hỗ trợ cùng doanh nghiệp làm việc với nhà đầu tư và đối tác theo yêu cầu</li>\r\n									<li> Tư vấn miễn phí với chuyên gia CPA với thời lượng 240 phút/tháng.</li>\r\n									<li> Tư vấn cân đối doanh thu - chi phí, dòng tiền tránh tắc nghẽn hoạt động kinh doanh và tối ưu hoá chi phí trên cơ sở quy định của pháp luật;</li>\r\n									<li> Báo cáo Quản trị về hiệu quả hoạt động tổng thể của doanh nghiệp.</li>\r\n								</ul>\r\n								<p>Phù hợp với những doanh nghiệp đã phát triển hoặc có nhu cầu quản trị nội bộ.</p>', 'Từ <b>9.000.000VND</b> / tháng <br> (Dựa trên nhu cầu doanh nghiệp)', NULL, NULL);

