INSERT INTO `service_content` (`content_id`, `content_content`, `created_at`, `updated_at`) VALUES
(1, '<p>Bạn c&oacute; đang gặp kh&oacute; khăn trong việc Khai nộp thuế?</p>\r\n\r\n<p>Bạn đang đau đầu trong việc tự quản l&yacute; sổ s&aacute;ch?</p>\r\n\r\n<p>Bạn kh&ocirc;ng nắm được Nghĩa vụ Kế to&aacute;n doanh nghiệp?</p>\r\n\r\n<p>Bạn l&uacute;ng t&uacute;ng chuẩn bị Số liệu b&aacute;o c&aacute;o cho Cổ đ&ocirc;ng ?</p>\r\n\r\n<p>ZIKZAK SẼ GI&Uacute;P BẠN HO&Agrave;N THIỆN!</p>', NULL, '2018-11-05 17:27:38'),
(2, '<p>&bull; &nbsp;Tư vấn sử dụng h&oacute;a đơn đầu v&agrave;o v&agrave; đầu ra</p>\r\n\r\n<p>&bull; &nbsp;Ph&acirc;n loại, kiểm tra t&iacute;nh hợp lệ chứng từ gốc, c&acirc;n đối thuế GTGT đầu v&agrave;o v&agrave; đầu ra</p>\r\n\r\n<p>&bull; &nbsp;Nộp b&aacute;o c&aacute;o thuế l&ecirc;n cơ quan thuế theo quy định</p>\r\n\r\n<p>&bull; &nbsp;Ho&agrave;n thiện chứng từ v&agrave; lập sổ s&aacute;ch kế to&aacute;n</p>\r\n\r\n<p>&bull; &nbsp;Lập b&aacute;o c&aacute;o t&agrave;i ch&iacute;nh cuối năm</p>', NULL, '2018-11-05 17:41:39'),
(3, '<p>ZikZak cung cấp cho doanh nghiệp, StartUp &amp; SME dịch vụ kế to&aacute;n<strong> </strong></p>\r\n\r\n<p><strong>nhanh ch&oacute;ng &amp; hiệu quả!</strong></p>', NULL, '2018-11-05 17:27:38'),
(4, '<p>Bạn c&oacute; đang gặp kh&oacute; khăn trong việc Khai nộp thuế?</p>\r\n\r\n<p>Bạn đang đau đầu trong việc tự quản l&yacute; sổ s&aacute;ch?</p>\r\n\r\n<p>Bạn kh&ocirc;ng nắm được Nghĩa vụ Kế to&aacute;n doanh nghiệp?</p>\r\n\r\n<p>Bạn l&uacute;ng t&uacute;ng chuẩn bị Số liệu b&aacute;o c&aacute;o cho Cổ đ&ocirc;ng ?</p>\r\n\r\n<p>ZIKZAK SẼ GI&Uacute;P BẠN HO&Agrave;N THIỆN NHỮNG ĐIỀU Đ&Oacute;!</p>\r\n\r\n<p>ZikZak tư vấn cho doanh nghiệp, đặc biệt l&agrave; c&aacute;c StartUp &amp; SME những thủ tục v&agrave; giấy cần thiết như:</p>\r\n\r\n<p>- Tư vấn sử dụng h&oacute;a đơn đầu v&agrave;o v&agrave; đầu ra</p>\r\n\r\n<p>- Ph&acirc;n loại, kiểm tra t&iacute;nh hợp lệ chứng từ gốc, c&acirc;n đối thuế GTGT đầu v&agrave;o v&agrave; đầu ra</p>\r\n\r\n<p>- Nộp b&aacute;o c&aacute;o thuế l&ecirc;n cơ quan thuế theo quy định</p>\r\n\r\n<p>- Ho&agrave;n thiện chứng từ v&agrave; lập sổ s&aacute;ch kế to&aacute;n</p>\r\n\r\n<p>- Lập b&aacute;o c&aacute;o t&agrave;i ch&iacute;nh cuối năm</p>\r\n\r\n<p>Kh&aacute;ch h&agrave;ng chỉ cần cung cấp cho ZikZak</p>\r\n\r\n<p>- Cung cấp h&oacute;a đơn đầu v&agrave;o v&agrave; đầu ra (H&oacute;a đơn gốc hoặc photo)</p>\r\n\r\n<p>- C&aacute;c chừng từ li&ecirc;n quan</p>\r\n\r\n<p>- Thiết bị chữ k&yacute; số để nộp b&aacute;o c&aacute;o Thuế qua mạng</p>\r\n\r\n<p>ZikZak sẽ cung cấp cho doanh nghiệp, StartUp &amp; SME dịch vụ kế to&aacute;n NHANH CH&Oacute;NG &amp; HIỆU QUẢ.</p>', NULL, '2018-10-29 11:15:44'),
(5, '<p>Bạn c&oacute; &yacute; tưởng kinh doanh v&agrave; muốn khởi nghiệp , kh&ocirc;ng biết chọn m&ocirc; h&igrave;nh c&ocirc;ng ty, doanh nghiệp n&agrave;o cho ph&ugrave; hợp?</p>\r\n\r\n<p>Bạn đang băn khoăn thủ tục ph&aacute;p l&yacute; cơ bản để ho&agrave;n th&agrave;nh một doanh nghiệp ?</p>\r\n\r\n<p>Bạn đang cần tư vấn c&aacute;c giấy tờ l&agrave;m việc với c&aacute;c nh&agrave; đầu tư ?</p>\r\n\r\n<p>ZIKZAK SẼ GI&Uacute;P BẠN HO&Agrave;N THIỆN!</p>', NULL, '2018-11-05 17:42:24'),
(6, '<p>&bull; &nbsp;Th&agrave;nh lập doanh nghiệp, chi nh&aacute;nh, văn ph&ograve;ng đại diện, địa điểm kinh doanh</p>\r\n\r\n<p>&bull; &nbsp;Thay đổi đăng k&yacute; kinh doanh, Chuyển đổi doanh nghiệp</p>\r\n\r\n<p>&bull; &nbsp;Giải quyết c&aacute;c vấn đề ph&aacute;t sinh trong qu&aacute; tr&igrave;nh hoạt động của doanh nghiệp</p>\r\n\r\n<p>&bull; &nbsp;Ph&aacute;p luật kế to&aacute;n, thuế, hợp đồng, quyền sở hữu tr&iacute; tuệ, lao động v.v..</p>\r\n\r\n<p>&bull; &nbsp;Phải quyết c&aacute;c tranh chấp nội bộ doanh nghiệp</p>', NULL, '2018-11-05 17:42:24'),
(7, '', NULL, '2018-11-05 17:42:24'),
(11, '<p>Bạn c&oacute; đang gặp kh&oacute; khăn trong việc Khai nộp thuế?</p>\r\n\r\n<p>Bạn đang đau đầu trong việc tự quản l&yacute; sổ s&aacute;ch?</p>\r\n\r\n<p>Bạn kh&ocirc;ng nắm được Nghĩa vụ Kế to&aacute;n doanh nghiệp?</p>\r\n\r\n<p>Bạn l&uacute;ng t&uacute;ng chuẩn bị Số liệu b&aacute;o c&aacute;o cho Cổ đ&ocirc;ng ?</p>\r\n\r\n<p>ZIKZAK SẼ GI&Uacute;P BẠN HO&Agrave;N THIỆN NHỮNG ĐIỀU Đ&Oacute;!</p>\r\n\r\n<p>ZikZak tư vấn cho doanh nghiệp, đặc biệt l&agrave; c&aacute;c StartUp &amp; SME những thủ tục v&agrave; giấy cần thiết như:</p>\r\n\r\n<p>- Tư vấn sử dụng h&oacute;a đơn đầu v&agrave;o v&agrave; đầu ra</p>\r\n\r\n<p>- Ph&acirc;n loại, kiểm tra t&iacute;nh hợp lệ chứng từ gốc, c&acirc;n đối thuế GTGT đầu v&agrave;o v&agrave; đầu ra</p>\r\n\r\n<p>- Nộp b&aacute;o c&aacute;o thuế l&ecirc;n cơ quan thuế theo quy định</p>\r\n\r\n<p>- Ho&agrave;n thiện chứng từ v&agrave; lập sổ s&aacute;ch kế to&aacute;n</p>\r\n\r\n<p>- Lập b&aacute;o c&aacute;o t&agrave;i ch&iacute;nh cuối năm</p>\r\n\r\n<p>Kh&aacute;ch h&agrave;ng chỉ cần cung cấp cho ZikZak</p>\r\n\r\n<p>- Cung cấp h&oacute;a đơn đầu v&agrave;o v&agrave; đầu ra (H&oacute;a đơn gốc hoặc photo)</p>\r\n\r\n<p>- C&aacute;c chừng từ li&ecirc;n quan</p>\r\n\r\n<p>- Thiết bị chữ k&yacute; số để nộp b&aacute;o c&aacute;o Thuế qua mạng</p>\r\n\r\n<p>ZikZak sẽ cung cấp cho doanh nghiệp, StartUp &amp; SME dịch vụ kế to&aacute;n NHANH CH&Oacute;NG &amp; HIỆU QUẢ.</p>', NULL, '2018-10-29 18:15:44'),
(12, '<p>Bạn c&oacute; &yacute; tưởng kinh doanh v&agrave; muốn khởi nghiệp , kh&ocirc;ng biết chọn m&ocirc; h&igrave;nh c&ocirc;ng ty, doanh nghiệp&nbsp;n&agrave;o cho ph&ugrave; hợp</p>\r\n\r\n<p>Bạn đang băn khoăn thủ tục ph&aacute;p l&yacute; cơ bản để ho&agrave;n th&agrave;nh một doanh nghiệp ?</p>\r\n\r\n<p>Bạn đang cần tư vấn c&aacute;c giấy tờ l&agrave;m việc với c&aacute;c nh&agrave; đầu tư ?</p>\r\n\r\n<p>H&Atilde;Y ĐỂ ZIKZAK GI&Uacute;P BẠN HO&Agrave;N THIỆN N&Oacute;</p>\r\n\r\n<p>VỚI DỊCH VỤ TƯ VẤN DOANH NGHIỆP ZikZak sẽ gi&uacute;p bạn</p>\r\n\r\n<p>- Th&agrave;nh lập doanh nghiệp;</p>\r\n\r\n<p>- Th&agrave;nh lập chi nh&aacute;nh, văn ph&ograve;ng đại diện, địa điểm kinh doanh;</p>\r\n\r\n<p>- Thay đổi đăng k&yacute; kinh doanh;</p>\r\n\r\n<p>- Chuyển đổi doanh nghiệp;</p>\r\n\r\n<p>- Giải quyết c&aacute;c vấn đề ph&aacute;t sinh trong qu&aacute; tr&igrave;nh hoạt động của doanh nghiệp;</p>\r\n\r\n<p>- Ph&aacute;p luật kế to&aacute;n, thuế;</p>\r\n\r\n<p>- Ph&aacute;p luật về hợp đồng;</p>\r\n\r\n<p>- Ph&aacute;p luật về quyền sở hữu tr&iacute; tuệ;</p>\r\n\r\n<p>- Ph&aacute;p luật lao động;</p>\r\n\r\n<p>- Phải quyết c&aacute;c tranh chấp nội bộ doanh nghiệp</p>', NULL, '2018-06-24 11:56:32');

